addon.presenter:
function AddonAbacus_create() {
    /**
     * 获取Module当前的运行环境, 通过icPlayer提供的接口
     * 已定义的环境枚举包括
     * icCreatePlayer.RUNTIME.TEACHER_MOBILE(教师端)
     * icCreatePlayer.RUNTIME.STUDENT_MOBILE(学生端)
     * icCreatePlayer.RUNTIME.PROJECTION_MOBILE(投影端)
     * icCreatePlayer.RUNTIME.WEB(WEB端 )
     * icCreatePlayer.RUNTIME.TEACHER_PC(PC端)
     * 判断例子
     * if(currentRuntime === icCreatePlayer.RUNTIME.TEACHER_MOBILE)
     */
    var ICR_JS_BRIDGE = "com.nd.pad.icr.ui.IcrJsBridge";

    var currentRuntime = player.getPlayerServices().getRuntime();

    var DIRECTION = {
        UP: 1,
        DOWN: 2,
        LEFT: 3,
        RIGHT: 4
    };
    var helper = {
        i18n: {},
        isTeacherPad: false,
        isTeacherPc: false,
        isProjector: false,
        isStudent: false,
        isWeb: false,
        isApp: false,
        model: undefined,
        direction: DIRECTION.UP,
        cleanAbacus: false,
        renderDeferred: $.Deferred()
    };

    switch (currentRuntime) {
        case icCreatePlayer.RUNTIME.TEACHER_MOBILE:
            helper.isTeacherPad = true;
            helper.isApp = true;
            break;
        case icCreatePlayer.RUNTIME.STUDENT_MOBILE:
            helper.isStudent = true;
            helper.isApp = true;
            break;
        case icCreatePlayer.RUNTIME.PROJECTION_MOBILE:
            helper.isProjector = true;
            helper.isApp = true;
            break;
        case icCreatePlayer.RUNTIME.TEACHER_PC:
            helper.isTeacherPc = true;
            helper.isApp = true;
            break;
        case icCreatePlayer.RUNTIME.WEB:
            helper.isWeb = true;
            helper.isApp = false;
            break;
        default:
            helper.isWeb = true;
            helper.isApp = false;
            break;
    }

    var EVENTS = {
        SENSOR_ENABLE_EVENT: 'SENSOR_ENABLE_EVENT'
    };

    //定义全局变量
    var SensorListener = null;
    var GlobalParam = {}; //需要全局使用的放在这下面
    var callbacks = {};

    //音效加载完之后
    var soundsLoaded = function () {
        GlobalParam.audioLeftRight = sounds[presenter.basePath + 'resources/wood/images/audio_left_right.wav'];
        GlobalParam.audioUpDown = sounds[presenter.basePath + 'resources/wood/images/audio_up_down.wav'];
    };

    var callNative = function (methodName, parameters) {
        var res = null;
        var params = parameters || {};
        if (helper.isApp && Bridge && Bridge.callNative) {
            res = Bridge.callNative(ICR_JS_BRIDGE, methodName, params);
        }
        return res;
    };
    var registerNativeListener = function (key, callback) {
        if (helper.isApp && Bridge && Bridge.registerListener) {
            callbacks[key] = Bridge.registerListener(key, callback);
        }
    };

    var unbindNativeListener = function (key) {
        if (helper.isApp && Bridge && Bridge.unRegisterListener) {
            if (!key) {
                $.each(callbacks, function (k, v) {
                    Bridge.unRegisterListener(k, callbacks[k]);
                });
                callbacks = {};
            } else {
                Bridge.unRegisterListener(key, callbacks[key]);
            }
        }
    };

    /**
     * Module的逻辑
     * @param view 视图对象, 根据是否为预览状态传入不同的视图对象
     * @param model 模型对象
     * @param isPreview 是否为编辑环境, true=编辑环境, false=运行环境
     */
    var presenterLogic = function (view, model, isPreview) {
        helper.model = model;
        //设置全局数据
        GlobalParam.$view = $(view);

        // 		if (helper.isStudent) { 			GlobalParam.$view.find('#btnSevenBead').addClass('disabled');
        // 			GlobalParam.$view.find('#btnFiveBead').addClass('disabled');
        // 			GlobalParam.$view.find('#btnGravity').addClass('disabled');
        // 			GlobalParam.$view.find('#btnAudio').addClass('disabled'); 		}

        /**
         * GlobalParam.setting配置参数说明
         *
         * abacus_type : "算盘类型 1=五珠，2=七珠",
         * sound_status : "是否开启音效 1=开启，0=关闭",
         * gravity_status : "是否开启重力感应清盘功能 1=开始 0=关闭",
         * abacus_layout : {
         *  "up" : [
         *      [{"pos":"up"}, {"pos":"down"}], //上排第一根柱子上珠子位置
         *      [{"pos":"down"}, {"pos":"down"}] //上排第二根柱子上珠子位置
         *   ],
         *  "down" : [
         *      [{"pos":"up"}, {"pos":"down"}, {"pos":"down"}, {"pos":"down"}, {"pos":"down"}], //下排第一根柱子上珠子位置
         *      [{"pos":"down"}, {"pos":"down"}, {"pos":"down"}, {"pos":"down"}, {"pos":"down"}] //下排第二根柱子上珠子位置
         *  ]
         *  }
         *
         */
        //本地数据 GlobalParam.setting = model["setting"]; 远程数据
        GlobalParam.setting = model["question_url"];

        //初始化按钮的事件
        buttonObj.init();

        animationObj.init(GlobalParam.setting.abacus_type, GlobalParam.setting.abacus_layout);

        //浏览器大小改变
        $(window).resize(function () {
            animationObj.changeGameArea();
        });
        //加载音效
        sounds.loaded = 0;
        sounds.load([
            presenter.basePath + 'resources/wood/images/audio_left_right.wav',
            presenter.basePath + 'resources/wood/images/audio_up_down.wav'
        ]);
        sounds.whenLoaded = soundsLoaded;

        //XXX： 解决122730，DOM还没有渲染完，造成获取到的容器宽高不对
        setTimeout(function () {
            !helper.isStudent && animationObj.changeGameArea();
        }, 100);
    };

    var initStudentPush = function () {
        var extendData = helper.model['__ExtendModel'];
        if ($.isEmptyObject(extendData)) {
            return;
        }

        if (extendData.abacus_type == 1) {
            try {
                console.log('手动点击触发');
                GlobalParam.$view.find('#btnFiveBead').trigger('click');
                console.log('手动点击触发完毕');
            } catch (e) {
                console.log(e);
            }
        }
        if (extendData.sound_status == 0) {
            GlobalParam.$view.find('#btnAudio').trigger('click');
        }
        if (extendData.gravity_status == 1) {
            GlobalParam.$view.find('#btnGravity').trigger('click');
        }

        //恢复珠子
        var widthRatio = GlobalParam.$view.find('.canvas_game_areas')[0].getBoundingClientRect().width / extendData.container.width;
        var heightRatio = GlobalParam.$view.find('.canvas_game_areas')[0].getBoundingClientRect().height / extendData.container.height;
        console.log('[initStudentPush]容器宽:' + GlobalParam.$view.find('.canvas_game_areas')[0].getBoundingClientRect().width);
        console.log('[initStudentPush]容器高:' + GlobalParam.$view.find('.canvas_game_areas')[0].getBoundingClientRect().height);
        var up = extendData.beadPosition.up;
        var down = extendData.beadPosition.down;
        //上珠的位置
        console.log('开始恢复上珠', animationObj.beadObjArr.up);
        for (var i = 0, iLen = up.length; i < iLen; i++) {
            for (var j = 0, jLen = up[i].length; j < jLen; j++) {
                animationObj.beadObjArr.up[i][j].position.y = up[i][j].y * heightRatio;
            }
        }
        //下珠的位置
        console.log('开始恢复上珠', animationObj.beadObjArr.down);
        for (var i = 0, iLen = down.length; i < iLen; i++) {
            for (var j = 0, jLen = down[i].length; j < jLen; j++) {
                animationObj.beadObjArr.down[i][j].position.y = down[i][j].y * heightRatio;
            }
        }

        if (extendData.isOpenAboutAbacus) {
            GlobalParam.$view.find('#btnAbout').trigger('click');
            //是否显示全部组件
            if (extendData.about.allPartVisible) {
                GlobalParam.$view.find('#btnAllParts').trigger('click');
            }
            //各个部件是否显示
            if (extendData.about.abacusPopVisible) {
                var $abacusPop = GlobalParam.$view.find('._abacus_pop');
                $abacusPop.show();

                //显示框
                if (extendData.about.frameVisible) {
                    GlobalParam.$view.find('#framePic').show();
                    GlobalParam.$view.find('#frameText').show();
                }
                //显示梁
                if (extendData.about.beamVisible) {
                    GlobalParam.$view.find('#beamPic').show();
                    GlobalParam.$view.find('#beamText').show();
                }
                //显示档
                if (extendData.about.shelvesVisible) {
                    GlobalParam.$view.find('#shelvesPic').show();
                    GlobalParam.$view.find('#shelvesText').show();
                }
                //显示上珠
                if (extendData.about.upBeadVisible) {
                    GlobalParam.$view.find('#upBeadPic').show();
                    GlobalParam.$view.find('#upBeadText').show();
                }
                //显示下珠
                if (extendData.about.downBeadVisible) {
                    GlobalParam.$view.find('#downBeadPic').show();
                    GlobalParam.$view.find('#downBeadText').show();
                }

            }
        }

    };

    var presenter = function () {};

    var service = {};

    service.setLocationProperties = function (properties) {
        helper.i18n = properties;
    };
    // presenter.setLocationProperties = function(properties) { 	helper.i18n = properties; };

    service.getQuestionInfo = function () {
        return {
            id: helper.model.ID,
            type_code: "Abacus",
            type_name: helper.i18n['abacus_abacus'],
            url: "",
            item: "Abacus",
            statistics_type: 'no_need',
            dispatchOnly: true,
            notExistStatistics: true,
            noNeedQuizProgress: true
        };
    };

    service.getExtendData = function () {
        //显示全部部件时的遮罩
        var $abacusPop = GlobalParam.$view.find('._abacus_pop');
        var up = [],
            down = [];
        //上珠的位置
        for (var i = 0, iLen = animationObj.beadObjArr.up.length; i < iLen; i++) {
            up[i] = [];
            for (var j = 0, jLen = animationObj.beadObjArr.up[i].length; j < jLen; j++) {
                var pos = animationObj.beadObjArr.up[i][j].position;
                up[i][j] = {
                    x: pos.x,
                    y: pos.y
                };
            }
        }
        //下珠的位置
        for (var i = 0, iLen = animationObj.beadObjArr.down.length; i < iLen; i++) {
            down[i] = [];
            for (var j = 0, jLen = animationObj.beadObjArr.down[i].length; j < jLen; j++) {
                var pos = animationObj.beadObjArr.down[i][j].position;
                down[i][j] = {
                    x: pos.x,
                    y: pos.y
                };
            }
        }

        return {
            abacus_type: GlobalParam.setting.abacus_type,
            sound_status: GlobalParam.setting.sound_status,
            gravity_status: GlobalParam.setting.gravity_status,
            isOpenAboutAbacus: GlobalParam.$view.find('#btnAbout').hasClass('abacus_btn_on') ? true : false,
            about: {
                allPartVisible: GlobalParam.$view.find('.abacus_tips').is(':visible'),
                abacusPopVisible: $abacusPop.is(':visible'),
                frameVisible: $abacusPop.find('#framePic').is(':visible'),
                beamVisible: $abacusPop.find('#beamPic').is(':visible'),
                shelvesVisible: $abacusPop.find('#shelvesPic').is(':visible'),
                upBeadVisible: $abacusPop.find('#upBeadPic').is(':visible'),
                downBeadVisible: $abacusPop.find('#downBeadPic').is(':visible')
            },
            container: {
                width: animationObj.gameAreaWidth,
                height: animationObj.gameAreaHeight
            },
            beadPosition: {
                up: up,
                down: down
            }
        };
    };
    presenter.metaInfo = {
        name: "Abacus",
        type: PresenterType.PRESENTER_COMPONENT
    };
    presenter.getService = function () {
        return service;
    };

    /**
     * 监听事件设置
     */
    presenter.setPlayerController = function (controller) {
        presenter.controller = controller;
        presenter.eventBus = presenter.controller.getEventBus();
    };

    /**
     * 编辑环境下, 创建/刷新预览视图的方法;
     * Module在编辑环境下,model改变; 位置改变; DOM结构改变时都会促发该方法
     * @param view 预览视图(DOM对象)
     * @param model Module的模型, Key-Value结构
     */
    presenter.createPreview = function (view, model) {
        //Todo: 在此处一般进行model的数据有效性验证
        presenterLogic(view, model, true);
    };

    /**
     * 运行环境下, 初始化Module的方法
     * @param view 运行视图(DOM对象)
     * @param model Module的模型, Key-Value结构
     * @remark 该方法为Module生命周期方法,仅在Module初始化时执行一次
     */
    presenter.run = function (view, model) {
        //		debugger;
        var result = AbacusUtils.init(sensorCallback);
        if (!result) {
            console.error('重力感应初始化失败');
        }
        this.__interface = {
            resizeScreen: function (data) {
                //data {ratio:4/3||3/4}
                console.log('全屏', data);
                var ratio = eval(data.ratio);
                animationObj.ratio = ratio;
                animationObj.savePosition();
                animationObj.resizeScreen(animationObj.ratio);
                animationObj.lastRatio = animationObj.ratio;
            }
        };
    };
    presenter.pageShow = function (view, model) {
        presenterLogic(view, model, false);
        //绑定Module事件
        bindEvent();
        initI18N(view);
        //开启传感器
        GlobalParam.gravityStatus == 1 ? handleSensor(true) : handleSensor(false);
        //检测学生端是否支持重力感应的事件
        callNative("sendToPresenter", {
            instanceId: 'abacus-student',
            type: EVENTS.SENSOR_ENABLE_EVENT
        });
        //GlobalParam.audioEffect==1?:;
        return helper.renderDeferred.promise();
    };
    /**
     * 运行环境下, 销毁Module时执行的方法
     * @remark 该方法为Module生命周期方法, 仅在ICPlayer切换页面时执行
     */
    presenter.destroy = function () {
        AbacusUtils.destroy();
    };

    presenter.pageLeave = function () {
        //解除绑定的Module事件
        unBindEvent();

        //ToDo:释放Module占用的内存对象
        handleSensor(false);

        //释放算盘 		delete PIXI.loader.resources['rotate']; 		delete PIXI.loader.resources['rotate_image'];
        if (animationObj) {
            //移除pixi资源
            window.cancelAnimationFrame(animationObj.globalAnimationId);
            animationObj.renderer.destroy();
            animationObj.renderer = null;
            animationObj.stage.destroy(true);
        }

        //释放资源
        if (helper.isApp && Bridge && Bridge.unRegisterListener) {
            Bridge.unRegisterListener('OrientationSensorEvent', SensorListener);
        }
    };
    /**
     * 获得当前Module的状态信息;
     * 可用于Module的状态恢复和保存
     */
    presenter.getState = function () {
        return '';

        //TODO: 添加符合Module需求的状态对象
        var moduleState = JSON.stringify({module_id: helper.model.ID});
        return moduleState;
    };

    /**
     * 设置Module当前状态
     * @param state Module状态对象
     */
    presenter.setState = function (state) {
        return false;

        var currentState;
        if (state) {
            currentState = JSON.parse(state);
            //ToDo:处理Module的状态恢复
        }
    };
    //获取项目跟路径
    presenter.setBasePath = function (path) {
        presenter.basePath = path;
    };

    /**
     * 开启传感器的监听回调
     *
     * @response
     *    POSITIVE : 顺时针方向旋转Pad(向内翻转Pad)
     *    NEGATIVE : 逆时针方向旋转Pad(向外翻转Pad)
     */
    function sensorCallback(type) {
        if (type == 'POSITIVE') {
            //顺时针方向旋转Pad(向内翻转Pad)
            animationObj.changeAbacusType(GlobalParam.setting.abacus_type, 'down');
            if (GlobalParam.audioEffect && helper.cleanAbacus != 'down') {
                GlobalParam.audioUpDown.play();
            }
            helper.cleanAbacus = 'down';
        } else if (type == 'NEGATIVE') {
            //逆时针方向旋转Pad(向外翻转Pad)
            animationObj.changeAbacusType(GlobalParam.setting.abacus_type, 'up');
            if (GlobalParam.audioEffect && helper.cleanAbacus != 'up') {
                GlobalParam.audioUpDown.play();
            }
            helper.cleanAbacus = 'up';
        }
    }

    /**
     * 控制方向传感器的开关
     * @param floorLevel下限值，范围:[15,60],越小越灵敏，默认值为45
     */
    function handleSensor(isOpen) {
        if (isOpen) {
            AbacusUtils.enablePitchSensor();
        } else {
            AbacusUtils.disablePitchSensor();
        }
    }

    function onSensorEnableEventHandler(data) {
        if (!data.enable) {
            var $btnGravity = GlobalParam.$view.find('#btnGravity');
            $btnGravity.hide();
        }
    }

    /**
     * 绑定事件的方法
     */
    function bindEvent() {
        registerNativeListener('SENSOR_ENABLE_EVENT', onSensorEnableEventHandler);
    }

    function initI18N(view) {
        $(view).find('.tips_num1 .abacus_tips_txt').html(helper.i18n['abacus_frame']);
        $(view).find('.tips_num3 .abacus_tips_txt').html(helper.i18n['abacus_shelves']);
        $(view).find('.tips_num2 .abacus_tips_txt').html(helper.i18n['abacus_beam']);
        $(view).find('.tips_num4 .abacus_tips_txt').html(helper.i18n['abacus_up_bead']);
        $(view).find('.tips_num5 .abacus_tips_txt').html(helper.i18n['abacus_down_bead']);
        $(view).find('#frameArea').html(helper.i18n['abacus_frame']);
        $(view).find('#beamArea').html(helper.i18n['abacus_beam']);
        $(view).find('#shelves').html(helper.i18n['abacus_shelves']);
        $(view).find('#upBeadArea').html(helper.i18n['abacus_up_bead']);
        $(view).find('#downBeadArea').html(helper.i18n['abacus_down_bead']);
        $(view).find('#btnSevenBead .tool_btns_text').html(helper.i18n['abacus_7_bead']);
        $(view).find('#btnFiveBead .tool_btns_text').html(helper.i18n['abacus_5_bead']);
        $(view).find('#btnGravity .tool_btns_text').html(helper.i18n['abacus_sensor']);
        $(view).find('#btnAllParts').html(helper.i18n['abacus_all_part_name']);
        $(view).find('#btnAudio .tool_btns_text').html(helper.i18n['abacus_sound_effect']);
        $(view).find('#btnAbout .tool_btns_text').html(helper.i18n['abacus_about_abacus']);
        $(view).find('#frameText').html(helper.i18n['abacus_frame']);
        $(view).find('#beamText').html(helper.i18n['abacus_beam']);
        $(view).find('#shelvesText').html(helper.i18n['abacus_shelves']);
        $(view).find('#upBeadText').html(helper.i18n['abacus_up_bead']);
        $(view).find('#downBeadText').html(helper.i18n['abacus_down_bead']);
        $(view).find('._abacus').addClass(helper.i18n['abacus_class_lang']);
    }

    /**
     * 解除事件绑定的方法
     */
    function unBindEvent() {
        unbindNativeListener('SENSOR_ENABLE_EVENT');
    }

    var animationObj = {
        /**
         * 初始化游戏界面
         *
         * @param type 类型 1=五珠，2=七珠
         * @param layout 珠子的布局
         */
        init: function (type, layout) {
            var self = this;

            //定义游戏区
            var gameContainer = GlobalParam.$view.find('.canvas_game_areas');

            //游戏区域的位置
            console.log('[init]容器宽:' + gameContainer[0].getBoundingClientRect().width);
            console.log('[init]容器高:' + gameContainer[0].getBoundingClientRect().height);
            self.gameAreaWidth = gameContainer[0].getBoundingClientRect().width;
            self.gameAreaHeight = gameContainer[0].getBoundingClientRect().height;

            //算盘珠子的数量
            self.pillarNum = 13;
            //上区一档珠子数量
            self.upBeadNum = type == 1 ? 1 : 2;
            //上区一档珠子数量
            self.downBeadNum = type == 1 ? 4 : 5;

            //算盘上区所占的比例
            self.upAreaPersent = 0.2629;

            //算盘下区所占的比例
            self.downAreaPersent = 0.6136;

            //精灵的比例
            self.scale = self.gameAreaWidth / 1528; //0.70;
            //珠子的高度
            self.beadHeight = 53 * self.scale;
            //珠子的宽度
            self.beadWidth = 105 * self.scale;

            //定义游戏区
            self.renderer = PIXI.autoDetectRenderer(self.gameAreaWidth, self.gameAreaHeight, {
                view: GlobalParam.$view.find('.canvas_game_areas canvas')[0],
                transparent: true
            }, true);
            self.stage = new PIXI.Container();

            //游戏数据
            self.gameType = type;
            self.gameLayout = layout;

            //被点击的珠子
            self.clickBead = null;

            //上次移动的时候的高度，用于判断移动方向
            self.historyMoveHight = -1;

            //记录刷新ID
            self.globalAnimationId = null;

            //精灵数组
            self.rotateArr = [];

            //所有珠子pixi对象数组
            self.beadObjArr = {
                'up': [],
                'down': []
            };

            //所有珠子pixi对象数组保存备份
            self.beadObjArrClone = {
                'up': [],
                'down': []
            };

            //缩放比例
            self.ratio = 1;

            //上一次的比例
            self.lastRatio = 1;

            // 加载精灵数据 if (!PIXI.loader.resources['rotate']) {    loader = PIXI.loader.add('rotate', presenter.basePath +
            // 'resources/wood/images/newzz.json').load(onTilesLoaded);
            //
            //    function onTilesLoaded(loader, resources) {        $.each(resources['rotate']['textures'], function (k, v) {
            // self.rotateArr.push(v);        });        //开始绘制珠子        self.initUI();        //学生推送的数据需要待珠子绘制完毕，再初始化
            // initStudentPush();    } } else {    $.each(PIXI.loader.resources['rotate']['textures'], function (k, v) {
            // self.rotateArr.push(v);    });    //开始绘制珠子    self.initUI();    //学生推送的数据需要待珠子绘制完毕，再初始化    initStudentPush(); }
            PIXI.loader.reset();
            loader = PIXI.loader.add('rotate', presenter.basePath + 'resources/wood/images/newzz.json').load(onTilesLoaded);

            function onTilesLoaded(loader, resources) {
                $.each(resources['rotate']['textures'], function (k, v) {
                    self.rotateArr.push(v);
                });
                //开始绘制珠子
                self.initUI();
                //学生推送的数据需要待珠子绘制完毕，再初始化
                initStudentPush();
                //渲染完
                helper.renderDeferred.resolve();
            }
        },
        initGameArea: function () {
            var self = this;
            //定义游戏区
            var gameContainer = GlobalParam.$view.find('.canvas_game_areas');

            //游戏区域的位置
            console.log('[initGameArea]容器宽:' + gameContainer[0].getBoundingClientRect().width);
            console.log('[initGameArea]容器高:' + gameContainer[0].getBoundingClientRect().height);
            self.gameAreaWidth = gameContainer[0].getBoundingClientRect().width;
            self.gameAreaHeight = gameContainer[0].getBoundingClientRect().height;
        },
        changeGameArea: function (scale) {
            var self = this;
            //定义游戏区
            var gameContainer = GlobalParam.$view.find('.canvas_game_areas');

            //游戏区域的位置
            console.log('[changeGameArea]容器宽:' + gameContainer[0].getBoundingClientRect().width);
            console.log('[changeGameArea]容器高:' + gameContainer[0].getBoundingClientRect().height);
            if (gameContainer[0].getBoundingClientRect().width != 0) {
                self.gameAreaWidth = gameContainer[0].getBoundingClientRect().width;
            }
            if (gameContainer[0].getBoundingClientRect().height != 0) {
                self.gameAreaHeight = gameContainer[0].getBoundingClientRect().height;
            }
            if (scale) {
                self.scale = scale;
            } else {
                self.scale = self.gameAreaWidth / 1528;
            }
            //珠子的高度
            self.beadHeight = 53 * self.scale;
            //珠子的宽度
            self.beadWidth = 105 * self.scale;
            self.stage.removeChildren();
            self.renderer.resize(self.gameAreaWidth, self.gameAreaHeight);
            self.initUI();
        },
        //保存珠子位置
        savePosition: function () {
            var self = this;
            //上珠的位置
            for (var i = 0, iLen = animationObj.beadObjArr.up.length; i < iLen; i++) {
                self.beadObjArrClone.up[i] = [];
                for (var j = 0, jLen = animationObj.beadObjArr.up[i].length; j < jLen; j++) {
                    var pos = animationObj.beadObjArr.up[i][j].position;
                    self.beadObjArrClone.up[i][j] = {
                        x: pos.x,
                        y: pos.y
                    };
                }
            }
            //下珠的位置
            for (var i = 0, iLen = animationObj.beadObjArr.down.length; i < iLen; i++) {
                self.beadObjArrClone.down[i] = [];
                for (var j = 0, jLen = animationObj.beadObjArr.down[i].length; j < jLen; j++) {
                    var pos = animationObj.beadObjArr.down[i][j].position;
                    self.beadObjArrClone.down[i][j] = {
                        x: pos.x,
                        y: pos.y
                    };
                }
            }
            console.log('self.beadObjArrClone', self.beadObjArrClone);
        },
        //工具全屏
        resizeScreen: function (ratio) {
            var self = this;
            var up = self.beadObjArrClone.up;
            var down = self.beadObjArrClone.down;
            //定义游戏区
            var gameContainer = GlobalParam.$view.find('.canvas_game_areas');

            //游戏区域的位置
            console.log('[changeGameArea]容器宽:' + gameContainer[0].getBoundingClientRect().width);
            console.log('[changeGameArea]容器高:' + gameContainer[0].getBoundingClientRect().height);
            if (gameContainer[0].getBoundingClientRect().width != 0) {
                self.gameAreaWidth = gameContainer[0].getBoundingClientRect().width;
            }
            if (gameContainer[0].getBoundingClientRect().height != 0) {
                self.gameAreaHeight = gameContainer[0].getBoundingClientRect().height;
            }
            self.scale = self.gameAreaWidth / 1528;

            //self.scale = scale; 珠子的高度
            self.beadHeight = 53 * self.scale;
            //珠子的宽度
            self.beadWidth = 105 * self.scale;
            self.stage.removeChildren();
            self.renderer.resize(self.gameAreaWidth, self.gameAreaHeight);
            self.initUI();
            console.log('scale', self.scale);
            //上珠的位置
            console.log('开始恢复上珠', animationObj.beadObjArr.up);
            for (var i = 0, iLen = up.length; i < iLen; i++) {
                for (var j = 0, jLen = up[i].length; j < jLen; j++) {
                    if (animationObj.ratio !== animationObj.lastRatio) {
                        animationObj.beadObjArr.up[i][j].position.y = up[i][j].y * ratio;
                    } else {
                        animationObj.beadObjArr.up[i][j].position.y = up[i][j].y;
                    }
                }
            }
            //下珠的位置
            console.log('开始恢复上珠', animationObj.beadObjArr.down);
            for (var i = 0, iLen = down.length; i < iLen; i++) {
                for (var j = 0, jLen = down[i].length; j < jLen; j++) {
                    if (animationObj.ratio !== animationObj.lastRatio) {
                        animationObj.beadObjArr.down[i][j].position.y = down[i][j].y * ratio;
                    } else {
                        animationObj.beadObjArr.down[i][j].position.y = down[i][j].y;
                    }
                }
            }
        },
        'changeAbacusType': function (type, direction) {
            var self = this;
            GlobalParam.setting.abacus_type = type;
            //算盘类型 1=五珠，2=七珠 上区一档珠子数量
            self.upBeadNum = type == 1 ? 1 : 2;
            //上区一档珠子数量
            self.downBeadNum = type == 1 ? 4 : 5;

            self.gameLayout.up = [];
            self.gameLayout.down = [];
            //恢复上区珠子
            for (var i = 0; i < self.pillarNum; i++) {
                self.gameLayout.up[i] = [];
                for (var j = 0; j < self.upBeadNum; j++) {
                    self.gameLayout.up[i][j] = {
                        pos: direction ? direction : 'up'
                    };
                }
            }
            //恢复下区一珠子
            for (var i = 0; i < self.pillarNum; i++) {
                self.gameLayout.down[i] = [];
                for (var j = 0; j < self.downBeadNum; j++) {
                    self.gameLayout.down[i][j] = {
                        pos: direction ? direction : 'down'
                    };
                }
            }

            //所有珠子pixi对象数组
            self.beadObjArr = {
                'up': [],
                'down': []
            };
            //重绘珠子
            self.initUI();
        },

        /**
         * 初始化游戏界面
         */
        initUI: function () {
            var self = this;
            window.cancelAnimationFrame(self.globalAnimationId);
            self.stage.removeChildren();
            //执行动画
            var runAnimation = function () {
                self.renderer.render(self.stage);
                self.globalAnimationId = requestAnimationFrame(runAnimation);
            }

            //开启动画
            runAnimation();

            //每个区域平均宽度
            var averageWidth = self.gameAreaWidth / self.pillarNum;

            //上区域的高度
            var upHeight = self.gameAreaHeight * self.upAreaPersent;

            //下区域的高度
            var downHeight = self.gameAreaHeight * self.downAreaPersent;

            //上区域和梁所占高度
            var tmpUpHeight = (1 - self.downAreaPersent) * self.gameAreaHeight;

            //开始绘制上区珠子
            for (var i = 0; i < self.gameLayout.up.length; i++) {
                var tmp = self.gameLayout.up[i];

                //计算水平位置
                var xPos = (i + 0.5) * averageWidth;

                //初始化游戏数组
                if (!self.beadObjArr['up'][i]) {
                    self.beadObjArr['up'][i] = [];
                }

                //循环珠子
                for (var j = 0, jLen = tmp.length; j < jLen; j++) {
                    var bead = tmp[j];

                    //计算垂直距离
                    var yPos = bead.pos === 'down' ? upHeight - ((j + 0.5) * self.beadHeight) : (jLen - 0.5 - j) * self.beadHeight;

                    //绘制珠子
                    var beadObj = new PIXI.extras.MovieClip(self.rotateArr);
                    beadObj.animationSpeed = 1;
                    beadObj.loop = false;
                    beadObj.height = this.beadHeight;
                    beadObj.width = this.beadWidth;
                    beadObj.position.x = xPos;
                    beadObj.position.y = yPos;
                    beadObj.interactive = true;
                    beadObj.anchor = {
                        x: 0.5,
                        y: 0.5
                    };

                    //计算珠子可以移动的范围
                    var topHeight = (tmp.length - 0.5 - j) * self.beadHeight;
                    var bottomHeight = upHeight - ((j + 0.5) * self.beadHeight);

                    //给每个元素添加游戏数据
                    beadObj.gameData = {
                        'upper_limit': topHeight, //上限
                        'lower_limit': bottomHeight, //下限
                        'type': 'up', //上区还是下区
                        'one_index': i, //在一维数组中的索引
                        'two_index': j //在二维数组中的索引
                    };

                    //给每个元素绑定事件
                    beadObj.on('mousedown', onDragStart).on('touchstart', onDragStart).on('mouseup', onDragEnd).on('mouseupoutside', onDragEnd).on('touchend', onDragEnd).on('touchendoutside', onDragEnd).on('mousemove', onDragMove).on('touchmove', onDragMove);

                    //将珠子加入舞台
                    self.stage.addChild(beadObj);

                    //将珠子加入游戏数组中
                    self.beadObjArr['up'][i][j] = beadObj;
                }
            }
            console.log('上珠子初始化完毕', self.beadObjArr['up']);

            //开始绘制下区珠子
            for (var i = 0; i < self.gameLayout.down.length; i++) {
                var tmp = self.gameLayout.down[i];

                //计算水平位置
                var xPos = (i + 0.5) * averageWidth;

                //初始化游戏数组
                if (!self.beadObjArr['down'][i]) {
                    self.beadObjArr['down'][i] = [];
                }

                //循环珠子
                for (var j = 0, jLen = tmp.length; j < jLen; j++) {
                    var bead = tmp[j];

                    //计算垂直距离
                    var yPos = bead.pos === 'down' ? self.gameAreaHeight - ((j + 0.5) * self.beadHeight) : tmpUpHeight + (jLen - j - 1 + 0.5) * self.beadHeight;

                    //绘制珠子
                    var beadObj = new PIXI.extras.MovieClip(self.rotateArr);
                    beadObj.animationSpeed = 1;
                    beadObj.loop = false;
                    beadObj.height = this.beadHeight;
                    beadObj.width = this.beadWidth;
                    beadObj.position.x = xPos;
                    beadObj.position.y = yPos;
                    beadObj.interactive = true;
                    beadObj.anchor = {
                        x: 0.5,
                        y: 0.5
                    };

                    //计算珠子可以移动的范围
                    var topHeight = (tmp.length - 0.5 - j) * self.beadHeight + tmpUpHeight;
                    var bottomHeight = self.gameAreaHeight - ((j + 0.5) * self.beadHeight);

                    //给每个元素添加游戏数据
                    beadObj.gameData = {
                        'upper_limit': topHeight,
                        'lower_limit': bottomHeight,
                        'type': 'down', //上区还是下区
                        'one_index': i, //在一维数组中的索引
                        'two_index': j //在二维数组中的索引
                    };

                    //给每个元素绑定事件
                    beadObj.on('mousedown', onDragStart).on('touchstart', onDragStart).on('mouseup', onDragEnd).on('mouseupoutside', onDragEnd).on('touchend', onDragEnd).on('touchendoutside', onDragEnd).on('mousemove', onDragMove).on('touchmove', onDragMove);

                    //将珠子加入舞台
                    self.stage.addChild(beadObj);

                    //将珠子加入游戏数组中
                    self.beadObjArr['down'][i][j] = beadObj;
                }
            }
            console.log('上珠子初始化完毕', self.beadObjArr['down']);
            //点击开始
            function onDragStart(event) {
                console.log('触发Start');
                self.data = event.data;

                //记录被点击的珠子
                self.clickBead = {
                    'type': this.gameData.type,
                    'one_index': this.gameData.one_index,
                    'two_index': this.gameData.two_index
                };
                self.historyMoveHight = -1;
                self.historyMoveWidth = -1;

                self.dragging = true;
                this.dragging = true;
            }

            //点击结束
            function onDragEnd() {
                self.dragging = false;
                this.dragging = false;
                self.data = null;
                self.clickBead = null;
                if (self.historyMoveHight > 10 && GlobalParam.audioEffect) {
                    GlobalParam.audioUpDown.play();
                }
                self.historyMoveHight = -1;
                self.historyMoveWidth = -1;
            }

            //鼠标移动
            function onDragMove(event) {
                if (self.dragging) {
                    var newPosition = self.data.getLocalPosition(this.parent);

                    var type = self.clickBead.type;
                    var one_index = self.clickBead.one_index;
                    var two_index = self.clickBead.two_index;

                    //获取被移动的珠子
                    var obj = self.beadObjArr[type][one_index][two_index];
                    //console.log('拔动珠子type:' + type, '拔动珠子one_index:' + one_index, '拔动珠子two_index:' + two_index);

                    if (self.historyMoveWidth == -1) {
                        self.historyMoveWidth = newPosition.x;
                    }
                    //判断珠子向右移动
                    if (this.position.x - this.width / 2 < newPosition.x && this.position.x + this.width / 2 > newPosition.x && this.position.y - this.height / 2 < newPosition.y && this.position.y + this.height / 2 > newPosition.y) {
                        if (newPosition.x - self.historyMoveWidth > 10 && newPosition.x > obj.position.x) {
                            if (GlobalParam.audioEffect) {
                                GlobalParam.audioLeftRight.play();
                            }
                            this.gotoAndPlay(0);
                            self.historyMoveWidth = -1;
                        } else if (self.historyMoveWidth - newPosition.x > 10 && newPosition.x < obj.position.x) { //向左
                            if (GlobalParam.audioEffect) {
                                GlobalParam.audioLeftRight.play();
                            }
                            this.gotoAndPlay(0);
                            self.historyMoveWidth = -1;
                        }
                    }

                    //判断珠子是否可以移动 console.log('触发移动位置', newPosition, '触发移动对象', obj.gameData);
                    if (self.historyMoveHight != -1 && newPosition.y < obj.gameData.upper_limit) {
                        newPosition.y = obj.gameData.upper_limit;
                    }
                    if (self.historyMoveHight != -1 && newPosition.y > obj.gameData.lower_limit) {
                        newPosition.y = obj.gameData.lower_limit;
                    }
                    if (newPosition.y >= obj.gameData.upper_limit && newPosition.y <= obj.gameData.lower_limit) {
                        //如果还没有记录，就不进行移动
                        if (self.historyMoveHight == -1) {
                            self.historyMoveHight = newPosition.y;
                            return false;
                        }

                        //判断珠子的移动方向
                        if (newPosition.y > self.historyMoveHight) {
                            //向下移动
                            for (var i = two_index; i >= 0; i--) {
                                //获取两个珠子的距离，判断是否需要一起移动
                                var realDis = self.beadObjArr[type][one_index][i].position.y - obj.position.y;

                                //获取如果珠子紧挨在一起的距离
                                var tmpDis = (two_index - i) * self.beadHeight;

                                if ((realDis - tmpDis) <= 0) {
                                    //一起移动
                                    self.beadObjArr[type][one_index][i]['position'].y = tmpDis + newPosition.y;
                                    self.historyMoveHight = newPosition.y;
                                    //console.log('向下移动', '移动one_inde', one_index, '移动two_index', i, '移动位置', newPosition.y, 'tmpDis', tmpDis);
                                }
                            }

                        } else if (newPosition.y < self.historyMoveHight) {
                            //向上移动
                            for (var i = two_index; i < self.beadObjArr[type][one_index].length; i++) {
                                //获取两个珠子的距离，判断是否需要一起移动
                                var realDis = obj.position.y - self.beadObjArr[type][one_index][i].position.y;

                                //获取如果珠子紧挨在一起的距离
                                var tmpDis = (i - two_index) * self.beadHeight;

                                if ((realDis - tmpDis) <= 0) {
                                    //一起移动
                                    self.beadObjArr[type][one_index][i]['position'].y = newPosition.y - tmpDis;
                                    self.historyMoveHight = newPosition.y;
                                    //console.log('向上移动', '移动one_inde', one_index, '移动two_index', i, '移动位置', newPosition.y, 'tmpDis', tmpDis);
                                }
                            }

                        }
                        //将相应方向的珠子进行移动 obj.position.y = newPosition.y; console.log('触发移动'); 记录移动的高度 self.historyMoveHight = newPosition.y;
                    } else {
                        //onDragEnd();
                    }

                    //珠子一旦有拨动过，那么就要把清盘的标记设置为false
                    helper.cleanAbacus = undefined;
                }
            }

            //鼠标进入
            function onDragOver(event) {
                this.gotoAndPlay(0);
                if (GlobalParam.audioEffect) {
                    GlobalParam.audioLeftRight.play();
                }
            }
        }
    };

    /**
     * 底部按钮的交互
     */
    var buttonObj = {
        init: function () {
            //珠子类型，5珠或者7珠
            this.beadType();
            //重力感应
            this.gravityInduction();
            //音效
            this.audioEffect();
            //关于算盘
            this.aboutAbacus();
            //显示所有部件
            this.allAllParts();
        },
        beadType: function () {
            var $beads = GlobalParam.$view.find('#btnSevenBead,#btnFiveBead');
            //算盘类型 1=五珠，2=七珠
            var abacusType = GlobalParam.setting.abacus_type;
            $beads.removeClass('abacus_btn_on');
            if (abacusType == 1) {
                GlobalParam.$view.find('#btnFiveBead').addClass('abacus_btn_on');
                GlobalParam.$view.find('._abacus_frame').addClass('abacus_normal');
            } else {
                GlobalParam.$view.find('._abacus_frame').removeClass('abacus_normal');
                GlobalParam.$view.find('#btnSevenBead').addClass('abacus_btn_on');
            }
            $beads.on('click', function (e) {
                //切换珠子类型时，清盘的标记设为false，用于重力感应
                helper.cleanAbacus = undefined;
                $beads.removeClass('abacus_btn_on');
                GlobalParam.setting.abacus_type = $(this).data('beadType');
                console.log(GlobalParam.setting.abacus_type == 1 ? '五珠按钮被点击' : '七珠按钮被点击');
                if (GlobalParam.setting.abacus_type == 1) {
                    GlobalParam.$view.find('#btnFiveBead').addClass('abacus_btn_on');
                    GlobalParam.$view.find('._abacus_frame').addClass('abacus_normal');
                } else {
                    GlobalParam.$view.find('._abacus_frame').removeClass('abacus_normal');
                    GlobalParam.$view.find('#btnSevenBead').addClass('abacus_btn_on');
                }

                animationObj.changeAbacusType(GlobalParam.setting.abacus_type);
            });
        },
        gravityInduction: function () {

            //是否开启重力感应清盘功能 1=开始 0=关闭
            var gravityStatus = GlobalParam.setting.gravity_status;
            GlobalParam.gravityStatus = gravityStatus;
            var $btnGravity = GlobalParam.$view.find('#btnGravity');
            if (gravityStatus == 1) {
                $btnGravity.addClass('abacus_btn_on');
            } else {
                $btnGravity.removeClass('abacus_btn_on');
            }

            $btnGravity.on('click', function (e) {
                var $me = $(this);
                if ($me.hasClass('abacus_btn_on')) {
                    $me.removeClass('abacus_btn_on');
                    GlobalParam.gravityStatus = 0;
                    GlobalParam.setting.gravity_status = 0;
                    handleSensor(false);
                } else {
                    $me.addClass('abacus_btn_on');
                    GlobalParam.gravityStatus = 1;
                    GlobalParam.setting.gravity_status = 1;
                    handleSensor(true);
                }
            });

        },
        audioEffect: function () {
            //是否开启音效 1=开启，0=关闭
            var soundStatus = GlobalParam.setting.sound_status;
            var $btnAudio = GlobalParam.$view.find('#btnAudio');
            GlobalParam.audioEffect = soundStatus;
            if (soundStatus == 1) {
                $btnAudio.addClass('abacus_btn_on');
            } else {
                $btnAudio.removeClass('abacus_btn_on');
            }
            $btnAudio.on('click', function (e) {
                var $me = $(this);
                if ($me.hasClass('abacus_btn_on')) {
                    $me.removeClass('abacus_btn_on');
                    GlobalParam.audioEffect = 0;
                    GlobalParam.setting.sound_status = 0;
                } else {
                    $me.addClass('abacus_btn_on');
                    GlobalParam.audioEffect = 1;
                    GlobalParam.setting.sound_status = 1;
                }
            });
        },
        aboutAbacus: function () {
            var self = this;

            GlobalParam.$view.find('#btnAbout').on('click', function (e) {
                //算盘类型 1=五珠，2=七珠
                var abacusType = GlobalParam.setting.abacus_type;
                GlobalParam.$view.find('.abacus_tips').fadeOut();
                $(this).toggleClass('abacus_btn_on');
                if ($(this).hasClass('abacus_btn_on')) { //打开关于算盘
                    GlobalParam.$view.find('#btnSevenBead,#btnFiveBead,#btnGravity,#btnAudio').off('click');
                    //关于算盘是7珠的，如果是在5珠模式下，要去掉abacus_normal
                    GlobalParam.$view.find('._abacus_frame').removeClass('abacus_normal');
                    //点击关于算盘关于重力感应 修复 74961 handleSensor(false);
                    GlobalParam.$view.find('#btnAllParts').removeClass('hide_dom');
                    GlobalParam.$view.find('._view_abacus_areas').removeClass('hide_dom');
                    GlobalParam.$view.find('._abacus_click_areas').removeClass('hide_dom');
                    GlobalParam.$view.find('._canvas_game_areas').addClass('hide_dom');
                    GlobalParam.$view.find('.abacus_box').addClass('abacus_box_view');
                    //打开关于算盘时，按钮不可用
                    GlobalParam.$view.find('#btnSevenBead').addClass('disabled');
                    GlobalParam.$view.find('#btnSevenBead').removeClass('abacus_btn_on');
                    GlobalParam.$view.find('#btnFiveBead').addClass('disabled');
                    GlobalParam.$view.find('#btnFiveBead').removeClass('abacus_btn_on');
                    GlobalParam.$view.find('#btnGravity').addClass('disabled');
                    GlobalParam.$view.find('#btnGravity').removeClass('abacus_btn_on');
                    GlobalParam.$view.find('#btnAudio').addClass('disabled');
                    GlobalParam.$view.find('#btnAudio').removeClass('abacus_btn_on');
                } else { //关闭关于算盘
                    self.beadType();
                    self.gravityInduction();
                    self.audioEffect();
                    GlobalParam.$view.find('.abacus_pop').trigger('click');
                    //如果原来是5珠的，关掉关于算盘要恢复梁的单位
                    if (abacusType == 1) {
                        GlobalParam.$view.find('._abacus_frame').addClass('abacus_normal');
                    }
                    //点击关于算盘关于重力感应 修复 74961 handleSensor(true);
                    GlobalParam.$view.find('#btnAllParts').addClass('hide_dom');
                    GlobalParam.$view.find('._view_abacus_areas').addClass('hide_dom');
                    GlobalParam.$view.find('._abacus_click_areas').addClass('hide_dom');
                    GlobalParam.$view.find('._canvas_game_areas').removeClass('hide_dom');
                    GlobalParam.$view.find('.abacus_box').removeClass('abacus_box_view');
                    //关闭关于算盘时，按钮可用
                    GlobalParam.$view.find('#btnSevenBead').removeClass('disabled');
                    GlobalParam.$view.find('#btnFiveBead').removeClass('disabled');
                    GlobalParam.$view.find('#btnGravity').removeClass('disabled');
                    GlobalParam.$view.find('#btnAudio').removeClass('disabled');
                    animationObj.savePosition();
                    animationObj.resizeScreen(animationObj.ratio);
                }

            });
            if (GlobalParam.isOpenAboutAbacus && !$(this).hasClass('abacus_btn_on')) {
                GlobalParam.$view.find('#btnAbout').trigger('click');
            }

            // 点击遮罩就隐藏遮罩
            GlobalParam.$view.find('.abacus_pop').on('click', function (e) {
                $(this).fadeOut();
                //框
                GlobalParam.$view.find('#framePic').fadeOut();
                GlobalParam.$view.find('#frameText').fadeOut();
                //梁
                GlobalParam.$view.find('#beamPic').fadeOut();
                GlobalParam.$view.find('#beamText').fadeOut();
                //档
                GlobalParam.$view.find('#shelvesPic').fadeOut();
                GlobalParam.$view.find('#shelvesText').fadeOut();
                //上珠
                GlobalParam.$view.find('#upBeadPic').fadeOut();
                GlobalParam.$view.find('#upBeadText').fadeOut();
                //下珠
                GlobalParam.$view.find('#downBeadPic').fadeOut();
                GlobalParam.$view.find('#downBeadText').fadeOut();
                //去掉部件淡化透明
                GlobalParam.$view.find('._abacus_frame,.abacus_beam_bg,.pillars_bg,.abacus_top_areas,.abacus_bottom_areas').removeClass('transparent_bacus_part');
            });

        },
        allAllParts: function () {
            GlobalParam.$view.find('#btnAllParts').on('click', function (e) {
                GlobalParam.$view.find('.abacus_tips').fadeIn();
                //去掉部件淡化透明
                GlobalParam.$view.find('._abacus_frame,.abacus_beam_bg,.pillars_bg,.abacus_top_areas,.abacus_bottom_areas').removeClass('transparent_bacus_part');
            });
            //框显示
            GlobalParam.$view.find('#frameArea').on('click', function (e) {
                //如果是已经是可见状态，则关闭
                if (GlobalParam.$view.find('#framePic').is(':visible')) {
                    GlobalParam.$view.find('.abacus_pop').fadeOut();
                    GlobalParam.$view.find('#framePic').fadeOut();
                    GlobalParam.$view.find('#frameText').fadeOut();
                    GlobalParam.$view.find('._abacus_frame').removeClass('transparent_bacus_part');
                    return;
                }
                //隐藏全部部件
                GlobalParam.$view.find('.abacus_tips').fadeOut();
                GlobalParam.$view.find('.abacus_pop').fadeIn();
                GlobalParam.$view.find('.abacus_pop div').fadeOut();
                GlobalParam.$view.find('#framePic').fadeIn();
                GlobalParam.$view.find('#frameText').fadeIn();
                GlobalParam.$view.find('._abacus_frame,.abacus_beam_bg,.pillars_bg,.abacus_top_areas,.abacus_bottom_areas').removeClass('transparent_bacus_part');
                GlobalParam.$view.find('._abacus_frame').addClass('transparent_bacus_part');
            });
            //梁显示
            GlobalParam.$view.find('#beamArea').on('click', function (e) {
                //如果是已经是可见状态，则关闭
                if (GlobalParam.$view.find('#beamPic').is(':visible')) {
                    GlobalParam.$view.find('.abacus_pop').fadeOut();
                    GlobalParam.$view.find('#beamPic').fadeOut();
                    GlobalParam.$view.find('#beamText').fadeOut();
                    GlobalParam.$view.find('.abacus_beam_bg').removeClass('transparent_bacus_part');
                    return;
                }
                //隐藏全部部件
                GlobalParam.$view.find('.abacus_tips').fadeOut();
                GlobalParam.$view.find('.abacus_pop').fadeIn();
                GlobalParam.$view.find('.abacus_pop div').fadeOut();
                GlobalParam.$view.find('#beamPic').fadeIn();
                GlobalParam.$view.find('#beamText').fadeIn();
                GlobalParam.$view.find('._abacus_frame,.abacus_beam_bg,.pillars_bg,.abacus_top_areas,.abacus_bottom_areas').removeClass('transparent_bacus_part');
                GlobalParam.$view.find('.abacus_beam_bg').addClass('transparent_bacus_part');
            });
            //档显示
            GlobalParam.$view.find('#shelvesArea .areas_btns3_list').on('click', function (e) {
                //如果是已经是可见状态，则关闭
                if (GlobalParam.$view.find('#shelvesPic').is(':visible')) {
                    GlobalParam.$view.find('.abacus_pop').fadeOut();
                    GlobalParam.$view.find('#shelvesPic').fadeOut();
                    GlobalParam.$view.find('#shelvesText').fadeOut();
                    GlobalParam.$view.find('.pillars_bg').removeClass('transparent_bacus_part');
                    return;
                }
                //隐藏全部部件
                GlobalParam.$view.find('.abacus_tips').fadeOut();
                GlobalParam.$view.find('.abacus_pop').fadeIn();
                GlobalParam.$view.find('.abacus_pop div').fadeOut();
                GlobalParam.$view.find('#shelvesPic').fadeIn();
                GlobalParam.$view.find('#shelvesText').fadeIn();
                GlobalParam.$view.find('._abacus_frame,.abacus_beam_bg,.pillars_bg,.abacus_top_areas,.abacus_bottom_areas').removeClass('transparent_bacus_part');
                GlobalParam.$view.find('.pillars_bg').addClass('transparent_bacus_part');
            });
            //上珠显示
            GlobalParam.$view.find('#upBeadArea').on('click', function (e) {
                //如果是已经是可见状态，则关闭
                if (GlobalParam.$view.find('#upBeadPic').is(':visible')) {
                    GlobalParam.$view.find('.abacus_pop').fadeOut();
                    GlobalParam.$view.find('#upBeadPic').fadeOut();
                    GlobalParam.$view.find('#upBeadText').fadeOut();
                    GlobalParam.$view.find('.abacus_top_areas').removeClass('transparent_bacus_part');
                    return;
                }
                //隐藏全部部件
                GlobalParam.$view.find('.abacus_tips').fadeOut();
                GlobalParam.$view.find('.abacus_pop').fadeIn();
                GlobalParam.$view.find('.abacus_pop div').fadeOut();
                GlobalParam.$view.find('#upBeadPic').fadeIn();
                GlobalParam.$view.find('#upBeadText').fadeIn();
                GlobalParam.$view.find('._abacus_frame,.abacus_beam_bg,.pillars_bg,.abacus_top_areas,.abacus_bottom_areas').removeClass('transparent_bacus_part');
                GlobalParam.$view.find('.abacus_top_areas').addClass('transparent_bacus_part');
            });
            //下珠显示
            GlobalParam.$view.find('#downBeadArea').on('click', function (e) {
                //如果是已经是可见状态，则关闭
                if (GlobalParam.$view.find('#downBeadPic').is(':visible')) {
                    GlobalParam.$view.find('.abacus_pop').fadeOut();
                    GlobalParam.$view.find('#downBeadPic').fadeOut();
                    GlobalParam.$view.find('#downBeadText').fadeOut();
                    GlobalParam.$view.find('.abacus_bottom_areas').removeClass('transparent_bacus_part');
                    return;
                }
                //隐藏全部部件
                GlobalParam.$view.find('.abacus_tips').fadeOut();
                GlobalParam.$view.find('.abacus_pop').fadeIn();
                GlobalParam.$view.find('.abacus_pop div').fadeOut();
                GlobalParam.$view.find('#downBeadPic').fadeIn();
                GlobalParam.$view.find('#downBeadText').fadeIn();
                GlobalParam.$view.find('._abacus_frame,.abacus_beam_bg,.pillars_bg,.abacus_top_areas,.abacus_bottom_areas').removeClass('transparent_bacus_part');
                GlobalParam.$view.find('.abacus_bottom_areas').addClass('transparent_bacus_part');
            });
        }
    };
    return presenter;
}
