addon.presenter:
"use strict";

(function () {
    var helper = {
        domObj: {},
        /*所有用到的dom对象*/
        Acceleration: null
    };
    var i18nModel = {
        "car_btn": "小汽车",
        "train_btn": "动车",
        "tank_btn": "坦克",
        "time_data": "时间：",
        "positive_direction": "正方向",
        "start_btn": "开始",
        "reset_btn": "重置",
        "pause_btn": "暂停",
        "replay_btn": "重播"
    };

    var AccelerationController;
    var Presenter = BasicPresenter.extend({
        metaInfo: {
            name: 'Acceleration',
            type: "",
            subtype: ""
        },
        /**
         * Presenter的初始化方法
         * @private
         */
        $init: function $init() {
            this._super();
        },
        /**
         * Presenter对外暴露的方法
         */
        _service_: {
            constructor: function constructor(parent) {
                this.parent = parent;
            },
            setLocationProperties: function setLocationProperties(data) {
                $.extend(i18nModel, data || {});
            },
            getExtendData: function getExtendData() {
                var options = helper.Acceleration.getData();
                return options;
            },
            getQuestionInfo: function getQuestionInfo() {
                return {
                    id: '',
                    type_code: 'Acceleration',
                    type_name: '加速度',
                    url: '',
                    noExistStatistics: true,
                    noNeedQuizProgress: true
                };
            }
        },
        getService: function getService() {
            this._service_.constructor(this);
            return this._service_;
        },
        /****以下开始为icPlayer的生命周期方法*****/
        run: function run(view, model) {
            var $deferred = $.Deferred();
            var that = this;
            var data = model.__ExtendModel;
            helper.$view = that.$view = $(view);
            helper.model = model;
            that.model = model;
            helper.basePath = that.basePath;

            require(['AccelerationController'], function (Acceleration) {
                AccelerationController = Acceleration;
                $(view).css('visibility', 'hidden');
                helper.Acceleration = new AccelerationController(helper, i18nModel);

                setTimeout(function () {
                    helper.Acceleration.render(data);
                    $(view).css('visibility', 'visible');
                    $deferred.resolve();
                }, 500);
            }).then(function () {});
            return $deferred.promise();
        },

        __interface: {
            resizeScreen: function resizeScreen(data) {
                //data {ratio:4/3||3/4}

                var isFullScreen,
                    scale = 1.333333;
                if (eval(data.ratio) == eval('4/3')) {
                    //    全屏
                    isFullScreen = true;
                } else {
                    //    缩小全屏
                    isFullScreen = false;
                }
                helper.Acceleration.resizeFun(isFullScreen, scale);
            }

        },
        pageShow: function pageShow() {
            this._bindEvent();
        },
        pageLeave: function pageLeave() {
            this._unbindEvent();
        },
        destroy: function destroy() {
            helper.Acceleration.destroy();
        },
        /**如果不需要处理icplayer的状态恢复事件, 请将以下两个方法删除掉**/
        getState: function getState() {},
        setState: function setState(state, options) {},
        /****以下开始为Presenter的私有方法*****/
        _bindEvent: function _bindEvent() {},
        _unbindEvent: function _unbindEvent() {}
    });

    window.AddonAcceleration_create = function () {
        return new Presenter('Acceleration');
    };
})();
'use strict';

define('AccelerationBase', ['VTDynamic'], function (VTDynamic) {

    var AccelerationBase = Class.extend({
        $init: function $init(helper) {
            this._helper = helper;
            this._node = null;
            this._subFrameTime = 0;
            this._restoreAnimTime = 0;
        },
        setNode: function setNode(node) {
            this._node = node;
        },
        getNode: function getNode() {
            return this._node;
        },
        setType: function setType(type) {
            this._type = type;
        },
        getType: function getType() {
            return this._type;
        },
        setAnimationData: function setAnimationData(data) {
            var that = this,
                scale = 13.5;
            that._scale = that._helper.domObj._$js_acc_main.width() / 1440;
            var node = that.getNode(),
                type = that.getType(),
                t = data.time,
                v0 = data.v0,
                v1 = data.v1;
            var initLeft = node.position().left;

            if (type == 'train') {
                t = t / 10;
                scale = 14.5;
            }
            if (type == 'tank') {
                scale = 15;
            }
            var a = (v1 - v0) / t;
            var v0IntervalNum = v0 / 5,
                v1IntervalNum = v1 / 5,
                timeIntervalNum = t;
            if (type == 'car') {
                timeIntervalNum = t / 2;
            }

            var subLeft = v0 * scale * that._restoreAnimTime + a * scale * (that._restoreAnimTime * that._restoreAnimTime) / 2;

            $(node).css('margin-left', subLeft * that._scale + 'px');

            that.showVTLine(v0IntervalNum, v1IntervalNum, timeIntervalNum);
            if (that._restoreAnimTime >= t) {
                that._vtDynamic._drawFinishedData(v0IntervalNum, v1IntervalNum, timeIntervalNum);
            }
            if (that._anim) {
                that._anim.stop();
            }

            that._anim = new Konva.Animation(function (frame) {

                if (data.v0 == 0 && data.v1 == 0) {
                    that._helper.domObj._$play_btn.trigger('setBtnReplay');
                }
                var frameTime = frame.time / 1000 + that._restoreAnimTime;
                var left = v0 * scale * frameTime + a * scale * (frameTime * frameTime) / 2;

                $(node).css('margin-left', left * that._scale + 'px');

                if (frameTime >= t) {
                    that._anim.stop();
                    that._helper.domObj._$play_btn.addClass('_js_finish_state').removeClass('_js_play_state').removeClass('_js_static_state');
                    that._helper.domObj._$play_btn.trigger('setBtnReplay');
                    player.controller.getEventBus().sendEvent("PLAYER.TOOL.FORBIDE_FULL", {
                        id: that._helper.model.$.unqiueId,
                        isForbide: false
                    });
                    that._subFrameTime = t * 1000;
                    that._vtDynamic._drawFinishedData(v0IntervalNum, v1IntervalNum, timeIntervalNum);
                    if (data.Acceleration == 0) {
                        that._showAcceleration(0);
                    } else {
                        that._showAcceleration(data.Acceleration);
                    }
                }
            });
        },
        reset: function reset() {
            var that = this;
            that._hideAcceleration();
            that.setRestoreAnimTime(0);
            that._subFrameTime = 0;
            that._helper.domObj._$play_btn.addClass('_js_static_state').removeClass('_js_play_state').removeClass('_js_finish_state');
            that._vtDynamic.reset();
            if (that._anim) {
                that._anim.stop();
            }
        },
        setRestoreAnimTime: function setRestoreAnimTime(time) {
            var that = this;
            that._restoreAnimTime = time;
        },
        pause: function pause() {
            var that = this;
            that._helper.domObj._$play_btn.addClass('_js_static_state').removeClass('_js_play_state').removeClass('_js_finish_state');
            that._vtDynamic.pause();
            if (that._anim) {
                that._subFrameTime = that._anim.frame.time;
                that._anim.stop();
            }
        },
        getSubFrameTime: function getSubFrameTime() {
            var that = this;
            return that._subFrameTime;
        },
        play: function play(data) {
            var that = this;
            that._scale = that._helper.domObj._$js_acc_main.width() / 1440;
            that._helper.domObj._$play_btn.addClass('_js_play_state').removeClass('_js_static_state').removeClass('_js_finish_state');
            //物体运动
            if (data.v0 == 0 & data.v1 == 0) {
                that._showAcceleration(0);
            }
            that._vtDynamic.play();
            if (that._anim) {
                that._subFrameTime = 0;
                that._anim.start();
            }
        },
        replay: function replay(data) {
            var that = this;
            that._helper.domObj._$play_btn.addClass('_js_play_state').removeClass('_js_static_state').removeClass('_js_finish_state');
            //创建动画
            if (data.v0 == 0 & data.v1 == 0) {
                that._showAcceleration(0);
            }
            that._vtDynamic.play();
            if (that._anim) that._anim.start();
        },
        showVTAxis: function showVTAxis($container) {
            var that = this;
            var type = this.getType();
            this._vtDynamic = new VTDynamic($container, that._helper);

            that._vtDynamic.render(type);
        },
        showVTLine: function showVTLine(v0IntervalNum, v1IntervalNum, time) {
            var that = this,
                timeScale = 1,
                restoreTime = that._restoreAnimTime,
                type = that.getType();
            if (type == 'car') {
                timeScale = 2;
                restoreTime = restoreTime / 2;
            }
            that._vtDynamic._drawTrace(v0IntervalNum, v1IntervalNum, time, timeScale, restoreTime);
        },
        _showAcceleration: function _showAcceleration(acceleration) {
            var that = this;
            var type = that.getType(),
                car_dataList = that._helper.domObj._$car_data_ul,
                train_dataList = that._helper.domObj._$train_data_ul,
                tank_dataList = that._helper.domObj._$tank_data_ul;
            switch (type) {
                case 'car':
                    car_dataList.find('._js_Acceleration_wenhao').addClass('hide_dom');
                    car_dataList.find('._js_Acceleration').html(acceleration);
                    break;
                case 'train':
                    train_dataList.find('._js_Acceleration_wenhao').addClass('hide_dom');
                    train_dataList.find('._js_Acceleration').html(acceleration);
                    break;
                default:
                    tank_dataList.find('._js_Acceleration_wenhao').addClass('hide_dom');
                    tank_dataList.find('._js_Acceleration').html(acceleration);
            }
        },
        destroy: function destroy() {
            var that = this;
            that._vtDynamic.destroy();
        },
        _hideAcceleration: function _hideAcceleration() {
            var that = this;
            var type = that.getType(),
                car_dataList = that._helper.domObj._$car_data_ul,
                train_dataList = that._helper.domObj._$train_data_ul,
                tank_dataList = that._helper.domObj._$tank_data_ul;
            switch (type) {
                case 'car':
                    car_dataList.find('._js_Acceleration_wenhao').removeClass('hide_dom');
                    car_dataList.find('._js_Acceleration').html('');
                    break;
                case 'train':
                    train_dataList.find('._js_Acceleration_wenhao').removeClass('hide_dom');
                    train_dataList.find('._js_Acceleration').html('');
                    break;
                default:
                    tank_dataList.find('._js_Acceleration_wenhao').removeClass('hide_dom');
                    tank_dataList.find('._js_Acceleration').html('');
            }
        },
        scaleFun: function scaleFun(scaleX, scaleY, isFullScreen) {
            var that = this;
            that._vtDynamic.scaleFun(scaleX, scaleY, isFullScreen);
        }

    });

    return AccelerationBase;
});
'use strict';

define('AccelerationController', ['Car', 'Train', 'Tank'], function (Car, Train, Tank) {

    var AccelerationController = Class.extend({
        $init: function $init(helper, i18nModel) {
            this._helper = helper;
            this.i18nModel = i18nModel;
            this._car = new Car(this._helper);
            this._train = new Train(this._helper);
            this._tank = new Tank(this._helper);

            //多语言翻译
            this._findView(this._helper.$view);
            this._initViewWidth = this._helper.domObj._$car_vtdynamic_box.width();
            this._initViewHeight = this._helper.domObj._$car_vtdynamic_box.height();
            this._i18n(i18nModel);

            this._currentType = 'car'; //car或train,tank
        },
        _findView: function _findView($view) {
            //汽车，动车，坦克的tab选项
            this._helper.domObj._$car_btn = $view.find('._js_car_btn');
            this._helper.domObj._$train_btn = $view.find('._js_train_btn');
            this._helper.domObj._$tank_btn = $view.find('._js_tank_btn');
            this._helper.domObj._$move_area = $view.find('._js_main_move_area');
            //汽车，动车，坦克
            this._helper.domObj._$car = $view.find('._js_acc_move_car');
            this._helper.domObj._$train = $view.find('._js_acc_move_train');
            this._helper.domObj._$tank = $view.find('._js_acc_move_tank');
            //绘制vt图像的容器
            this._helper.domObj._$car_vtdynamic_box = $view.find('._js_car_vtdynamic_box');
            this._helper.domObj._$train_vtdynamic_box = $view.find('._js_train_vtdynamic_box');
            this._helper.domObj._$tank_vtdynamic_box = $view.find('._js_tank_vtdynamic_box');
            this._helper.domObj._$acc_data_box = $view.find('._js_acc_data_box');

            //汽车，动车，坦克的数据列表
            this._helper.domObj._$car_data_ul = $view.find('._js_acc_data_list_car');
            this._helper.domObj._$train_data_ul = $view.find('._js_acc_data_list_train');
            this._helper.domObj._$tank_data_ul = $view.find('._js_acc_data_list_tank');
            //底部开始，重置按钮
            this._helper.domObj._$play_btn = $view.find('._js_play_btn');
            this._helper.domObj._$reset_btn = $view.find('._js_reset_btn');
            this._helper.domObj._$pause_btn = $view.find('._js_pause_btn');
            this._helper.domObj._$select = $view.find('select');

            this._helper.domObj._$js_acc_main = $view.find('._js_Acceleration_main');
            this._helper.domObj._$js_acc_main_top = $view.find('._js_main_top');

            //V0,V1,Time下拉框
            this._helper.domObj._$js_input_com = $view.find('._js_input_com');

            this._helper.domObj._$js_dropinput_v0 = $view.find('._js_dropinput_v0');
            this._helper.domObj._$js_dropinput_v1 = $view.find('._js_dropinput_v1');
            this._helper.domObj._$js_dropinput_time = $view.find('._js_dropinput_time');

            this._helper.domObj._$js_input_keyboard = $view.find('._js_input_keyboard');
            this._helper.domObj._$js_input_btn = $view.find('._js_input_btn');
            this._helper.domObj._$js_Acceleration = $view.find('._js_Acceleration');
            this._helper.domObj._$js_Acceleration_wenhao = $view.find('._js_Acceleration_wenhao');

            //底部操作
            this._helper.domObj._$js_acc_btns_list = $view.find('._js_acc_btns_list');
            this._helper.domObj._$js_acc_tabs_list = $view.find('._js_acc_tabs_list');
            this._helper.domObj._$js_change_btn = $view.find('._js_change_btn');

            //   ------------------------- 多语言翻译------------------------------------
            //    正方向
            this._helper.domObj._$positive_direction = $view.find('._js_positive_direction');
            this._helper.domObj._$positive_direction_box = $view.find('._js_positive_direction_box');
            //    时间
            this._helper.domObj._$js_time_text = $view.find('._js_time_text');
        },
        _i18n: function _i18n(i18nModel) {
            this._helper.domObj._$car_btn.find('span').html(i18nModel['car_btn']);
            this._helper.domObj._$train_btn.find('span').html(i18nModel['train_btn']);
            this._helper.domObj._$tank_btn.find('span').html(i18nModel['tank_btn']);

            this._helper.domObj._$reset_btn.find('._js_txt').html(i18nModel['reset_btn']);
            this._helper.domObj._$play_btn.find('._js_txt').html(i18nModel['start_btn']);

            this._helper.domObj._$positive_direction.html(i18nModel['positive_direction']);

            this._helper.domObj._$js_time_text.html(i18nModel['time_data']);

            this._helper.domObj._$pause_btn.find('._js_txt').html(i18nModel['pause_btn']);
        },
        isEmptyObject: function isEmptyObject(obj) {
            for (var name in obj) {
                return false;
            }

            return true;
        },
        render: function render(data) {
            var that = this;

            that._car.render();
            that._train.render();
            that._tank.render();
            that._scale = that._helper.domObj._$js_acc_main.width() / 1440;
            that._scale2 = that._helper.domObj._$js_acc_main.height() / 704;
            //that._helper.domObj._$car[0].style.left = 100 * that._scale + 'px';
            //that._carLeft = $(that._helper.domObj._$car[0]).offset().left;
            var isMobile = navigator.userAgent.match(/Android|iPhone|iPad|iPod|BlackBerry|IEMobile/i);
            if (isMobile) {
                //that._helper.domObj._$train.css('left', -1200 + 'px');

            }

            that.setInputData();
            that.setAnimationData();
            that._initInputData();
            if (!that.isEmptyObject(data)) {
                //推送学生端保存状态
                that.renderView(data);
            }
            that._bindEvent(this._helper.$view);
            that._bindGlobalEvent(this._helper.$view);

            this._helper.domObj._$js_dropinput_v0.attr('uuid', that.getUuid());
            this._helper.domObj._$js_dropinput_v1.attr('uuid', that.getUuid());
            this._helper.domObj._$js_dropinput_time.attr('uuid', that.getUuid());
        },
        renderInputDropBtn: function renderInputDropBtn(data) {
            var that = this;
            var car_dataList = that._helper.domObj._$car_data_ul,
                train_dataList = that._helper.domObj._$train_data_ul,
                tank_dataList = that._helper.domObj._$tank_data_ul,
                car_default_v0 = car_dataList.find('.acc_drop_numselect_v0 ._js_input_btn'),
                car_default_v1 = car_dataList.find('.acc_drop_numselect_v1 ._js_input_btn'),
                car_default_time = car_dataList.find('acc_drop_numselect_time ._js_input_btn'),
                train_default_v0 = train_dataList.find('.acc_drop_numselect_v0 ._js_input_btn'),
                train_default_v1 = train_dataList.find('.acc_drop_numselect_v1 ._js_input_btn'),
                train_default_time = train_dataList.find('.acc_drop_numselect_time ._js_input_btn'),
                tank_default_v0 = tank_dataList.find('.acc_drop_numselect_v0 ._js_input_btn'),
                tank_default_v1 = tank_dataList.find('.acc_drop_numselect_v1 ._js_input_btn'),
                tank_default_time = tank_dataList.find('.acc_drop_numselect_time ._js_input_btn');

            car_dataList.find('._js_input_btn').removeClass('ui_btn_active');
            car_dataList.find('._js_input_v0').html(data.car.v0);
            car_dataList.find('._js_input_v1').html(data.car.v1);
            car_dataList.find('._js_input_time').html(data.car.time);
            for (var i = 0; i < car_default_v0.length; i++) {
                if (parseInt($(car_default_v0[i]).find('span').html()) == data.car.v0) {
                    $(car_default_v0[i]).addClass('ui_btn_active');
                } else {
                    $(car_default_v0[i]).removeClass('ui_btn_active');
                }
            }
            for (i = 0; i < car_default_v1.length; i++) {
                if (parseInt($(car_default_v1[i]).find('span').html()) == data.car.v1) {
                    $(car_default_v1[i]).addClass('ui_btn_active');
                } else {
                    $(car_default_v1[i]).removeClass('ui_btn_active');
                }
            }
            for (i = 0; i < car_default_time.length; i++) {
                if (parseInt($(car_default_time[i]).find('span').html()) == data.car.time) {
                    $(car_default_time[i]).addClass('ui_btn_active');
                } else {
                    $(car_default_time[i]).removeClass('ui_btn_active');
                }
            }

            train_dataList.find('._js_input_btn').removeClass('ui_btn_active');
            train_dataList.find('._js_input_v0').html(data.train.v0);
            train_dataList.find('._js_input_v1').html(data.train.v1);
            train_dataList.find('._js_input_time').html(data.train.time);

            for (i = 0; i < train_default_v0.length; i++) {

                if (parseInt($(train_default_v0[i]).find('span').html()) == data.train.v0) {
                    $(train_default_v0[i]).addClass('ui_btn_active');
                } else {
                    $(train_default_v0[i]).removeClass('ui_btn_active');
                }
            }
            for (i = 0; i < train_default_v1.length; i++) {
                if (parseInt($(train_default_v1[i]).find('span').html()) == data.train.v1) {
                    $(train_default_v1[i]).addClass('ui_btn_active');
                } else {
                    $(train_default_v1[i]).removeClass('ui_btn_active');
                }
            }
            for (i = 0; i < train_default_time.length; i++) {
                if (parseInt($(train_default_time[i]).find('span').html()) == data.train.time) {
                    $(train_default_time[i]).addClass('ui_btn_active');
                } else {
                    $(train_default_time[i]).removeClass('ui_btn_active');
                }
            }

            tank_dataList.find('._js_input_btn').removeClass('ui_btn_active');
            tank_dataList.find('._js_input_v0').html(data.tank.v0);
            tank_dataList.find('._js_input_v1').html(data.tank.v1);
            tank_dataList.find('._js_input_time').html(data.tank.time);

            for (i = 0; i < tank_default_v0.length; i++) {
                if (parseInt($(tank_default_v0[i]).find('span').html()) == data.tank.v0) {
                    $(tank_default_v0[i]).addClass('ui_btn_active');
                } else {
                    $(tank_default_v0[i]).removeClass('ui_btn_active');
                }
            }
            for (i = 0; i < tank_default_v1.length; i++) {
                if (parseInt($(tank_default_v1[i]).find('span').html()) == data.tank.v1) {
                    $(tank_default_v1[i]).addClass('ui_btn_active');
                } else {
                    $(tank_default_v1[i]).removeClass('ui_btn_active');
                }
            }
            for (i = 0; i < tank_default_time.length; i++) {
                if (parseInt($(tank_default_time[i]).find('span').html()) == data.tank.time) {
                    $(tank_default_time[i]).addClass('ui_btn_active');
                } else {
                    $(tank_default_time[i]).removeClass('ui_btn_active');
                }
            }
        },

        change: function change(type) {
            var that = this;
            that._currentType = type;
            if (type == 'car') {
                that._helper.domObj._$js_acc_main.removeClass('show_train').removeClass('show_tank').addClass('show_car');
                that._car.showVTAxis(this._helper.domObj._$car_vtdynamic_box);
                that._helper.domObj._$move_area.css('margin-left', '2em');
            } else if (type == 'train') {
                that._helper.domObj._$js_acc_main.removeClass('show_car').removeClass('show_tank').addClass('show_train');
                that._train.showVTAxis(this._helper.domObj._$train_vtdynamic_box);
                that._helper.domObj._$move_area.css('margin-left', '-4em');
            } else {
                that._helper.domObj._$js_acc_main.removeClass('show_train').removeClass('show_car').addClass('show_tank');
                that._tank.showVTAxis(this._helper.domObj._$tank_vtdynamic_box);
                that._helper.domObj._$move_area.css('margin-left', '2em');
            }
        },
        destroy: function destroy() {
            var that = this;
            that.resetAnimation(); //将以前的动画暂停
            that._car.destroy();
            that._train.destroy();
            that._tank.destroy();
        },
        _bindEvent: function _bindEvent() {
            var that = this;
            that._helper.domObj._$play_btn.on('click', function (e) {
                var $this = $(this);
                //static--暂停；finish--暂停；play--播放
                that._updateBtnTxt();
                if ($this.hasClass('_js_finish_state')) {
                    that.replay();
                } else if ($this.hasClass('_js_static_state')) {
                    that.play();
                } else if ($this.hasClass('_js_play_state')) {
                    that.pause();
                }
            });
            that._helper.domObj._$reset_btn.on('click', function () {
                //重置当前预设
                that.resetAnimation(); //将以前的动画暂停
                that._initInputData(); //初始化当前预设数据
                that._initCurrentBtnState(); //初始化按钮
                that.setInputData(); //设置当前的输入框数据
                that.setAnimationData();
            });

            that._helper.domObj._$js_input_com.on('click', function () {
                var $this = $(this);
                $this.toggleClass('ui_open');
                if ($this.hasClass('ui_open')) {
                    $this.siblings('._js_input_keyboard').removeClass('hide_dom');
                } else {
                    $this.siblings('._js_input_keyboard').addClass('hide_dom');
                }
            });
            that._helper.domObj._$js_input_keyboard.on('click', function (e) {
                //键盘
                var $target = $(e.target);
                var $this = $(this);
                var $children = $this.find('.acc_numselect_wrap').children();
                $children.removeClass('ui_btn_active');
                if ($target[0].tagName.toLocaleLowerCase() !== 'a') {
                    $target = $target.closest('a');
                }
                if ($target.length > 0) {
                    //如果有选中，关闭键盘
                    $target.addClass('ui_btn_active');
                    $this.addClass('hide_dom');
                    var val = parseInt($target.find('span').text());

                    $this.closest('.acc_input_box').find('._js_input_com').removeClass('ui_open');
                    $this.closest('.acc_input_box').find('.num_input_text').text(val);

                    that.resetAnimation(); //将以前的动画暂停
                    that._initCurrentBtnState(); //初始化按钮
                    that.setInputData(); //设置当前的输入框数据
                    that.setAnimationData();
                }
            });

            //底部菜单事件绑定
            that._helper.domObj._$js_acc_tabs_list.on('click', function (e) {
                var $target = $(e.target).closest('._js_tabs_item');
                if ($target.find('a').hasClass('ui_btn_active')) {
                    return;
                }
                var type = $target.data('type');
                $target.siblings().find('a').removeClass('ui_btn_active');
                $target.find('a').addClass('ui_btn_active');
                that.resetAnimation(); //将以前的动画暂停
                that._initCurrentBtnState(); //初始化当前按钮
                //that.hideVTDynamic();
                that.change(type);
                //that.showVTDynamic();
                that.setInputData();
                that.setAnimationData();
            });
            that._helper.domObj._$js_input_btn.on('click', function (e) {
                //键盘的按键
                var $target = $(e.target);
                $target.siblings().removeClass('ui_btn_active');
                $target.addClass('ui_btn_active');
            });
            that._helper.domObj._$play_btn.bind('setBtnReplay', function () {
                that._helper.domObj._$play_btn.find('._js_txt').html(that.i18nModel['replay_btn']);
                that._helper.domObj._$js_change_btn.removeClass('tool_kaishi').removeClass('tool_pause').addClass('tool_chongbo');
            });

            //    失去焦点事件
            //that._helper.domObj._$js_input_com.off('domblur');
            that._helper.domObj._$js_input_com.on('domblur', function (event, id) {
                if (id !== that._helper.domObj._$js_dropinput_v0.attr('uuid')) {
                    that._helper.domObj._$js_dropinput_v0.find('._js_input_keyboard').addClass('hide_dom');
                    that._helper.domObj._$js_dropinput_v0.find('._js_input_com').removeClass('ui_open');
                }
                if (id !== that._helper.domObj._$js_dropinput_v1.attr('uuid')) {
                    that._helper.domObj._$js_dropinput_v1.find('._js_input_keyboard').addClass('hide_dom');
                    that._helper.domObj._$js_dropinput_v1.find('._js_input_com').removeClass('ui_open');
                }
                if (id !== that._helper.domObj._$js_dropinput_time.attr('uuid')) {
                    that._helper.domObj._$js_dropinput_time.find('._js_input_keyboard').addClass('hide_dom');
                    that._helper.domObj._$js_dropinput_time.find('._js_input_com').removeClass('ui_open');
                }
            });
        },
        _initCurrentBtnState: function _initCurrentBtnState() {
            var that = this;
            that._helper.domObj._$play_btn.find('._js_txt').html(that.i18nModel['start_btn']);
            that._helper.domObj._$js_change_btn.removeClass('tool_pause').removeClass('tool_chongbo').addClass('tool_kaishi');
            that._helper.domObj._$reset_btn.find('._js_txt').html(that.i18nModel['reset_btn']);
        },
        _initInputData: function _initInputData() {
            //初始化当前预设的数据
            var that = this;
            this._helper.domObj._$js_Acceleration.html('');
            this._helper.domObj._$js_Acceleration_wenhao.removeClass('hide_dom');
            var data = {
                'car': {
                    v0: 0,
                    v1: 10,
                    time: 4
                },
                'train': {
                    v0: 0,
                    v1: 10,
                    time: 60
                },
                'tank': {
                    v0: 0,
                    v1: 10,
                    time: 6
                }
            };
            var car_dataList = that._helper.domObj._$car_data_ul,
                train_dataList = that._helper.domObj._$train_data_ul,
                tank_dataList = that._helper.domObj._$tank_data_ul,
                car_default_v0 = car_dataList.find('.acc_drop_numselect_v0 ._js_input_btn'),
                car_default_v1 = car_dataList.find('.acc_drop_numselect_v1 ._js_input_btn'),
                car_default_time = car_dataList.find('acc_drop_numselect_time ._js_input_btn'),
                train_default_v0 = train_dataList.find('.acc_drop_numselect_v0 ._js_input_btn'),
                train_default_v1 = train_dataList.find('.acc_drop_numselect_v1 ._js_input_btn'),
                train_default_time = train_dataList.find('.acc_drop_numselect_time ._js_input_btn'),
                tank_default_v0 = tank_dataList.find('.acc_drop_numselect_v0 ._js_input_btn'),
                tank_default_v1 = tank_dataList.find('.acc_drop_numselect_v1 ._js_input_btn'),
                tank_default_time = tank_dataList.find('.acc_drop_numselect_time ._js_input_btn');
            switch (that._currentType) {
                case 'car':
                    car_dataList.find('._js_input_v0').html(data.car.v0);
                    car_dataList.find('._js_input_v1').html(data.car.v1);
                    car_dataList.find('._js_input_time').html(data.car.time);

                    for (var i = 0; i < car_default_v0.length; i++) {
                        if (parseInt($(car_default_v0[i]).find('span').html()) == data.car.v0) {
                            $(car_default_v0[i]).addClass('ui_btn_active');
                        } else {
                            $(car_default_v0[i]).removeClass('ui_btn_active');
                        }
                    }
                    for (i = 0; i < car_default_v1.length; i++) {
                        if (parseInt($(car_default_v1[i]).find('span').html()) == data.car.v1) {
                            $(car_default_v1[i]).addClass('ui_btn_active');
                        } else {
                            $(car_default_v1[i]).removeClass('ui_btn_active');
                        }
                    }
                    for (i = 0; i < car_default_time.length; i++) {
                        if (parseInt($(car_default_time[i]).find('span').html()) == data.car.time) {
                            $(car_default_time[i]).addClass('ui_btn_active');
                        } else {
                            $(car_default_time[i]).removeClass('ui_btn_active');
                        }
                    }
                    break;
                case 'train':
                    train_dataList.find('._js_input_v0').html(data.train.v0);
                    train_dataList.find('._js_input_v1').html(data.train.v1);
                    train_dataList.find('._js_input_time').html(data.train.time);

                    for (i = 0; i < train_default_v0.length; i++) {

                        if (parseInt($(train_default_v0[i]).find('span').html()) == data.train.v0) {
                            $(train_default_v0[i]).addClass('ui_btn_active');
                        } else {
                            $(train_default_v0[i]).removeClass('ui_btn_active');
                        }
                    }
                    for (i = 0; i < train_default_v1.length; i++) {
                        if (parseInt($(train_default_v1[i]).find('span').html()) == data.train.v1) {
                            $(train_default_v1[i]).addClass('ui_btn_active');
                        } else {
                            $(train_default_v1[i]).removeClass('ui_btn_active');
                        }
                    }
                    for (i = 0; i < train_default_time.length; i++) {
                        if (parseInt($(train_default_time[i]).find('span').html()) == data.train.time) {
                            $(train_default_time[i]).addClass('ui_btn_active');
                        } else {
                            $(train_default_time[i]).removeClass('ui_btn_active');
                        }
                    }

                    break;
                default:
                    tank_dataList.find('._js_input_v0').html(data.tank.v0);
                    tank_dataList.find('._js_input_v1').html(data.tank.v1);
                    tank_dataList.find('._js_input_time').html(data.tank.time);

                    for (i = 0; i < tank_default_v0.length; i++) {
                        if (parseInt($(tank_default_v0[i]).find('span').html()) == data.tank.v0) {
                            $(tank_default_v0[i]).addClass('ui_btn_active');
                        } else {
                            $(tank_default_v0[i]).removeClass('ui_btn_active');
                        }
                    }
                    for (i = 0; i < tank_default_v1.length; i++) {
                        if (parseInt($(tank_default_v1[i]).find('span').html()) == data.tank.v1) {
                            $(tank_default_v1[i]).addClass('ui_btn_active');
                        } else {
                            $(tank_default_v1[i]).removeClass('ui_btn_active');
                        }
                    }
                    for (i = 0; i < tank_default_time.length; i++) {
                        if (parseInt($(tank_default_time[i]).find('span').html()) == data.tank.time) {
                            $(tank_default_time[i]).addClass('ui_btn_active');
                        } else {
                            $(tank_default_time[i]).removeClass('ui_btn_active');
                        }
                    }

            }
        },
        _updateBtnTxt: function _updateBtnTxt() {
            var that = this;
            if (that._helper.domObj._$play_btn.hasClass('_js_play_state')) {
                //按钮置为播放
                that._helper.domObj._$play_btn.find('._js_txt').html(that.i18nModel['start_btn']);
                that._helper.domObj._$js_change_btn.removeClass('tool_pause').removeClass('tool_chongbo').addClass('tool_kaishi');
            } else {
                //按钮置为暂停
                that._helper.domObj._$play_btn.find('._js_txt').html(that.i18nModel['pause_btn']);
                that._helper.domObj._$js_change_btn.removeClass('tool_kaishi').removeClass('tool_chongbo').addClass('tool_pause');
            }
        },
        setAnimationData: function setAnimationData() {
            var that = this;
            var data = that.getInputData();
            switch (that._currentType) {
                case 'car':
                    that._car.setAnimationData(data.car);
                    break;
                case 'train':
                    that._train.setAnimationData(data.train);
                    break;
                default:
                    that._tank.setAnimationData(data.tank);
            }
        },
        setRestoreAnimTime: function setRestoreAnimTime(time) {
            var that = this;
            switch (that._currentType) {
                case 'car':
                    that._car.setRestoreAnimTime(time);
                    break;
                case 'train':
                    that._train.setRestoreAnimTime(time);
                    break;
                default:
                    that._tank.setRestoreAnimTime(time);
            }
        },
        setInputData: function setInputData() {
            var that = this;
            var car_dataList = that._helper.domObj._$car_data_ul,
                train_dataList = that._helper.domObj._$train_data_ul,
                tank_dataList = that._helper.domObj._$tank_data_ul;

            that._data = {};
            that._data.car = {};
            that._data.train = {};
            that._data.tank = {};
            that._scale = that._helper.domObj._$js_acc_main.width() / 1440;

            switch (that._currentType) {
                case 'car':
                    that._data.car.v0 = parseInt(car_dataList.find('._js_input_v0').html());
                    that._data.car.v1 = parseInt(car_dataList.find('._js_input_v1').html());
                    that._data.car.time = parseInt(car_dataList.find('._js_input_time').html());
                    that._helper.domObj._$car.css('margin-left', '0px');
                    if (that._data.car.v0 < 0 || that._data.car.v0 == 0 && that._data.car.v1 < 0) {
                        //    改变小汽车初始位置
                        //TODO:这里设置area的margin-left
                        that._helper.domObj._$move_area.css('margin-left', '62em');
                    } else {
                        that._helper.domObj._$move_area.css('margin-left', '2em');
                    }

                    if (that._data.car.v1 - that._data.car.v0 == 0) {
                        that._data.car.Acceleration = 0;
                    } else {
                        that._data.car.Acceleration = ((that._data.car.v1 - that._data.car.v0) / that._data.car.time).toFixed(2);
                    }
                    break;
                case 'train':
                    that._data.train.v0 = parseInt(train_dataList.find('._js_input_v0').html());
                    that._data.train.v1 = parseInt(train_dataList.find('._js_input_v1').html());
                    that._data.train.time = parseInt(train_dataList.find('._js_input_time').html());
                    if (that._data.train.v1 - that._data.train.v0 == 0) {
                        that._data.train.Acceleration = 0;
                    } else {
                        that._data.train.Acceleration = ((that._data.train.v1 - that._data.train.v0) / that._data.train.time).toFixed(2);
                    }
                    var isMobile = navigator.userAgent.match(/Android|iPhone|iPad|iPod|BlackBerry|IEMobile/i);

                    that._helper.domObj._$train.css('margin-left', '0px');
                    break;
                default:
                    that._data.tank.v0 = parseInt(tank_dataList.find('._js_input_v0').html());
                    that._data.tank.v1 = parseInt(tank_dataList.find('._js_input_v1').html());
                    that._data.tank.time = parseInt(tank_dataList.find('._js_input_time').html());
                    if (that._data.tank.v1 - that._data.tank.v0 == 0) {
                        that._data.tank.Acceleration = 0;
                    } else {
                        that._data.tank.Acceleration = ((that._data.tank.v1 - that._data.tank.v0) / that._data.tank.time).toFixed(2);
                    }
                    that._helper.domObj._$tank.css('margin-left', '0px');
            }
        },
        getInputData: function getInputData() {
            var that = this;
            return that._data;
        },
        resetAnimation: function resetAnimation() {
            var that = this;
            player.controller.getEventBus().sendEvent("PLAYER.TOOL.FORBIDE_FULL", {
                id: that._helper.model.$.unqiueId,
                isForbide: false
            });
            switch (that._currentType) {
                case 'car':
                    that._car.reset();
                    break;
                case 'train':
                    that._train.reset();
                    break;
                default:
                    that._tank.reset();
            }
        },
        _bindGlobalEvent: function _bindGlobalEvent($view) {
            var that = this;
            var handler = function handler(e) {
                var $target = $(e.target);
                var blur = $target.closest('._js_blur');
                var id = blur.attr('uuid');
                that._helper.domObj._$js_input_com.trigger('domblur', id || '');
            };
            $view.on('click', function (e) {
                handler(e);
            });
        },

        play: function play() {

            var that = this;
            var data = that.getInputData();
            player.controller.getEventBus().sendEvent("PLAYER.TOOL.FORBIDE_FULL", {
                id: that._helper.model.$.unqiueId,
                isForbide: true
            });
            switch (that._currentType) {
                case 'car':
                    that._car.play(data.car);
                    break;
                case 'train':
                    that._train.play(data.train);
                    break;
                default:
                    that._tank.play(data.tank);
            }
        },
        pause: function pause() {
            var that = this;
            player.controller.getEventBus().sendEvent("PLAYER.TOOL.FORBIDE_FULL", {
                id: that._helper.model.$.unqiueId,
                isForbide: false
            });
            switch (that._currentType) {
                case 'car':
                    that._car.pause();
                    break;
                case 'train':
                    that._train.pause();
                    break;
                default:
                    that._tank.pause();
            }
        },
        replay: function replay() {
            var that = this;
            that.resetAnimation();
            that.setInputData();
            that.setAnimationData();
            var data = that.getInputData();
            player.controller.getEventBus().sendEvent("PLAYER.TOOL.FORBIDE_FULL", {
                id: that._helper.model.$.unqiueId,
                isForbide: true
            });
            switch (that._currentType) {
                case 'car':
                    that._car.replay(data.car);
                    break;
                case 'train':
                    that._train.replay(data.car);
                    break;
                default:
                    that._tank.replay(data.car);
            }
        },
        getUuid: function getUuid() {
            var s = [];
            var hexDigits = "0123456789abcdef";
            for (var i = 0; i < 36; i++) {
                s[i] = hexDigits.substr(Math.floor(Math.random() * 0x10), 1);
            }
            s[14] = "4"; // bits 12-15 of the time_hi_and_version field to 0010
            s[19] = hexDigits.substr(s[19] & 0x3 | 0x8, 1); // bits 6-7 of the clock_seq_hi_and_reserved to 01
            s[8] = s[13] = s[18] = s[23] = "-";

            var uuid = s.join("");
            return uuid;
        },
        getSubFrameTime: function getSubFrameTime() {
            var that = this;
            switch (that._currentType) {
                case 'car':
                    return that._car.getSubFrameTime();
                    break;
                case 'train':
                    return that._train.getSubFrameTime();
                    break;
                default:
                    return that._tank.getSubFrameTime();
            }
        },
        _showAcceleration: function _showAcceleration(inputData, type) {
            var that = this,
                car_dataList = that._helper.domObj._$car_data_ul,
                train_dataList = that._helper.domObj._$train_data_ul,
                tank_dataList = that._helper.domObj._$tank_data_ul;
            switch (type) {
                case 'car':
                    car_dataList.find('._js_Acceleration_wenhao').addClass('hide_dom');
                    car_dataList.find('._js_Acceleration').html(inputData.car.Acceleration);
                    break;
                case 'train':
                    train_dataList.find('._js_Acceleration_wenhao').addClass('hide_dom');
                    train_dataList.find('._js_Acceleration').html(inputData.train.Acceleration);
                    break;
                default:
                    tank_dataList.find('._js_Acceleration_wenhao').addClass('hide_dom');
                    tank_dataList.find('._js_Acceleration').html(inputData.tank.Acceleration);
            }
        },
        renderView: function renderView(data) {
            var index,
                that = this,
                type = data.currentType;
            var inputData = data.inputData;
            if (type == 'car') {
                index = 0;
            } else if (type == 'train') {
                index = 1;
            } else {
                index = 2;
            }
            that._helper.domObj._$js_acc_tabs_list.find('._js_tabs_item').find('a').removeClass('ui_btn_active');
            that._helper.domObj._$js_acc_tabs_list.find('._js_tabs_item').eq(index).find('a').addClass('ui_btn_active');
            that.change(type);
            if (data.isRunning) {
                //正在播放
                that._initCurrentBtnState();
                that._currentPlayState = 'init';
                that.renderInputDropBtn(data.inputData);
                that.resetAnimation();
                that.setInputData();
                that.setAnimationData();
                that._updateBtnTxt();
                that.play();
            } else if (data.isFinish) {
                that._showAcceleration(inputData, data.currentType);
                that.renderInputDropBtn(data.inputData);
                that.setRestoreAnimTime(data.subFrameTime);
                that._helper.domObj._$play_btn.addClass('_js_finish_state').removeClass('_js_play_state').removeClass('_js_static_state');
                that._helper.domObj._$play_btn.find('._js_txt').html(that.i18nModel['replay_btn']);
                that._helper.domObj._$js_change_btn.removeClass('tool_kaishi').removeClass('tool_pause').addClass('tool_chongbo');
                that.setInputData();
                switch (type) {
                    case 'car':
                        that._car.setAnimationData(data.inputData.car);
                        break;
                    case 'train':
                        that._train.setAnimationData(data.inputData.train);
                        break;
                    default:
                        that._tank.setAnimationData(data.inputData.tank);
                }
                // that.play();
            } else {
                //初始或者暂停
                that.renderInputDropBtn(data.inputData);
                that.setRestoreAnimTime(data.subFrameTime);
                //按钮置为播放
                that._helper.domObj._$play_btn.find('._js_txt').html(that.i18nModel['start_btn']);
                that._helper.domObj._$js_change_btn.removeClass('tool_pause').removeClass('tool_chongbo').addClass('tool_kaishi');

                that.setInputData();
                switch (type) {
                    case 'car':
                        that._car.setAnimationData(data.inputData.car);
                        break;
                    case 'train':
                        that._train.setAnimationData(data.inputData.train);
                        break;
                    default:
                        that._tank.setAnimationData(data.inputData.tank);
                }
            }
        },
        getData: function getData() {
            var that = this;
            var data = {};
            data.position = {};
            data.currentType = that._currentType;
            data.inputData = that.getInputData();
            data.subFrameTime = that.getSubFrameTime() / 1000;
            data.isRunning = that._helper.domObj._$play_btn.hasClass('_js_play_state');
            data.isStatic = that._helper.domObj._$play_btn.hasClass('_js_static_state');
            data.isFinish = that._helper.domObj._$play_btn.hasClass('_js_finish_state');
            return data;
        },
        resizeFun: function resizeFun(isFullScreen, scale) {
            //    全屏功能
            var that = this;
            var height = that._helper.domObj._$car_vtdynamic_box.height();
            var scaleView = height / 578;
            switch (that._currentType) {
                case 'car':
                    that._car.scaleFun(scaleView);
                    break;
                case 'train':
                    that._train.scaleFun(scaleView);
                    break;
                default:
                    that._tank.scaleFun(scaleView);
            }

            if (isFullScreen) {
                that._helper.domObj._$car.css('margin-left', parseInt(that._helper.domObj._$car[0].style.marginLeft) * scale + 'px');
                that._helper.domObj._$train.css('margin-left', parseInt(that._helper.domObj._$train[0].style.marginLeft) * scale + 'px');
                that._helper.domObj._$tank.css('margin-left', parseInt(that._helper.domObj._$tank[0].style.marginLeft) * scale + 'px');
                //    坐标轴放大
            } else {
                that._helper.domObj._$car.css('margin-left', parseInt(that._helper.domObj._$car[0].style.marginLeft) / scale + 'px');
                that._helper.domObj._$train.css('margin-left', parseInt(that._helper.domObj._$train[0].style.marginLeft) / scale + 'px');
                that._helper.domObj._$tank.css('margin-left', parseInt(that._helper.domObj._$tank[0].style.marginLeft) / scale + 'px');
                //    坐标轴缩小
            }
        }
    });
    return AccelerationController;
});
'use strict';

define('Car', ['AccelerationBase'], function (AccelerationBase) {
  var Car = AccelerationBase.extend({
    $init: function $init(helper) {
      this._super(helper);
      this._helper = helper;
      // TODO :这里初始化汽车数据
    },
    render: function render() {
      var that = this;
      that._carNode = this._helper.domObj._$car;
      that.setNode(that._carNode);
      that.setType('car');

      that.showVTAxis(this._helper.domObj._$car_vtdynamic_box);
      var isMobile = navigator.userAgent.match(/Android|iPhone|iPad|iPod|BlackBerry|IEMobile/i);
      if (isMobile) {
        that._helper.domObj._$car.css('bottom', '1.38333em');
      }
    }
  });
  return Car;
});
'use strict';

define('Tank', ['AccelerationBase'], function (AccelerationBase) {
  var Tank = AccelerationBase.extend({
    $init: function $init(helper) {
      this._super(helper);
      this._helper = helper;
    },
    render: function render() {
      var that = this;
      that._tankNode = this._helper.domObj._$tank;
      that.setNode(that._tankNode);
      that.setType('tank');
    }
  });
  return Tank;
});
'use strict';

define('Train', ['AccelerationBase', 'VTDynamic'], function (AccelerationBase) {
  var Train = AccelerationBase.extend({
    $init: function $init(helper) {
      this._super(helper);
      this._helper = helper;
      // TODO :这里初始化动车数据
    },
    render: function render() {
      var that = this;
      that._trainNode = this._helper.domObj._$train;
      that.setNode(that._trainNode);
      that.setType('train');
      var isMobile = navigator.userAgent.match(/Android|iPhone|iPad|iPod|BlackBerry|IEMobile/i);
      if (isMobile) {
        that._helper.domObj._$train.css('bottom', '0.03333em');
      }
    }

  });
  return Train;
});
"use strict";

define('VTDynamic', function (VTDynamic) {
    //坐标轴的单个间隔以及缩放系数
    var userAgent = navigator.userAgent;
    console.log(userAgent);
    var isSafari = userAgent.indexOf("Safari") > -1 && userAgent.indexOf("Chrome") < 1; //判断是否Safari
    console.log(isSafari);
    var LENGTH_OF_INTERVAL_X = 100,
        LENGTH_OF_INTERVAL_Y = 75;
    var SCALE_OF_AXIS = 1;
    //缩放值为1时的宽高信息
    var defaultPx = {
        staticHeight: 578
    };
    //坐标轴
    var AxisLineStyle = {
        stroke: '#313131'
    };
    var VTLineStyle = {
        stroke: '#f39800',
        strokeWidth: 5
    };
    var VTDashedLineStyle = {
        dashEnabled: true,
        dash: [19, 10],
        stroke: '#0098ff',
        strokeWidth: 5.5,
        lineJoin: 'bevel',
        lineCap: 'sqare'
    };
    var Margin = {
        x: 107, //x方向上间距
        top: 50, //上间距
        bigTop: 60, //下间距
        dashTop: 30 //虚线上下间距
    };
    var fontSize = 48;
    //文本样式正体
    var AxisFontStyle = {
        fontSize: fontSize,
        fontFamily: 'ndnewromanRegular',
        fill: '#384650',
        fontStyle: 'bold'
    };
    var VTDynamic = Class.extend({
        _height: defaultPx.staticHeight, //默认高度
        $init: function $init($container, helper) {
            var that = this;
            that._$container = $container;
            that._helper = helper;
        },
        render: function render(type) {
            var that = this;
            var width = that._$container.width();
            var height = that._$container.height();
            this._width = width / height * this._height;
            //缩放值
            var scale = width / this._width;
            that._mathPosInfo(type);
            that._draw(width, height, scale, type);
        },
        _draw: function _draw(width, height, scale, type) {
            this._stage = new Konva.Stage({
                container: this._$container[0],
                width: width,
                height: height
            });
            //进行缩放
            this._stage.scale({ x: scale, y: scale });
            //坐标轴和坐标原点
            this._axislayer = new Konva.Layer();
            //轨迹层
            this._lineLayer = new Konva.Layer();
            //绘制坐标轴
            this._drawAxis(this._midWidth, this._centerX, this._centerY, type);
            this._drawAxisTick(this._midWidth, this._centerX, this._centerY, type);

            //绘制轨迹
            //绘制完成后，缩放为目前的宽高值
            this._stage.add(this._axislayer);
            this._stage.add(this._lineLayer);
        },
        _drawAxisTick: function _drawAxisTick(width, centerX, centerY, type) {
            //绘制x轴和y轴的刻度
            var axisInterval_X = SCALE_OF_AXIS * LENGTH_OF_INTERVAL_X,
                axisInterval_Y = SCALE_OF_AXIS * LENGTH_OF_INTERVAL_Y,
                numInterval_X = this._numInterval_X;
            switch (type) {
                case 'car':
                    //画x轴的刻度
                    for (var i = 1; i <= numInterval_X; i++) {
                        var xTick = new Konva.Line($.extend({
                            points: [centerX + i * axisInterval_X, centerY + 12, centerX + i * axisInterval_X, centerY - 12],
                            strokeWidth: 5
                        }, AxisLineStyle));
                        this._axislayer.add(xTick);
                        var xTick_txt = new Konva.Text($.extend({
                            y: centerY + 15,
                            text: 2 + (i - 1) * 2
                        }, AxisFontStyle));
                        xTick_txt.attrs.x = centerX + i * axisInterval_X - xTick_txt.getWidth() / 2;
                        this._axislayer.add(xTick_txt);
                    }
                    //画y轴的刻度
                    for (var i = 1; i < 3; i++) {
                        var yTick = new Konva.Line($.extend({
                            points: [centerX - 12, centerY + i * axisInterval_Y, centerX + 12, centerY + i * axisInterval_Y],
                            strokeWidth: 5
                        }, AxisLineStyle));
                        this._axislayer.add(yTick);

                        yTick = new Konva.Line($.extend({
                            points: [centerX - 12, centerY - i * axisInterval_Y, centerX + 12, centerY - i * axisInterval_Y],
                            strokeWidth: 5
                        }, AxisLineStyle));
                        this._axislayer.add(yTick);

                        var yTick_txt = new Konva.Text($.extend({
                            text: -5 - (i - 1) * 5
                        }, AxisFontStyle));
                        console.log(yTick_txt.attrs.x, yTick_txt.getWidth());
                        yTick_txt.attrs.x = centerX - 50 - yTick_txt.getWidth() / 2;
                        yTick_txt.attrs.y = centerY + i * axisInterval_Y - yTick_txt.getHeight() / 2;
                        this._axislayer.add(yTick_txt);

                        yTick_txt = new Konva.Text($.extend({
                            x: centerX - 35 - yTick_txt.getWidth() / 2,
                            y: centerY - i * axisInterval_Y - yTick_txt.getHeight() / 2,
                            text: 5 + (i - 1) * 5
                        }, AxisFontStyle));
                        this._axislayer.add(yTick_txt);
                    }
                    break;
                case 'train':
                    for (var i = 1; i <= numInterval_X; i++) {
                        var xTick = new Konva.Line($.extend({
                            points: [centerX + i * axisInterval_X, centerY + 12, centerX + i * axisInterval_X, centerY - 12],
                            strokeWidth: 5
                        }, AxisLineStyle));
                        this._axislayer.add(xTick);
                        var xTick_txt = new Konva.Text($.extend({
                            y: centerY + 15,
                            align: 'center',
                            text: 10 + (i - 1) * 10
                        }, AxisFontStyle));
                        xTick_txt.attrs.x = centerX + i * axisInterval_X - xTick_txt.getWidth() / 2;
                        this._axislayer.add(xTick_txt);
                    }
                    //画y轴的刻度
                    for (var i = 1; i < 5; i++) {
                        var yTick = new Konva.Line($.extend({
                            points: [centerX - 12, centerY - i * axisInterval_Y, centerX + 12, centerY - i * axisInterval_Y],
                            strokeWidth: 5
                        }, AxisLineStyle));
                        this._axislayer.add(yTick);
                        var yTick_txt = new Konva.Text($.extend({
                            text: 5 + (i - 1) * 5
                        }, AxisFontStyle));
                        yTick_txt.attrs.x = centerX - 40 - yTick_txt.getWidth() / 2;
                        yTick_txt.attrs.y = centerY - i * axisInterval_Y - yTick_txt.getHeight() / 2;
                        this._axislayer.add(yTick_txt);
                    }
                    break;
                default:
                    for (var i = 1; i <= numInterval_X; i++) {
                        var xTick = new Konva.Line($.extend({
                            points: [centerX + i * axisInterval_X, centerY + 12, centerX + i * axisInterval_X, centerY - 12],
                            strokeWidth: 5
                        }, AxisLineStyle));
                        this._axislayer.add(xTick);
                        var xTick_txt = new Konva.Text($.extend({
                            y: centerY + 15,
                            align: 'center',
                            text: 1 + (i - 1)
                        }, AxisFontStyle));
                        xTick_txt.attrs.x = centerX + i * axisInterval_X - xTick_txt.getWidth() / 2;

                        this._axislayer.add(xTick_txt);
                    }
                    //画y轴的刻度
                    for (var i = 1; i < 5; i++) {
                        var yTick = new Konva.Line($.extend({
                            points: [centerX - 12, centerY - i * axisInterval_Y, centerX + 12, centerY - i * axisInterval_Y],
                            strokeWidth: 5
                        }, AxisLineStyle));
                        this._axislayer.add(yTick);
                        var yTick_txt = new Konva.Text($.extend({
                            text: 5 + (i - 1) * 5
                        }, AxisFontStyle));
                        yTick_txt.attrs.x = centerX - 40 - yTick_txt.getWidth() / 2;
                        yTick_txt.attrs.y = centerY - i * axisInterval_Y - yTick_txt.getHeight() / 2;
                        this._axislayer.add(yTick_txt);
                    }
            }
        },
        _mathPosInfo: function _mathPosInfo(type) {
            var width = this._width,
                centerX;
            //坐标轴的单个间距
            if (type == 'car') {
                SCALE_OF_AXIS = 1;
            } else {
                SCALE_OF_AXIS = 0.9;
            }
            var axisInterval_X = SCALE_OF_AXIS * LENGTH_OF_INTERVAL_X,
                axisInterval_Y = SCALE_OF_AXIS * LENGTH_OF_INTERVAL_Y;
            var midWidth = width - Margin.x * 2,
                centerY;
            //xy轴交点中心点坐标
            if (type == "car") {
                centerX = axisInterval_X * 1 + Margin.x - 20;
                centerY = axisInterval_Y * 2 + Margin.top + 72;
                this._numInterval_X = 6;
            } else {
                centerX = axisInterval_X * 1 + Margin.x - 10;
                centerY = axisInterval_Y * 4 + Margin.top + 90;
                this._numInterval_X = 7;
            }

            //存储绘制系统变量
            this._centerX = centerX;
            this._centerY = centerY;
            this._midWidth = midWidth;
            this._axisInterval_X = axisInterval_X;
            this._axisInterval_Y = axisInterval_Y;
        },
        _drawAxis: function _drawAxis(width, centerX, centerY, type) {
            //坐标x轴
            var that = this;
            that.basePath = that._helper.basePath;
            var axisInterval_X = this._axisInterval_X,
                axisInterval_Y = this._axisInterval_Y,
                numInterval_X = this._numInterval_X;
            var xLine = new Konva.Line($.extend({
                points: [Margin.x, centerY, centerX + axisInterval_X * numInterval_X + 100, centerY],
                strokeWidth: 6
            }, AxisLineStyle));
            this._axislayer.add(xLine);

            var xArrowRight = new Image();
            xArrowRight.onload = function () {
                var arrowImage = new Konva.Image({
                    x: Margin.x + xLine.getWidth() - 12,
                    y: centerY - 19,
                    image: xArrowRight,
                    width: 28,
                    height: 38
                });
                that._axislayer.add(arrowImage);
                that._axislayer.draw();
                xArrowRight.onload = null;
            };
            xArrowRight.src = that.basePath + 'resources/wood/images/icon_axis_arrow_right.png';

            var yArrowTop = new Image();
            yArrowTop.onload = function () {
                var arrowImage = new Konva.Image({
                    x: centerX - 19,
                    y: Margin.bigTop - 10,
                    image: yArrowTop,
                    width: 38,
                    height: 28
                });
                that._axislayer.add(arrowImage);
                that._axislayer.draw();
                yArrowTop.onload = null;
            };
            yArrowTop.src = that.basePath + 'resources/wood/images/icon_axis_arrow_top.png';
            //x轴单位
            var xText = new Image();
            xText.onload = function () {
                var arrowImage = new Konva.Image({
                    x: centerX + axisInterval_X * numInterval_X + 76,
                    y: centerY + 18,
                    image: xText,
                    width: 46,
                    height: 34
                });
                that._axislayer.add(arrowImage);
                that._axislayer.draw();
                xText.onload = null;
            };
            xText.src = that.basePath + 'resources/wood/images/icon_ts.png';
            //坐标y轴
            var yLine = new Konva.Line($.extend({
                points: [centerX, Margin.bigTop, centerX, Margin.bigTop + axisInterval_Y * 4 + 140],
                strokeWidth: 6
            }, AxisLineStyle));
            this._axislayer.add(yLine);
            if (type == 'car') {
                // 小汽车y轴两个箭头
                var yArrowBottom = new Image();
                yArrowBottom.onload = function () {
                    var arrowImage = new Konva.Image({
                        x: centerX - 19,
                        y: Margin.top + yLine.getHeight() - 10,
                        image: yArrowBottom,
                        width: 38,
                        height: 28
                    });
                    that._axislayer.add(arrowImage);
                    that._axislayer.draw();
                    yArrowBottom.onload = null;
                };
                yArrowBottom.src = that.basePath + 'resources/wood/images/icon_axis_arrow_bottom.png';
            }
            var yArrow = new Image();
            yArrow.onload = function () {
                var arrowImage = new Konva.Image({
                    x: Margin.x - 80,
                    y: Margin.top - fontSize + 30,
                    image: yArrow,
                    width: 137,
                    height: 43
                });
                that._axislayer.add(arrowImage);
                that._axislayer.draw();
                yArrow.onload = null;
            };
            yArrow.src = that.basePath + 'resources/wood/images/icon_vms.png';

            // 0点
            var oText = new Konva.Text({
                x: centerX - 35,
                y: centerY + 5,
                text: '0',
                fontSize: 48,
                fontFamily: 'ndnewromanRegular',
                fill: '#384650',
                fontStyle: 'bold'
            });
            this._axislayer.add(oText);
        },
        _drawTrace: function _drawTrace(v0IntervalNum, v1IntervalNum, time, timeScale, restoreTime) {
            var that = this,
                v0IntNum = v0IntervalNum,
                v1IntNum = v1IntervalNum,
                t = time,
                centerX = this._centerX,
                centerY = this._centerY,
                axisInterval_X = this._axisInterval_X,
                axisInterval_Y = this._axisInterval_Y;
            var k = (v1IntNum - v0IntNum) / t,
                yTempNum = k * restoreTime + v0IntNum;
            var lineInit = new Konva.Line($.extend({
                points: [centerX, centerY - v0IntNum * axisInterval_Y, centerX + restoreTime * axisInterval_X, centerY - yTempNum * axisInterval_Y]
            }, VTLineStyle));
            that._lineLayer.add(lineInit);
            this._lineLayer.setZIndex(0);
            this._lineLayer.draw();
            if (v0IntNum == 0 && v1IntNum == 0) {
                //原点显示一个圆
                var oCircle = new Konva.Circle({
                    x: centerX,
                    y: centerY,
                    radius: 5,
                    fill: '#f39800'
                });

                this._lineLayer.add(oCircle);
                this._lineLayer.setZIndex(99);
                that.tweenLine = new Konva.Tween({
                    node: oCircle,
                    radius: 5,
                    duration: 0
                });
            } else {
                var line = new Konva.Line($.extend({
                    points: [centerX + restoreTime * axisInterval_X, centerY - yTempNum * axisInterval_Y, centerX + restoreTime * axisInterval_X, centerY - yTempNum * axisInterval_Y]
                }, VTLineStyle));
                that._lineLayer.add(line);

                that.tweenLine = new Konva.Tween({
                    node: line,
                    duration: (t - restoreTime) * timeScale,
                    points: [centerX + restoreTime * axisInterval_X, centerY - yTempNum * axisInterval_Y, centerX + t * axisInterval_X, centerY - v1IntNum * axisInterval_Y]
                });
            }
        },
        _drawFinishedData: function _drawFinishedData(v0IntervalNum, v1IntervalNum, time) {
            var that = this;
            var axisInterval_X = this._axisInterval_X,
                axisInterval_Y = this._axisInterval_Y,
                t = time,
                v0IntNum = v0IntervalNum,
                v1IntNum = v1IntervalNum,
                centerX = this._centerX,
                centerY = this._centerY;
            //显示V0
            //结束数据层
            if (v0IntNum == 0 && v1IntNum == 0) {
                return;
            }
            this._finishLayer = new Konva.Layer();
            this._stage.add(this._finishLayer);

            if (v0IntNum == v1IntNum) {
                var V0Tick = new Image();
                V0Tick.onload = function () {
                    var arrowImage = new Konva.Image({
                        x: centerX + 23,
                        y: centerY - v0IntNum * axisInterval_Y - 50,
                        image: V0Tick,
                        width: 141,
                        height: 33
                    });
                    that._finishLayer.add(arrowImage);
                    that._finishLayer.draw();
                    V0Tick.onload = null;
                };
                V0Tick.src = that.basePath + 'resources/wood/images/icon_v0=v1.png';
            } else {
                var V0Tick = new Image();
                V0Tick.onload = function () {
                    var arrowImage = new Konva.Image({
                        x: centerX - 150,
                        y: centerY - v0IntNum * axisInterval_Y - 60 + 40,
                        image: V0Tick,
                        width: 51,
                        height: 33
                    });
                    that._finishLayer.add(arrowImage);
                    that._finishLayer.draw();
                    V0Tick.onload = null;
                };
                V0Tick.src = that.basePath + 'resources/wood/images/icon_v0_blue.png';

                var V1Tick = new Image();
                V1Tick.onload = function () {
                    var arrowImage = new Konva.Image({
                        x: centerX - 150,
                        y: centerY - v1IntNum * axisInterval_Y - 60 + 40,
                        image: V1Tick,
                        width: 49,
                        height: 33
                    });
                    that._finishLayer.add(arrowImage);
                    that._finishLayer.draw();
                    V1Tick.onload = null;
                };
                V1Tick.src = that.basePath + 'resources/wood/images/icon_v1_blue.png';
            }

            //显示T0
            var t0Tick = new Image();
            t0Tick.onload = function () {
                var arrowImage = new Konva.Image({
                    x: centerX - 16,
                    y: Margin.bigTop + axisInterval_Y * 4 + 200 - 50,
                    image: t0Tick,
                    width: 37,
                    height: 39
                });
                that._finishLayer.add(arrowImage);
                that._finishLayer.draw();
                t0Tick.onload = null;
            };
            t0Tick.src = that.basePath + 'resources/wood/images/icon_t0.png';

            var t1Tick = new Image();
            t1Tick.onload = function () {
                var arrowImage = new Konva.Image({
                    x: centerX + axisInterval_X * t - 17,
                    y: Margin.bigTop + axisInterval_Y * 4 + 200 - 50,
                    image: t1Tick,
                    width: 35,
                    height: 39
                });
                that._finishLayer.add(arrowImage);
                that._finishLayer.draw();
                t1Tick.onload = null;
            };
            t1Tick.src = that.basePath + 'resources/wood/images/icon_t1.png';

            var dashedLine = new Konva.Line($.extend({
                points: [centerX + axisInterval_X * t + 2, centerY - v1IntNum * axisInterval_Y, centerX, centerY - v1IntNum * axisInterval_Y]
            }, VTDashedLineStyle));
            this._finishLayer.add(dashedLine);

            dashedLine = new Konva.Line($.extend({
                points: [centerX + axisInterval_X * t, centerY - v1IntNum * axisInterval_Y - 2, centerX + axisInterval_X * t, centerY]
            }, VTDashedLineStyle));
            this._finishLayer.add(dashedLine);
            this._finishLayer.setZIndex(99);
            this._finishLayer.draw();
        },
        play: function play() {
            var that = this;
            if (that.tweenLine) {
                that.tweenLine.play();
            }
        },
        reset: function reset() {
            var that = this;
            if (that._finishLayer) {
                that._finishLayer.clear();
                that._finishLayer.destroyChildren();
            }
            that._lineLayer.clear();
            that._lineLayer.destroyChildren();
            if (that.tweenLine) {
                that.tweenLine.destroy();
            }
        },
        pause: function pause() {
            var that = this;
            if (that.tweenLine) {
                that.tweenLine.pause();
            }
        },
        destroy: function destroy() {
            this._stage.destroy();
        },
        scaleFun: function scaleFun(scaleView) {
            var that = this;
            this._stage.scale({ x: scaleView, y: scaleView });
            var width = that._$container.width();
            var height = that._$container.height();
            this._stage.setWidth(width);
            this._stage.setHeight(height);
            this._stage.draw();
        }
    });
    return VTDynamic;
});
