		/**
   * 增加形状
   */

	}, {
		key: "addShape",
		value: function addShape(shapeData, type, row, col, half) {
			//this._('addShape 0', row, col, half);
			if (typeof shapeData == "string") shapeData = _shape2.default.getInst().getShapeByType(shapeData);
			if (!shapeData || !type || row == null || col == null) return;
			//this._('addShape 1', row, col, half);
			half = half || 0;
			var cmpData = this._compareList[type];
			if (cmpData == null || row >= cmpData.row || col >= cmpData.col || row < 0 || col < 0) return;
			// 左右两边不可以放置三角形
			if (col == 0 && half == -1 || col == cmpData.col - 1 && half == 1) return;

			var _getPositionEm = this.getPositionEm(row, col, half),
			    left = _getPositionEm.left,
			    top = _getPositionEm.top;

			var $shape = this.getShape(type, row, col, half);
			$shape = $shape || _shape2.default.getInst().createNewShape(shapeData.type, type, row, col, half);
			cmpData.$shapeDom.append($shape);
			//$shape.css({ left : `${left}em`, top : `${top}em`, '-webkit-transform' : `rotate(${(half == 0)? 0 : 180}deg)`});
			$shape.css({ left: left + "em", top: top + "em" });
			_shape2.default.getInst().dealHalfShape($shape, half);
			this.switchSelect(false, [type]);
			_common2.default.getInst().onShapeCountUpdate();

			//this._(this.getExtendData());
		}

		/**
