			var newCmpData = this._compareList[type];
			if (newCmpData == null || newCmpData.row < row || newCmpData.col < col) return;

			half = half || 0;

			var _getPositionEm2 = this.getPositionEm(row, col, half),
			    left = _getPositionEm2.left,
			    top = _getPositionEm2.top;

			newCmpData.$shapeDom.append($shape);
			$shape.attr({ row: row, col: col, half: half, owner: type }).css({ left: left + "em", top: top + "em" });
			_shape2.default.getInst().dealHalfShape($shape, half);
			this.switchSelect(false, [type]);
		}

