		/**
   * 获取指定位子上的形状
   *
   * @returns [null|$shape]
   */

	}, {
		key: "getShape",
		value: function getShape(type, row, col, half) {
			var cmpData = this._compareList[type];
			if (cmpData == null) return null;

			if (row < 0 || row > cmpData.row || col < 0 || col > cmpData.col) return null;

			half = half || 0;
			var $shape = cmpData.$shapeDom.find("[row=" + row + "][col=" + col + "][half=" + half + "]");
			if ($shape && $shape.length) return $shape;
			if (half == 1) {
				$shape = cmpData.$shapeDom.find("[row=" + row + "][col=" + (col + 1) + "][half=-1]");
			} else if (half == -1) {
				$shape = cmpData.$shapeDom.find("[row=" + row + "][col=" + (col - 1) + "][half=1]");
			}
			return $shape == null || $shape.length == 0 ? null : $shape;
		}

		/**
   * 根据行和列获取在形状内的坐标
   */

	}, {
		key: "getPositionEm",
		value: function getPositionEm(row, col, half) {
			var res = {
				top: this._appConfig.gridSizeEm * row,
				left: this._appConfig.gridSizeEm * col
			};
			if (half == 1) res.left += this._appConfig.gridSizeEm / 2;
			if (half == -1) res.left -= this._appConfig.gridSizeEm / 2;
			return res;
		}

		/**
   * 根据行和列获取在形状内的坐标
   */

	}, {
		key: "getPositionPx",
		value: function getPositionPx(row, col) {
			return {
				top: this._appConfig.gridSizePx * row,
				left: this._appConfig.gridSizePx * col
			};
		}

		/**
   * 将带比较图形中的偏移量坐标，转换为行列坐标
   */

	}, {
		key: "_offset2RowAndCol",
		value: function _offset2RowAndCol(type, pageX, pageY, evtType) {
			var errRes = { row: -1, col: -1, half: 0 };

			if (type == null || pageX == null || pageY == null) return errRes;
			var cmpData = this._compareList[type];
			if (cmpData == null) return errRes;

			// 点击位子相对于待比较图形的偏移
			var offsetX = pageX - cmpData.$shapeDom.offset().left,
			    offsetY = pageY - cmpData.$shapeDom.offset().top;
			//this._(type, offsetX, offsetY, '>>>>>', offsetY / this._appConfig.gridSizePx, pageX, cmpData.$dom.offset().left);
			// 点击位子的格子坐标
			var row = Math.floor(offsetY / this._appConfig.gridSizePx),
			    col = Math.floor(offsetX / this._appConfig.gridSizePx);
			// 格子坐标的像素位子

			var _getPositionPx = this.getPositionPx(row, col),
			    left = _getPositionPx.left,
			    top = _getPositionPx.top;
			// 点击位子在格子内的偏移量


			var px = offsetX - left,
			    py = offsetY - top;
			// 边界判断

			if (row < 0 || row >= cmpData.row || col < 0 || col >= cmpData.col) return errRes;

			// 触碰点在三角形中
			if (_util2.default.pointInTriangle(px, py, this._appConfig.gridSizePx)) return { row: row, col: col };
			var half = offsetX < left + this._appConfig.gridSizePx / 2 ? -1 : 1;
			// 点击在左右两边时，按照中间处理
			if (col == 0 && half == -1 || col == cmpData.col - 1 && half == 1) half = 0;
			//this._('_offset2RowAndCol 1',half, row, col );
			// 在未选中三角形，同时点击待比较图形中的三角形dom时，还是需要相应交互（拖拽）
			if (['mousedown', 'touchstart'].indexOf(evtType) != -1) {
				var $shape = this.getShape(type, row, col, half);
				if ($shape != null) return { row: row, col: col, half: half };
			}
			//this._('_offset2RowAndCol 2',half, row, col );
			// 鼠标放开时，如果拖拽的是三角形，那么还是需要计算三角形的
			if (['mouseup', 'touchend'].indexOf(evtType) != -1) {
				var _$shape = this._moveInfo.$target;
				if (_$shape && _$shape.attr('type') == 'triangle') return { row: row, col: col, half: half };
			}
			//this._('_offset2RowAndCol 3',half, row, col );
			if (_shape2.default.getInst().curSelectShape && !_shape2.default.getInst().curSelectShape.enableHalf) return { row: row, col: col };

			// 在未选择任何形状时，根据当前已经有的形状判断是否需要返回half
			if (_shape2.default.getInst().curSelectShape == null) {
				var _$shape2 = this._$bodyRoot.find('span[type="triangle"]:first');
				if (_$shape2 == null || _$shape2.length == 0) return { row: row, col: col };
			}

			//this._('_offset2RowAndCol 4',half, row, col );
			return { row: row, col: col, half: half };
		}

		/***************************************************************************
   * 鼠标相关
   **************************************************************************/

		/**
   * 移动开始前的初始化
   */

