var Util = function () {
	function Util() {
		_classCallCheck(this, Util);
	}

	_createClass(Util, null, [{
		key: "pointInTriangle",

		/**
   * 判断点是否在三角形区域内
   * 算法：三角形内点分割的3个三角形面积之和等于三角形面积
   */
		value: function pointInTriangle(px, py, length) {
			var rect = {
				a: new Point(0, 0),
				b: new Point(length, 0),
				c: new Point(length, length),
				d: new Point(0, length)
			};

			var tgl = {
				a: rect.d,
				b: rect.c,
				c: new Point(length / 2, 0)
			};

			var p = new Point(px, py);

			var area1 = Util.getTglArea({ a: tgl.a, b: tgl.b, c: p });
			var area2 = Util.getTglArea({ a: tgl.a, b: tgl.c, c: p });
			var area3 = Util.getTglArea({ a: tgl.c, b: tgl.b, c: p });
			var sumArea = Util.getTglArea(tgl);
			var pointArea = area1 + area2 + area3;
			var rate = Math.round((pointArea - sumArea) / sumArea * 100);
			return rate <= 5;
		}

		///**
		// * 两点距离
		// */
		//static dist( p1, p2 )
		//{
		//	let x = p1.x - p2.x;
		//	let y = p1.y - p2.y;
		//	return Math.sqrt( x* x + y * y );
		//}

		/**
   * 三角形面积
   */

	}, {
		key: "getTglArea",
		value: function getTglArea(tgl) {
			var t1 = tgl.a.x * tgl.b.y + tgl.b.x * tgl.c.y + tgl.c.x * tgl.a.y;
			var t2 = tgl.a.y * tgl.b.x + tgl.b.y * tgl.c.x + tgl.c.y * tgl.a.x;
			return Math.abs((t1 - t2) / 2);
		}

		/**
   * 判断两个矩形是否相交
   * 这种算法要求两个矩形的边都是垂直与x或者y轴
   */

	}, {
		key: "areaIntersect",
		value: function areaIntersect(area1, area2) {
			var zx = Math.abs(area1.left + area1.right - area2.left - area2.right);
			var x = Math.abs(area1.left - area1.right) + Math.abs(area2.left - area2.right);
			var zy = Math.abs(area1.top + area1.bottom - area2.top - area2.bottom);
			var y = Math.abs(area1.top - area1.bottom) + Math.abs(area2.top - area2.bottom);
			return zx <= x && zy <= y;
		}

		/**
   * 判断点是否在矩形中
   */

	}, {
		key: "pointInArea",
		value: function pointInArea(px, py, area) {
			return area.left <= px && px <= area.right && area.top <= py && py <= area.bottom;
		}

		/**
   * 获取兑现的值
   */

	}, {
		key: "values",
		value: function values(obj) {
			var res = [];
			for (var key in obj) {
				res.push(obj[key]);
			}
			return res;
		}
	}]);

	return Util;
