	      var cupBottom = $cup.offset().top + 2 * $cup.height() / 3 - weightTop - lineHeight;

	      // fontSize = parseFloat($('#buoyancy-tool').css('font-size'));
	      var scaleMark = 1.2 * fontSize;

	      var weightTopHeight = 1.1 * fontSize;
	      var weightHeight = 2 * fontSize - weightTopHeight / 2;
	      var waterHeight = $view.find('._js_water').height();

	      //                heightToWater += weightTopHeight;
	      heightToWater += weightTopHeight / 2;

	      var viewConfig = {
	        HEIGHT_TO_WATER_LEVEL: heightToWater,
	        HEIGHT_TO_CUP_BOTTOM: cupBottom,
	        WEIGHT_HEIGHT: weightHeight,
	        SCALE_MARK: scaleMark,
	        WATER_HEIGHT: waterHeight,
	        HEIGHT_TO_HALF_WATER: heightToWater + 4 * waterHeight / 5,
	        LINE_HEIGHT: lineHeight,
	        WEIGHT_TOP_HEIGHT: weightTopHeight,
	        ABSOLUTE_HEIGHT: weightHeight - scaleMark + weightTopHeight / 2
	      };
	      $.extend(_common.config, viewConfig);
	      var speed = _common.config.HEIGHT_TO_HALF_WATER / _common.config.TIME_TO_HALF_WATER;
	      _common.config.SPEED = speed;

	      var flow3Data = {
	        flow3Data: _this.calculateAnimate()
	      };
	      //                logger.info('flow3 data', flow3Data);
	      $.extend(_common.config, flow3Data);

	      // 推送到学生端自适应调整
	      var pushBasicFlowData = _this.$root.pushData['basicFlowData'];
	      if (pushBasicFlowData) {
	        pushBasicFlowData.fallingHeight *= fontSize / _this.$root.pushData['pcFontSize'];
	      }
	      $.extend(_this.$data, _this.$root.pushData['basicFlowData']);

	      // 小烧杯自适应
	      //        this.smallCupScale = fontSize / 24
	      _logger2.default.debug('basic-flow config', _common.config);
	    });
	  },

	  methods: {
	    /**
	     * 动画
	     * @param options
