	    tweenStart: function tweenStart(options) {
	      var startHeight = options.startHeight || 0;
	      var endHeight = options.endHeight || _common.config.HEIGHT_TO_CUP_BOTTOM;
	      // let duration = options.duration || 5000;

	      var defaultDuration = (endHeight - startHeight) / _common.config.SPEED;
	      var duration = options.duration || defaultDuration;

	      var vm = options.vm;
	      var callback = options.callback || function () {};

	      var that = this;
	      // 动画前先清除上一次的raf
	      //                caf(that.animationFrame);

	      // 推送用，标记正在动画
	      _common.config.isAnimating = true;
	      (0, _common.controlFull)(true);

	      function animate(time) {
	        /* global TWEEN: true */
	        TWEEN.update(time);
	        that.animationFrame = (0, _raf.raf)(animate);
	        //                    logger.debug('animate', that.fallingHeight)
	      }

	      new TWEEN.Tween({
	        fallingHeight: startHeight
	      }).to({
	        fallingHeight: endHeight
	      }, duration).onUpdate(function () {
	        that.fallingHeight = this.fallingHeight;
	      }).onComplete(function () {
	        _common.config.isAnimating = false;
	        (0, _common.controlFull)(false);
	        (0, _raf.caf)(that.animationFrame);
	        callback(vm, that);
	      }).start();

	      that.animationFrame = (0, _raf.raf)(animate);
	    },
	    setViewData: function setViewData(fallingHeight) {
	      this.fallingHeight = fallingHeight || 0;
	    },


	    /**
	     * 重置下落高度, 停止所有动画
	     */
	    reset: function reset() {
	      this.fallingHeight = 0;
	      _logger2.default.debug('basic-flow reset');
	      (0, _raf.caf)(this.animationFrame);
	      _common.config.isAnimating = false;
	      (0, _common.controlFull)(false);
