	    /**
	     * 计算浸入体积分别为0, 1/5, 2/5, 3/5, 4/5, 1时的动画参数
	     * @returns {Array}
	     */
	    calculateAnimate: function calculateAnimate() {
	      var timeArr = [1500, 1000, 1000, 1000, 1000];
	      var startArr = [0];
	      var endArr = [];
	      var level = 5;
	      var delta = _common.config.ABSOLUTE_HEIGHT / level;

	      var adjArr = [1.3, 0.9, 0.9, 0.9, 1];
	      for (var i = 0; i < level; i++) {
	        // let startHeight = config.HEIGHT_TO_WATER_LEVEL + i * delta;
	        var endHeight = _common.config.HEIGHT_TO_WATER_LEVEL + (i + adjArr[i]) * delta;
	        if (i > 0) {
	          startArr.push(endArr[i - 1]);
	        }
	        endArr.push(endHeight);
	      }
	      _logger2.default.info(startArr, endArr);
	      return {
	        startArr: startArr,
	        endArr: endArr,
	        timeArr: timeArr
	      };
	    }
