	  computed: {
	    smallCupScale: function smallCupScale() {
	      return this.fontSize / 24;
	    },
	    springHeight: function springHeight() {
	      return this.fallingHeight;
	    },
	    waterHeight: function waterHeight() {
	      if (this.$parent.name === 'flow5') {
	        // 预设5中大烧杯水位不会上升
	        return _common.config.WATER_HEIGHT;
	      } else {
	        return this.waterRiseH;
	      }
	    },
	    waterRiseH: function waterRiseH() {
	      // top: 初始位置到水面的高度
	      // bottom: 下落高度刚好让砝码沉浸在上升的水面中
	      // config.ABSOLUTE_HEIGHT: 进入水面后下落的高度使得砝码刚好浸入水中
	      var top = _common.config.HEIGHT_TO_WATER_LEVEL;
	      var bottom = void 0;
	      if (this.$parent.name === 'flow5') {
	        bottom = _common.config.HEIGHT_TO_WATER_LEVEL + _common.config.WEIGHT_HEIGHT;
	      } else {
	        bottom = _common.config.HEIGHT_TO_WATER_LEVEL + _common.config.ABSOLUTE_HEIGHT;
	      }

	      if (this.$parent.name === 'flow5') {
	        if (this.fallingHeight > top && this.fallingHeight < bottom) {
	          return _common.config.WATER_HEIGHT + this.fallingHeight - top;
	        } else if (this.fallingHeight >= bottom) {
	          return _common.config.WATER_HEIGHT + _common.config.WEIGHT_HEIGHT;
	        } else {
	          return _common.config.WATER_HEIGHT;
	        }
	      }

	      if (this.fallingHeight > top && this.fallingHeight < bottom) {
	        var riseHeight = _common.config.SCALE_MARK * (this.fallingHeight - top) / _common.config.ABSOLUTE_HEIGHT;
	        // riseHeight 水面应当上升的高度
	        return _common.config.WATER_HEIGHT + riseHeight;
	      } else if (this.fallingHeight >= bottom) {
	        return _common.config.WATER_HEIGHT + _common.config.SCALE_MARK;
	      } else {
	        return _common.config.WATER_HEIGHT;
	      }
	    },
	    weightHeight: function weightHeight() {
	      var top = _common.config.HEIGHT_TO_WATER_LEVEL;
	      var bottom = _common.config.HEIGHT_TO_WATER_LEVEL + _common.config.ABSOLUTE_HEIGHT;
	      var val = void 0;
	      if (this.$parent.name === 'flow5') {
	        bottom = _common.config.HEIGHT_TO_WATER_LEVEL + _common.config.WEIGHT_HEIGHT;
	        if (this.fallingHeight - bottom < _common.config.WEIGHT_TOP_HEIGHT && this.fallingHeight - bottom > -1.5) {
	          val = _common.config.LINE_HEIGHT - (this.fallingHeight - top + _common.config.WEIGHT_TOP_HEIGHT);
	          return val;
	        }
	        if (this.fallingHeight > top && this.fallingHeight < bottom) {
	          // val = config.LINE_HEIGHT - (this.fallingHeight - top) * 1.1;
	          val = _common.config.LINE_HEIGHT - (this.fallingHeight - top);
	        } else if (this.fallingHeight > bottom) {
	          val = Math.max(_common.config.LINE_HEIGHT - (this.fallingHeight - top + _common.config.WEIGHT_TOP_HEIGHT), 0);
	        } else {
	          val = _common.config.LINE_HEIGHT;
	        }
	        return val;
	      }

	      // 如果刚好浸没到水面，则砝码的透明的高度加上config.WEIGHT_TOP_HEIGHT
	      if (this.fallingHeight - bottom < _common.config.WEIGHT_TOP_HEIGHT && this.fallingHeight - bottom > -1.5) {
	        val = _common.config.LINE_HEIGHT - (_common.config.SCALE_MARK + this.fallingHeight - top + _common.config.WEIGHT_TOP_HEIGHT);
	        return val;
	      }

	      if (this.fallingHeight > top && this.fallingHeight < bottom) {
	        // riseHeight 水面上升的高度
	        var riseHeight = _common.config.SCALE_MARK * (this.fallingHeight - top) / _common.config.ABSOLUTE_HEIGHT;
	        // 砝码浸入水面的高度=水面上升的高度+下落高度-初始位置到水面的高度
	        val = _common.config.LINE_HEIGHT - (riseHeight + this.fallingHeight - top);
	        // val = config.LINE_HEIGHT - (riseHeight + this.fallingHeight - top) - config.WEIGHT_TOP_HEIGHT / 2;
	      } else if (this.fallingHeight > bottom) {
	        val = Math.max(_common.config.LINE_HEIGHT - (_common.config.SCALE_MARK + this.fallingHeight - top + _common.config.WEIGHT_TOP_HEIGHT), 0);
	      } else {
	        val = _common.config.LINE_HEIGHT;
	      }
	      return val;
	    },
	    forceNumOne: function forceNumOne() {
	      var waterHeight = this.waterRiseH;
	      // logger.debug('w', waterHeight)
	      if (waterHeight) {
	        // let tmp = (waterHeight - config.WATER_HEIGHT) / config.SCALE_MARK;
	        var deltaWater = waterHeight - _common.config.WATER_HEIGHT;
	        var val = void 0;
	        // logger.debug(deltaWater);
	        if (this.$parent.name === 'flow5') {
	          val = (_common.config.GRAVITY - _common.config.F_WATER_MAX) * (1 - deltaWater / _common.config.WEIGHT_HEIGHT) + _common.config.F_WATER_MAX;
	          // logger.debug(val)
	        } else {
	          val = (_common.config.GRAVITY - _common.config.F_WATER_MAX) * (1 - deltaWater / _common.config.SCALE_MARK) + _common.config.F_WATER_MAX;
	        }

	        return val.toFixed(1);
	      } else {
	        return _common.config.GRAVITY;
	      }
	    },
	    forceNumTwo: function forceNumTwo() {
	      if (this.waterRiseH) {
	        // let tmp = (this.waterHeight - config.WATER_HEIGHT) / config.SCALE_MARK;
	        var val = (_common.config.GRAVITY - _common.config.F_BRINE_MAX) * (1 - (this.waterRiseH - _common.config.WATER_HEIGHT) / _common.config.SCALE_MARK) + _common.config.F_BRINE_MAX;
	        return val.toFixed(1);
	      } else {
	        return _common.config.GRAVITY;
	      }
	    },
	    zIndex: function zIndex() {
	      if (this.fallingHeight > _common.config.HEIGHT_TO_HALF_WATER) {
	        return 11;
	      } else {
	        return 10;
	      }
	    }
	  }
