  /* 点击参数框和元件展开收缩效果*/
  upDownpop: function upDownpop() {
    var DROP_COM = this.domobj.drop_com;
    var DROP_NUM = this.domobj.drop_num;
    DROP_COM.hide();
    DROP_NUM.removeClass('ui_open');
    DROP_NUM.eq(1).addClass('ui_open');
    DROP_NUM.eq(3).addClass('ui_open');
    this.domobj.thm_list.hide();
  },
  /* 点击算法原件更改算法预设*/
  changeAlorithm: function changeAlorithm(e) {
    var self = this;
    var array = [];
    var BTN_SYMBOL = this.domobj.thm_change_btn_symbol;
    if (e.target.classList.contains('click_disabled') || e.target.classList.contains('caf_operator_list')) {
      return;
    }
    this.domobj.btns.removeClass('click_disabled');
    switch ($(e.target).text().trim()) {
      case '+':
        array = [this.i18n.show_added_num, this.i18n.show_add_num];
        this.currentThm = self.THM['addfraction'];
        BTN_SYMBOL.attr('class', 'caf_operator_symbol operator_add');
        break;
      case '-':
        array = [this.i18n.show_subed_num, this.i18n.show_sub_num];
        this.currentThm = self.THM['subfraction'];
        BTN_SYMBOL.attr('class', 'caf_operator_symbol operator_sub');
        break;
      case '×':
        array = [this.i18n.show_muled_num, this.i18n.show_mul_num];
        this.currentThm = self.THM['mulfraction'];
        BTN_SYMBOL.attr('class', 'caf_operator_symbol operator_mul');
        break;
      case '÷':
        array = [this.i18n.show_divided_num, this.i18n.show_divide_num];
        this.currentThm = self.THM['dividefraction'];
        BTN_SYMBOL.attr('class', 'caf_operator_symbol operator_div');
        break;
    }
    /* 切换加减乘除，list也要跟着变化*/
    var curthm = this.domobj.thm_change_btn.text();
    var curclassname = this.domobj.thm_change_btn.text().trim();
    this.domobj.thm_change_btn.text($(e.target).text());
    $(e.target).text(curthm);
    switch (curclassname) {
      case '+':
        $(e.target).parent().attr('class', 'caf_drop_box caf_add');
        break;
      case '-':
        $(e.target).parent().attr('class', 'caf_drop_box caf_sub');
        break;
      case '×':
        $(e.target).parent().attr('class', 'caf_drop_box caf_mul');
        break;
      case '÷':
        $(e.target).parent().attr('class', 'caf_drop_box caf_div');
        break;
    }

    this.domobj.caf_btn_1.text(array[0]);
    this.domobj.caf_btn_2.text(array[1]);
    this.setParameter();
    this.resetStatus();
    this.showParameter();
    this.btnstatus = {};
  },
  showThmList: function showThmList(e) {
    var self = this;
    e.stopPropagation();
    self.upDownpop();
    self.domobj.thm_list.show();
    if (parseFloat(parameter.nume1 / parameter.deno1) > parseFloat(parameter.nume0 / parameter.deno0)) {
      self.domobj.thm_list.find('.caf_drop_box').each(function () {
        if ($(this).text().trim() === '-') {
          $(this).addClass('click_disabled');
        }
      });
    } else {
      this.domobj.thm_list.find('.caf_drop_box').removeClass('click_disabled');
    }
  },
