  },
  /* 显示参数*/
  showParameter: function showParameter() {
    var numcontainer = this.parent.$view.find('.caf_equation_box');
    var leftblock = this.parent.$view.find('.caf_bignum_dropbox');
    leftblock.nextAll().remove();
    var maxn0 = this.maxCommonNum(parameter.nume0, parameter.deno0); // 第一个分数的最大公约数
    var maxn1 = this.maxCommonNum(parameter.nume1, parameter.deno1); // 第二个分数的最大公约数
    var num0 = parseFloat(parameter.nume0 / parameter.deno0); // 第一个分数的值
    var num1 = parseFloat(parameter.nume1 / parameter.deno1); // 第二个分数的值
    var $fenshu0 = this.$template.find('.js_fenshu').clone();
    var $fenshu1 = this.$template.find('.js_fenshu').clone();
    var $int0 = this.$template.find('.caf_equation_item_int').clone();
    var $int1 = this.$template.find('.caf_equation_item_int').clone();

    /* 字符串1 符号 字符串2*/
    $fenshu0.find('.caf_fenzi .num_1').text(parameter.nume0 / maxn0);
    $fenshu0.find('.caf_fenmu .num_1').text(parameter.deno0 / maxn0);
    var str1 = $fenshu0;
    /* 替换减法中的减号符*/
    var $fuhao = this.$template.find('.js_fuhao').clone();
    $fuhao.find('.fuhao').text(this.domobj.thm_change_btn.text());
    if (this.currentThm === this.THM['subfraction']) {
      $fuhao.find('.fuhao').text('－');
    }
    var fuhao = $fuhao;

    $fenshu1.find('.caf_fenzi .num_1').text(parameter.nume1 / maxn1);
    $fenshu1.find('.caf_fenmu .num_1').text(parameter.deno1 / maxn1);
    var str2 = $fenshu1;

    this.num0type = '';
    this.num1type = '';
    parameter.setstatus = false; // 未显示化简式子的时候setstatus为true

    /* 第一个分数为整数*/
    if (num0 === 1 || num0 === 2 || num0 === 3) {
      this.num0type = 'int'; // 整数型
      $int0.find('.num_1').text(parameter.nume0 / parameter.deno0);
      str1 = $int0;
    }
    /* 第二个分数为整数*/
    if (num1 === 1 || num1 === 2 || num1 === 3) {
      this.num1type = 'int';
      $int1.find('.num_1').text(parameter.nume1 / parameter.deno1);
      str2 = $int1;
    }
    /* 两个分数都为整数的时候，底部除了‘展示算式’按钮其他都为置灰态*/
    if (this.num0type === 'int' && this.num1type === 'int') {
      this.domobj.btns.not(':last').addClass('click_disabled');
    }
    switch (true) {
      /* 加法减法化简为整数处理，两个分数同分母设置参数不显示化简式子*/
      case parameter.deno1 === parameter.deno0 && (this.currentThm === this.THM['addfraction'] || this.currentThm === this.THM['subfraction']):
        {
          /* 同分母情况下如果两个分数都为整数，则按照整数整数情况*/
          if (this.num0type === 'int' && this.num1type === 'int') {
            parameter.nume0 = parameter.nume0 / parameter.deno0;
            parameter.deno0 = 1;
            parameter.nume1 = parameter.nume1 / parameter.deno1;
            parameter.deno1 = 1;
            break;
          }
          str1 = '';str2 = '';fuhao = '';
          parameter.setstatus = true;
          break;
        }
      case maxn0 !== 1 || maxn1 !== 1:
        {
          // if(this.num0type=="int"&&this.num1type=="int"){break}
          /* 化简后对两个分数的分子分母进行更新信息*/
          parameter.nume0 = parameter.nume0 / maxn0;
          parameter.deno0 = parameter.deno0 / maxn0;
          parameter.nume1 = parameter.nume1 / maxn1;
          parameter.deno1 = parameter.deno1 / maxn1;
          break;
        }
      case maxn0 === 1 && maxn1 === 1:
        {
          if (this.num0type === 'int' || this.num1type === 'int') {
            // parameter.setstatus = true;
            break;
          }
          str1 = '';
          str2 = '';
          fuhao = '';
          parameter.setstatus = true;
          break;
        }
      default:
        console.log('其他情况');

        break;
    }
    numcontainer.append(str1, fuhao, str2);
    this.calyf();
    this.judgeResultBtn();
  },
  /** 展示结果按钮判断通分计算约分三个按钮是否出现  根据分数判断当前图形显示的类名**/
  judgeResultBtn: function judgeResultBtn() {
    parameter.tf = false;
    var CAFBTN3 = this.domobj.cafbtn3;
    var CONTAINER = this.domobj.container;
    var sumfenzi = parseInt(parameter.nume0) + parseInt(parameter.nume1);
    var fenzisum = parseInt(parameter.nume0 * this.minCommonNum(parameter.deno0, parameter.deno1) / parameter.deno0) + parseInt(parameter.nume1 * this.minCommonNum(parameter.deno0, parameter.deno1) / parameter.deno1);
    switch (this.currentThm) {
      case this.THM['addfraction']:
        /* 同分母相加*/
        if (parameter.deno0 === parameter.deno1) {
          CAFBTN3.attr('class', 'caf_btn_item caf_btn_3 caf_show_results show_jy');
          if (this.maxCommonNum(sumfenzi, parameter.deno0) === 1) {
            this.parent.$view.find('.caf_btn_3_sanjiao').hide();
            this.domobj.resultbtn.hide();
          }
        } else /* 不同分母相加*/{
            CAFBTN3.attr('class', 'caf_btn_item caf_btn_3 caf_show_results show_tj');
            parameter.tf = true;
            /* 不同分母相加可约分则出现3个按钮*/
            if (this.maxCommonNum(fenzisum, this.maxCommonNum(parameter.deno0, parameter.deno1)) !== 1) {
              CAFBTN3.attr('class', 'caf_btn_item caf_btn_3 caf_show_results show_tjy');
              parameter.tf = true;
            }
          }
        /* 加法图形类名展示*/
        CONTAINER.find('.caf_show_add').removeClass('hide_dom');
        CONTAINER.children().not('.caf_show_add').addClass('hide_dom');
        break;
      case this.THM['subfraction']:
        /* 同分母相减*/
        if (parameter.deno0 === parameter.deno1) {
          CAFBTN3.attr('class', 'caf_btn_item caf_btn_3 caf_show_results show_jy');
          if (this.maxCommonNum(sumfenzi, parameter.deno0) === 1 || parseFloat(parameter.nume0 / parameter.deno0) === parseFloat(parameter.nume1 / parameter.deno1)) {
            this.parent.$view.find('.caf_btn_3_sanjiao').hide();
            this.domobj.resultbtn.hide();
          }
        } else /* 不同分减相加*/{
            CAFBTN3.attr('class', 'caf_btn_item caf_btn_3 caf_show_results show_tj');
            parameter.tf = true;
            /* 不同分母相减可约分则出现3个按钮*/
            if (this.maxCommonNum(fenzisum, this.maxCommonNum(parameter.deno0, parameter.deno1)) !== 1) {
              CAFBTN3.attr('class', 'caf_btn_item caf_btn_3 caf_show_results show_tjy');
            }
          }
        /* 减法图形类名展示*/
        CONTAINER.find('.caf_show_sub').removeClass('hide_dom');
        CONTAINER.children().not('.caf_show_sub').addClass('hide_dom');
        break;
      case this.THM['mulfraction']:
        /* 分数与分数相乘*/
        if (this.num0type !== 'int' && this.num1type !== 'int') {
          // this.domobj.cafbtn3.attr('class', "caf_btn_item caf_btn_3 caf_show_results show_tj");
          // parameter.tf = true;
          if (this.maxCommonNum(parameter.nume0 * parameter.nume1, this.minCommonNum(parameter.deno0, parameter.deno1)) !== 1) {
            CAFBTN3.attr('class', 'caf_btn_item caf_btn_3 caf_show_results show_jy');
          } else {
            this.parent.$view.find('.caf_btn_3_sanjiao').hide();
            this.domobj.resultbtn.hide();
          }
          /* 乘法分数乘以分数的图形类名展示*/
          CONTAINER.find('.caf_show_mul_ff').removeClass('hide_dom');
          CONTAINER.children().not('.caf_show_mul_ff').addClass('hide_dom');
        } else {
          CAFBTN3.attr('class', 'caf_btn_item caf_btn_3 caf_show_results show_jy');
          if (this.maxCommonNum(parameter.nume0 * parameter.nume1, this.minCommonNum(parameter.deno0, parameter.deno1)) === 1) {
            this.parent.$view.find('.caf_btn_3_sanjiao').hide();
            this.domobj.resultbtn.hide();
          }
          /* 乘法分数乘以整数或者整数乘以分数的图形类名展示*/
          CONTAINER.find('.caf_show_mul_fz').removeClass('hide_dom');
          CONTAINER.children().not('.caf_show_mul_fz').addClass('hide_dom');
          if (parseFloat(parameter.nume1 / parameter.deno1) === 1) {
            CONTAINER.find('.caf_show_mul_fz .js_clear_show').eq(0).addClass('hide_dom');
            CONTAINER.find('.caf_show_mul_fz .js_clear_show').eq(1).removeClass('hide_dom');
          }
        }

        break;
      case this.THM['dividefraction']:
        /* 两个分数都为分数且分母各不同   或者 整数除以分数的可以整除的情况 或者 分母相同且两个分子相除*/
        if (this.num0type !== 'int' && this.num1type !== 'int' && parameter.deno0 !== parameter.deno1 || this.num0type === 'int' && this.num1type !== 'int' && parseInt(parameter.nume1) !== 1 || parameter.deno0 === parameter.deno1 && parameter.nume0 % parameter.nume1 !== 0) {
          CONTAINER.find('.caf_show_div_ff').removeClass('hide_dom');
