          } else {
            /* 除法大多情况只有展示结果按钮，没有通分计算约分动画*/
            this.parent.$view.find('.caf_btn_3_sanjiao').hide();
            this.domobj.resultbtn.hide();
          }
        }
        break;
      default:
        break;
    }
  },
  minCommonNum: function minCommonNum(a, b) {
    var count = a * b / this.maxCommonNum(a, b);
    return count;
  },
  /* 最大公约数*/
  maxCommonNum: function maxCommonNum(a, b) {
    if (a < b) {
      var temp = a;
      a = b;
      b = temp;
    }
    while (b !== 0) {
      /* 利用辗除法，直到b为0为止*/
      var _temp = a % b;
      a = b;
      b = _temp;
    }
    return parseInt(a);
  },

  update: function update() {
    var DROP_NUM_TEXT = this.domobj.drop_num.find('.num_input_text');
    var BTNSYMBOL = this.domobj.thm_change_btn_symbol;
    DROP_NUM_TEXT.eq(0).text(this.parameter[0]);
    DROP_NUM_TEXT.eq(1).text(this.parameter[1]);
    DROP_NUM_TEXT.eq(2).text(this.parameter[2]);
    DROP_NUM_TEXT.eq(3).text(this.parameter[3]);
    this.domobj.thm_change_btn.text(this.parameter[4]);
    this.domobj.btns.eq(0).find('.btns-primary-txt').text(this.parameter[5]);
    this.domobj.btns.eq(1).find('.btns-primary-txt').text(this.parameter[6]);
    /* 如果是加法或乘除法预设，则原件初始没有加法或乘除法按钮*/
    switch (this.parameter[4].trim()) {
      case '+':
        this.parent.$view.find('.caf_operator_list .caf_add').hide();
        this.currentThm = this.THM['addfraction'];
        BTNSYMBOL.attr('class', 'caf_operator_symbol operator_add');
        break;
      case '-':
        this.parent.$view.find('.caf_operator_list .caf_sub').hide();
        this.currentThm = this.THM['subfraction'];
        BTNSYMBOL.attr('class', 'caf_operator_symbol operator_sub');
        break;
      case '×':
        this.parent.$view.find('.caf_operator_list .caf_mul').hide();
        this.currentThm = this.THM['mulfraction'];
        BTNSYMBOL.attr('class', 'caf_operator_symbol operator_mul');
        break;
      case '÷':
        this.parent.$view.find('.caf_operator_list .caf_div').hide();
        this.currentThm = this.THM['dividefraction'];
        BTNSYMBOL.attr('class', 'caf_operator_symbol operator_div');
        break;
    }
  },
