  calyf: function calyf() {
    var minnum = void 0;
    var fenzihe = void 0;
    switch (this.domobj.thm_change_btn.text().trim()) {
      case '+':
        minnum = this.minCommonNum(parameter.deno0, parameter.deno1);
        fenzihe = parseInt(parameter.nume0 * minnum / parameter.deno0) + parseInt(parameter.nume1 * minnum / parameter.deno1);
        parameter.yf = {
          z: fenzihe / this.maxCommonNum(fenzihe, minnum),
          m: minnum / this.maxCommonNum(fenzihe, minnum)
        };
        if (this.maxCommonNum(fenzihe, minnum) === 1) {
          parameter.yf = {};
        }
        break;
      case '-':
        minnum = this.minCommonNum(parameter.deno0, parameter.deno1);
        fenzihe = parseInt(parameter.nume0 * minnum / parameter.deno0) - parseInt(parameter.nume1 * minnum / parameter.deno1);
        parameter.yf = {
          z: fenzihe / this.maxCommonNum(fenzihe, minnum),
          m: minnum / this.maxCommonNum(fenzihe, minnum)
        };
        if (this.maxCommonNum(fenzihe, minnum) === 1) {
          parameter.yf = {};
        }
        break;
      case '×':
        minnum = parameter.deno0 * parameter.deno1;
        fenzihe = parameter.nume0 * parameter.nume1;
        parameter.yf = {
          z: fenzihe / this.maxCommonNum(fenzihe, minnum),
          m: minnum / this.maxCommonNum(fenzihe, minnum)
        };
        if (this.maxCommonNum(fenzihe, minnum) === 1) {
          parameter.yf = {};
        }
        break;
      case '÷':
        minnum = parameter.nume0 * this.minCommonNum(parameter.deno0, parameter.deno1) / parameter.deno0;
        fenzihe = parameter.nume1 * this.minCommonNum(parameter.deno0, parameter.deno1) / parameter.deno1;
        parameter.yf = {
          m: fenzihe / this.maxCommonNum(fenzihe, minnum),
          z: minnum / this.maxCommonNum(fenzihe, minnum)
        };
        if (this.maxCommonNum(fenzihe, minnum) === 1) {
          parameter.yf = {};
        }
        break;
    }
  }

};
function throttle(fn, delay, immediate, debounce) {
  var curr = +new Date(),
      // 当前事件
  last_call = 0,
      last_exec = 0,
      timer = null,
      diff = void 0,
      // 时间差
  context = void 0,
      // 上下文
  args = void 0,
      exec = function exec() {
    last_exec = curr;
    fn.apply(context, args);
  };
  return function () {
    curr = +new Date();
    context = this;
    args = arguments, diff = curr - (debounce ? last_call : last_exec) - delay;
    clearTimeout(timer);
    if (debounce) {
      if (immediate) {
        timer = setTimeout(exec, delay);
      } else if (diff >= 0) {
        exec();
      }
    } else {
      if (diff >= 0) {
        exec();
      } else if (immediate) {
        timer = setTimeout(exec, -diff);
      }
    }
    last_call = curr;
  };
}
/*
 * 空闲控制 返回函数连续调用时，空闲时间必须大于或等于 delay，fn 才会执行
 * @param fn {function}  要调用的函数
 * @param delay   {number}    空闲时间
 * @param immediate  {bool} 给 immediate参数传递false 绑定的函数先执行，而不是delay后后执行。
 * @return {function}实际调用函数
 */
function debounce(fn, delay, immediate) {
  return throttle.call(this, fn, delay, immediate, true);
}
