function maxCommonNum(a, b) {
  if (a < b) {
    var temp = a;a = b;b = temp;
  }
  while (b !== 0) {
    /* 利用辗除法，直到b为0为止*/
    var _temp = a % b;
    a = b;
    b = _temp;
  }
  return parseInt(a);
}
