function getNum1(num, scaleW) {
  var halfWidth = scaleW ? 'halfWidth' : '';
  if (num < 10) {
    return '<span class="num ' + halfWidth + ' num_' + num + '"></span>';
  } else if (num >= 100) {
    var numStr = num + '';
    var numArr = numStr.split('');
    return '<span class="num ' + halfWidth + ' num_' + numArr[0] + '"></span>' + '<span class="num ' + halfWidth + ' num_' + numArr[1] + '"></span>' + '<span class="num ' + halfWidth + ' num_' + numArr[2] + '"></span>';
  } else {
    var _numStr = num + '';
    var _numArr = _numStr.split('');
    return '<span class="num ' + halfWidth + '  num_' + _numArr[0] + '"></span>' + '<span class="num ' + halfWidth + ' num_' + _numArr[1] + '"></span>';
  }
}

