      });
    });
  },
  _getCenterX: function _getCenterX(ele, n) {
    return ele.offset().left + ele.width() * n;
  },
  minCommonNum: function minCommonNum(a, b) {
    var count = a * b / this.maxCommonNum(a, b);
    return count;
  },
  /* 最大公约数*/
  maxCommonNum: function maxCommonNum(a, b) {
    if (a < b) {
      var temp = a;
      a = b;
      b = temp;
    }
    while (b !== 0) {
      /* 利用辗除法，直到b为0为止*/
      var _temp = a % b;
      a = b;
      b = _temp;
    }
    return a;
  },
  hasInt: function hasInt(param) {
    var n1 = param.num1;
    var n2 = param.num2;
    if ((n1.z / n1.m | 0) === n1.z / n1.m) {
      return 1;
    }
    if ((n2.z / n2.m | 0) === n2.z / n2.m) {
      return 2;
    }
    return null;
  },
  destroy: function destroy() {}
};
