      'verticalAlign': 'top',
      'transformOrigin': 'center top',
      'visibility': 'hidden'
    });
    cloneNode.find('div').eq(0).empty().removeClass('border').siblings('div').remove();
    cloneNode.find('.num').remove();
    cloneNode.find('.js_item').removeClass('caf_part_item_pink_h').addClass('caf_part_item_transparent');

    // let len = lLiLen * rLiLen
    var tatolNum = opt.num1.m * opt.num2.m;
    var hasNum = true;

    for (var i = 0; i < lLiLen; i++) {
      var _loop = function _loop(j) {
        // len--
        var ctnNode = cloneNode.clone();
        var d = _this._getBgData(i, j, lLi, rLi);
        ctnNode.find('>.js_item').each(function (index) {
          var count = opt.num1.m;
          while (count--) {
            var hasBorder = count !== 0;
            var bg = opt.num1.m - count - 1;
            // let n = opt.num1.m - count - 1
            var _j = opt.num1.m - count - 1;
            if (tatolNum > 25 || opt.num2.m > 6) {
              // hasNum = false;
              hasNum = !!(index + _j * opt.num2.m === 0 || index + _j * opt.num2.m === tatolNum - 1);
            }
            if (index < d.p && opt.num1.m - count - 1 < d.b) {
              bg = 'green';
              // n = 1
            } else if (index < d.p && opt.num1.m - count - 1 >= d.b) {
              bg = 'pink';
              // n = 1.3
            } else if (index >= d.p && opt.num1.m - count - 1 < d.b) {
              bg = 'brown';
              // n = 1.3
            } else {
              bg = '';
            }
            $(this).append(getSmallItem({
              'divNum': opt.num1.m,
              'hasBorder': hasBorder,
              'hasBag': true,
              'bg': bg,
              'num': index + _j * opt.num2.m + 1,
              'hasNum': hasNum,
              'divVer': 1,
              'm1': opt.num1.m,
              'm2': opt.num2.m,
              'isfull': true

            }));
          }
        });
        if (tatolNum > 25 || opt.num2.m > 6) {
          var greenLen = ctnNode.find('.smallItem_green_bg  ').length;
          ctnNode.append($('<span class="big_than_25">' + (0, _BaseAnimate.getNum)({
            num: greenLen,
            hasNum: true
          }) + '</span>'));
        }

        if (opt.yfbtn) {
          ctnNode.css('visibility', 'hidden').find('.show_result').css('visibility', 'hidden');
        }
        resultDom.append(ctnNode);

        self._mulTransfirst(opt, i, j, rLiLen, lLi, rLi, resultDom);
      };

      for (var j = 0; j < rLiLen; j++) {
        _loop(j);
      }
    }
    self._mulTransSecond(opt, resultDom, lLiLen, rLiLen);
    return null;
  },
  // 假分数计算第一段动画
  _mulTransfirst: function _mulTransfirst(opt, a, b, rLiLen, lLi, rLi, resultDom) {
    var self = this;
    var ldiv = $(lLi[a]).find('div')[b];
    var rdiv = $(rLi[b]).find('div')[a];
    var sdiv = resultDom.find('li')[a * rLiLen + b];
    var t1 = setTimeout(function () {
      if (opt.yfbtn) {
        ldiv.style.transitionDuration = '0s';
        ldiv.style.transitionDuration = '0s';
      }
      self._mulLiTrans(ldiv, rdiv, sdiv, opt);
      var t2 = setTimeout(function () {
        if (!opt.yfbtn) {
          $(sdiv).css('visibility', 'visible').find('.show_result').css('visibility', 'visible');
        }
        ldiv.style.display = 'none';
        rdiv.style.display = 'none';
      }, opt.yfbtn ? 0 : 1500);

      self._timerArray.push(t2);
    }, opt.yfbtn ? 0 : (a * rLiLen + b) * 700);

    self._timerArray.push(t1);
  },
  // 假分数第二段动画
  _mulTransSecond: function _mulTransSecond(opt, resultDom, lLiLen, rLiLen) {
    var self = this;
    var zoom = 0.78;
    var t1 = setTimeout(function () {
      opt.lP.addClass('hide_dom');
      opt.rP.addClass('hide_dom');
      resultDom.addClass('caf_mul_ff_results_center');
      switch (lLiLen * rLiLen) {// 自适应大小
        case 9:
          zoom = zoom * 1.6;
          resultDom[0].style.webkitTransform = 'translate(50%,-50%) scale(' + 1.6 + ')';
          opt.yfbtn && (resultDom[0].style.transitionDuration = '0s');

          break;
        case 6:
          zoom = zoom * 2;
          resultDom[0].style.webkitTransform = 'translate(50%,-50%)scale(' + 2 + ')';
          opt.yfbtn && (resultDom[0].style.transitionDuration = '0s');

          break;
        case 4:
          zoom = zoom * 2.4;
          resultDom[0].style.webkitTransform = 'translate(50%,-50%)scale(' + 2.4 + ')';
          opt.yfbtn && (resultDom[0].style.transitionDuration = '0s');

          break;
        default:
          zoom = zoom * 2.7;
          resultDom[0].style.webkitTransform = 'translate(50%,-50%)scale(' + 2.7 + ')';
          opt.yfbtn && (resultDom[0].style.transitionDuration = '0s');

          break;
      }
    }, opt.yfbtn ? 0 : lLiLen * rLiLen * 700 + 2000);
    var t2 = setTimeout(function () {
      opt.tf || self._mulTranslateCompute(opt, resultDom, zoom);
    }, opt.yfbtn ? 0 : lLiLen * rLiLen * 700 + 3000);
    this._timerArray.push(t1);
    this._timerArray.push(t2);
  },
  // 假分数计算,小方块运动
  _mulTranslateCompute: function _mulTranslateCompute(opt, ctn, zoom) {
    var self = this;
    var visible = opt.yfbtn ? 'inherit' : 'visible';
    ctn.find('li').each(function () {
      // $(this).removeClass('border');
      var cloneNode = $(this).find('>.js_item').clone();
      cloneNode.find('.num').remove();
      cloneNode.last().find('.caf_part_line_h').addClass('hide_dom');
      $(this).find('.smallItem_h').removeClass('smallItem_brown_bg smallItem_green_bg smallItem_pink_bg');
      $(this).find('.show_result').css('visibility', visible).append(cloneNode);
    });
    var pinkBrownSpan = ctn.find('.show_result .smallItem_pink_bg, .show_result .smallItem_brown_bg');
    var greenSpan = ctn.find('.show_result .smallItem_green_bg');
    var pinkBrownLen = pinkBrownSpan.length;
    var greenLen = greenSpan.length;

    this._hideNum([ctn]);

    var smallestNum = void 0; // 算出从哪一个绿色方块开始移动
    for (var k = 0; k < pinkBrownLen; k++) {
      if (this._getEleOffsetXD(pinkBrownSpan[k], greenSpan[greenLen - k - 1]) >= 0) break;
      if ($(pinkBrownSpan[k]).closest('div').is($(greenSpan[greenLen - k - 1]).closest('div'))) break;
      smallestNum = greenLen - k - 1;
    }
    var i = void 0;
    for (i = 0; i < pinkBrownLen; i++) {
      var h = smallestNum + i;
      if (h > greenLen - 1) break;
      var pos = this._getTransXY(pinkBrownSpan[i], greenSpan[h]);
      pos.x *= 1.95 / zoom;
      pos.y *= 1.95 / zoom;
      $(greenSpan[h]).empty().addClass('trans_clone');
      greenSpan[h].style.webkitTransform = 'translate(' + pos.x + 'px, ' + pos.y + 'px)';
      greenSpan[h].style.transform = 'translate(' + pos.x + 'px, ' + pos.y + 'px)';
      $(greenSpan[h]).css('visibility', 'hidden');
    }
    var t1 = setTimeout(function () {
      pinkBrownSpan.slice(0, i).addClass('smallItem_green_bg').removeClass('smallItem_pink_bg smallItem_brown_bg ');
    }, opt.yfbtn ? 0 : 1500);

    var t2 = setTimeout(this._hideEmptyCtn.bind(this, opt, ctn, 0, 'smallItem_green_bg', ''), opt.yfbtn ? 0 : 1800);
    // setTimeout(this._hideEmptyCtn.bind(this, opt.lP, brownLen), 1800);
    var t3 = setTimeout(this._adoptPos.bind(this, opt), opt.yfbtn ? 0 : 2500);

    var t4 = setTimeout(function () {
      opt.yfbtn && showCtn([ctn]);
      opt.yfbtn && ctn.find('li').css('visibility', 'visible');
      self._showMulGreenNum(ctn, opt);
    }, opt.yfbtn ? 0 : 2500);

    this._timerArray = this._timerArray.concat([t1, t2, t3, t4]);
    opt.js || this._mulReFaction(opt, ctn);
  },
  // 显示中间大数字
  _showMulGreenNum: function _showMulGreenNum(ctn, opt) {
    if (ctn.find('li>.js_item .smallItem_h').eq(1).find('.num').length !== 0) return;

    ctn.find('li').not('.deleted').find('.show_result').each(function () {
      var greenLen = $(this).find('.smallItem_green_bg').not('.trans_clone').length;
      $(this).parent('li').append($('<span class="big_than_25">' + (0, _BaseAnimate.getNum)({
        num: greenLen,
        hasNum: true
      }) + '</span>'));
    });
  },
  // 假分数约分
  _mulReFaction: function _mulReFaction(opt, ctn) {
    if (opt.yf && !opt.yf.m) return;
    // if (opt.yf.m > 10) return;
    var parent = ctn || opt.rP.siblings('div');
    var delay1 = opt.yfbtn ? 1500 : 3800;
    var t1 = setTimeout(function () {
      parent.find('.caf_part_line_h').each(function (index) {
        $(this).removeClass('caf_part_line_h').addClass('caf_part_line_h_dashed');
        $(this).css({ // 自适应
          'backgroundRepeat': 'repeat-x',
          'backgroundSize': 100 / opt.num1.m + '% 100%' // 虚线自适应
        });
      });
      parent.find('.caf_part_line_v').each(function () {
        $(this).addClass('caf_part_line_v_dashed');
      });
    }, opt.zhss ? 0 : delay1);
    var t2 = setTimeout(function () {
      var liDoms = parent[0].tagName === 'LI' ? parent : parent.find('li').not('.deleted');
      var len = liDoms.length;
      var divVer = void 0; // 约分后的有几行
      if (opt.num2.m % opt.yf.m === 0 || opt.yf.m % opt.num2.m === 0) {
        divVer = opt.yf.m / opt.num2.m;
      } else {
        divVer = opt.yf.m / opt.num1.m;
      }
      divVer = Math.ceil(Math.max(1, divVer));
      var hasNum = true;
      var liDomsLen = liDoms.length;
      liDoms.each(function (index) {
        $(this).empty().css('whiteSpace', 'normal');
        var divH = divVer === 1 ? opt.yf.m : opt.yf.m / divVer;
        for (var i = 0; i < opt.yf.m; i++) {
          var hasBorder = (i + 1) % divH !== 0;
          var hasBorderH = i < opt.yf.m - divH;

          hasNum = !!(i === 0 || i === opt.yf.m - 1);
          if (opt.yf.m <= 25) hasNum = true;

          if (index === len - 1) {
            if (i < opt.yf.z - opt.yf.m * index) {
              $(this).append(getSmallItem({
                'divNum': divH,
                'hasBorder': hasBorder,
                'hasBag': true,
                'bg': 'green',
                'n': 1,
                'num': i + 1,
                'hasNum': hasNum,
                'divVer': divVer,
                'hasBorderH': hasBorderH,
                'm1': opt.yf.m
              }));
            } else {
              $(this).append(getSmallItem({
                'divNum': divH,
                'hasBorder': hasBorder,
                'hasBag': true,
                'bg': 'trans',
                'n': 1,
                'num': i + 1,
                'hasNum': hasNum,
                'divVer': divVer,
                'hasBorderH': hasBorderH,
                'm1': opt.yf.m
              }));
            }
          } else {
            $(this).append(getSmallItem({
              'divNum': divH,
              'hasBorder': hasBorder,
              'hasBag': true,
              'bg': 'green',
              'n': 1,
              'num': i + 1,
              'hasNum': hasNum,
              'divVer': divVer,
              'hasBorderH': hasBorderH,
              'm1': opt.yf.m
            }));
          }
        }

        if (opt.yf.m > 25) {
          var numShow = void 0;
          if (liDomsLen > 1) {
            numShow = opt.yf.m;
            if (index > 0) {
              numShow = opt.yf.z - opt.yf.m * index;
            }
          } else {
            numShow = opt.yf.z;
          }
          $(this).append($('<span class="big_than_25">' + (0, _BaseAnimate.getNum)({
            num: numShow,
            hasNum: true
          }) + '</span>'));
        }
      });
    }, opt.zhss ? 0 : delay1 + 500);
    this._timerArray = this._timerArray.concat([t1, t2]);
  },
  // 获取brown
  _getBgData: function _getBgData(i, j, lLi, rLi) {
    var brownLen = $(lLi[i]).find('>.caf_part_item_brown_v').length;
    var pinkLen = $(rLi[j]).find('>.caf_part_item_pink_h').length;
    return {
      b: brownLen,
      p: pinkLen
    };
  },
  _mulLiTrans: function _mulLiTrans(ele1, ele2, ele3, opt) {
    var visible = opt.yfbtn ? 'inherit' : 'visible';
    var pos1 = this._getTransXY(ele3, ele1, true);
    var pos2 = this._getTransXY(ele3, ele2, true);
    pos1.x = pos1.x / 0.78;
    pos2.x = pos2.x / 0.78;
    $(ele1).css('visibility', visible).addClass('trans_clone');
    $(ele2).css('visibility', visible).addClass('trans_clone');
    this._setTransform(ele1, pos1, 0.4 / 0.78, true);
    this._setTransform(ele2, pos2, 0.4 / 0.78, true);
  },

  _setTransform: function _setTransform(ele, pos, zoom, origin) {
    if (origin) {
      ele.style.webkitTransformOrigin = '0 50%';
      ele.style.TransformOrigin = '0 50%';
    }
    ele.style.webkitTransform = 'translate3d(' + pos.x + 'px, ' + pos.y + 'px, 0px)scale(' + zoom + ')';
    ele.style.transform = 'translate3d(' + pos.x + 'px, ' + pos.y + 'px, 0px)scale(' + zoom + ')';
  },
  _preMulTrans: function _preMulTrans(ctn, indexNum) {
    ctn.css('verticalAlign', 'top').find('>span').addClass('hide_dom');
    ctn.find('li').each(function (i) {
      $(this).css({
        'zIndex': indexNum - i,
        'transform': 'scale(0.78)',
        'transform-origin': '50% 0'
      });
    });

    ctn.find('.show_result').each(function (index) {
      var cloneNode = $(this).siblings('span').clone();
      cloneNode.find('.num').remove();
      $(this).css({
        'visibility': 'hidden',
        'zIndex': 0
      }).addClass('border').append(cloneNode);

      var i = 2;
      while (i--) {
        $(this).parent('li').prepend($(this).clone()); // 复制div
      }
    });
  },
  // 计算完成后重新容器宽度以居中显示
  _adoptPos: function _adoptPos(opt, ctn) {
    var baseLiLen = parseFloat(opt.lP.find('li').css('width'));
    // console.log(baseLiLen)
    opt.lP.css('width', '100%');
    // opt.rP.addClass('0%');
    opt.lP.find('li').width(baseLiLen + 'px');
    var rCtnLi = opt.rP.find('li').not('.deleted').width(baseLiLen + 'px');

    var lastSpan = opt.lP.find('>span:last-child');
    if (!opt.rP.hasClass('deleted')) rCtnLi.insertBefore(lastSpan);

    opt.lP.find('.show_result').each(function () {
      if ($($(this)[0]).find('.js_item').length < 26) return;
      var brownNum = $(this).find('.caf_part_item_brown_v').not('.trans_clone').length;
      $(this).append($('<span class="big_than_25">' + (0, _BaseAnimate.getNum)({
        num: brownNum,
        hasNum: true
      }) + '</span>'));
    });
  },
  // 复制通分dom到外层
  _preTranslate: function _preTranslate(ctn) {
    ctn.find('li>.js_item').remove();
    ctn.find('.show_result').each(function () {
      var cloneNodes = $(this).children('.js_item').clone();
      // cloneNodes.find('.num').addClass('hide_dom');
      $(this).parent('li').append(cloneNodes);
    });
    ctn.find('li>.caf_part_item_brown_v').removeClass('caf_part_item_brown_v').addClass('caf_part_item_transparent');
  },
  // 清空全透明色块
  _hideEmptyCtn: function _hideEmptyCtn(opt, ctn, iterateLen, className, relattion) {
    var classN = className || 'caf_part_item_brown_v';
    var relate = relattion === '' ? ' ' : '>';
    if (ctn.find('.' + classN).length === iterateLen) {
      ctn.find('li').addClass('deleted');
      ctn.addClass('deleted');
      ctn.fadeOut(opt.yfbtn ? 0 : 500);
      return;
    }
    ctn.find('li').each(function () {
      var aLen = $(this).find('div' + relate + '.' + classN).length;
      var bLen = $(this).find('div' + relate + '.trans_clone').length;
      if (aLen === bLen) {
        $(this).addClass('deleted');
        $(this).fadeOut(opt.yfbtn ? 0 : 500);
      }
    });
  },
  // 隐藏文字
  _hideText: function _hideText(opt) {
    opt.rP.find('.txt').addClass('hide_dom');
    opt.lP.find('.txt').addClass('hide_dom');
    opt.zP && opt.zP.find('.txt').addClass('hide_dom');
  },
  _hideNum: function _hideNum(ctn) {
    ctn.forEach(function (item) {
      item.find('.big_than_25').addClass('hide_dom');
    });
  },
  // 获取平移距离
  _getTransXY: function _getTransXY(ele1, ele2, fix) {
    // if (!ele2) return;

    var pos1 = $(ele1).offset();
    var pos2 = $(ele2).offset();

    return {
      x: pos1.left - pos2.left,
      y: pos1.top - pos2.top
    };
  },
  _getEleOffsetXD: function _getEleOffsetXD(ele1, ele2) {
    // if (!ele2) return;
    return $(ele1).offset().left - $(ele2).offset().left;
  },

  destroy: function destroy() {
    clearTimer(this._timerArray);
  }

};
exports.ComputeAnimate = ComputeAnimate;
exports.clearTimer = clearTimer;
exports.showCtn = showCtn;

