/** 私有方法 **/
function getCtn(wh) {
  return $('<li class="caf_li_item"><div class="show_result"></div></li>').css({
    'width': 100 * 0.85 / wh.w + '%'
    // 'height': wh.h,
  });
}

function getTextItem(text) {
  return $('<span class="caf_add_left_text"><span class="txt">' + text + '</span></span>');
}

function getNum(num, scaleW) {
  var halfWidth = scaleW ? ' halfWidth ' : '';
  if (num < 10) {
    return '<span class="num num_' + num + halfWidth + '"></span>';
  } else {
    var numStr = num + '';
    var numArr = numStr.split('');
    return '<span class="num num_' + numArr[0] + halfWidth + ' second_num_size"></span>' + '<span class="num num_' + numArr[1] + halfWidth + ' second_num_size second_num_pos"></span>';
  }
}

function getItem(type, m, f, num, hasBorder, isVertical, scaleW) {
  var h = f || 1;
  var border = hasBorder ? isVertical ? 'caf_part_line_v' : 'caf_part_line_h' : 'hide_dom';
  var heught = CONFIG.BLOCK_HEIGHT[h - 1];
  switch (type) {
    case 'brown':
      return $('<span class="js_item caf_part_item_brown_v">' + getNum.call(this, num, scaleW) + '<span class="' + border + '"></span>' + '</span>').css({
        'width': 100 / m + '%',
        'height': heught
      });
    case 'pink':
      return $('<span class="js_item caf_part_item_pink_h">' + getNum.call(this, num, scaleW) + '<span class="' + border + '"></span>' + '</span>').css({
        'width': 100 / m + '%',
        'height': heught
      });
    case 'trans':
      return $('<span class="js_item caf_part_item_transparent">' + getNum.call(this, num, scaleW) + '<span class="' + border + '"></span>' + '</span>').css({
        'width': 100 / m + '%',
        'height': heught
      });
  }
}

function getSmallItem(num, divNum, hasBorder, isVertical, isHide, hasBag, isBorderHide) {
  var isShow = isHide ? 'hide_dom' : '';
  var hasBg = hasBag ? 'smallItem_bg' : '';
  var border = hasBorder ? isVertical ? 'caf_part_line_v' : 'caf_part_line_h' : 'hide_dom';
  var borderHide = isBorderHide || '';
  return $('<span class="smallItem ' + isShow + hasBg + '">' + getNum.call(this, num) + '<span class="' + border + ' ' + borderHide + '"></span>' + '</span>').css({
    'width': 100 + '%',
    'height': 100 / divNum + '%'

  });
}

var ShowBase = {
  _parent: null,
  _ctn: [],
  _num1: null, // { z: 1, m: 2}
  _num2: null, // { z: 1, m: 2}
  _text: null, // 什么数

  _type: null,
