  init: function init(option) {
    this._parent = option.parent;
    this._num1 = option.num1;
    this._num2 = option.num2;
    this._text = option.text;
    this._ctn = [];
    this._type = option.type;
    this._showNum = option.showNum;
    this._isVertical = option.isVertical;
    this._parentHide = option.parentHide || false;

    option.render && this.baseRender(option.showNum, option.type, option.isVertical, true);
    option.render && this._text && this._parent.append(getTextItem.call(this, this._text));
  },
  baseRender: function baseRender(num, type, isVertical, needEmpty) {
    this.destroy();
    var self = this;
    var maxCtnNum = this._getMaxCtnNum(num);
    var w = isVertical ? num.m : 1;
    var h = isVertical ? 1 : num.m;

    var ctnItem = getCtn.call(this, this._getCtnWidthHheight(isVertical));
    this._parent.removeClass('deleted');
    this._parentHide && this._parent.removeClass('deleted').css('visibility', 'hidden');
    this._parent.parent('div').css('textAlign', 'left');

    this._setParent(num);
    var j = maxCtnNum;
    this._ctn = [];
    while (j--) {
      this._ctn.push(ctnItem.clone());
    }var scaleW = num.m >= 10;
    switch (maxCtnNum) {
      case 1:
        for (var i = 0; i < num.m; i++) {
          var hasBorder = (i + 1) % num.m !== 0;
          if (i < num.z) {
            this._ctn[0].append(getItem.call(this, type, w, h, i + 1, hasBorder, isVertical, scaleW));
          } else {
            this._ctn[0].append(getItem.call(this, 'trans', w, h, i + 1, hasBorder, isVertical, scaleW));
          }
        }
        break;
      case 2:

        for (var _i = 0; _i < num.m; _i++) {
          var _hasBorder = (_i + 1) % num.m !== 0;
          this._ctn[0].append(getItem.call(this, type, w, h, _i + 1, _hasBorder, isVertical, scaleW));
          if (_i < num.z - num.m) {
            this._ctn[1].append(getItem.call(this, type, w, h, _i + 1, _hasBorder, isVertical, scaleW));
          } else {
            this._ctn[1].append(getItem.call(this, 'trans', w, h, _i + 1, _hasBorder, isVertical, scaleW));
          }
        }
