function getNum(option) {
  if (!option.hasNum) return '';
  if (option.num < 10) {
    return '<span style=width:' + getWidth({
      m1: option.m1,
      m2: option.m2,
      isTrueNum: option.isTrueNum,
      halfWidth: option.halfWidth
    }) + '% class="num num_' + option.num + ' "></span>';
  } else {
    var numStr = option.num + '';
    var numArr = numStr.split('');
    return '<span style=width:' + getWidth({
      m1: option.m1,
      m2: option.m2,
      isTrueNum: option.isTrueNum,
      halfWidth: option.halfWidth

    }) + '% class="num num_' + numArr[0] + ' second_num_mul_size "></span>' + '<span style=width:' + getWidth({
