  }
}

/**
 * 获取数字的宽度
 *
 * @param {any} m1 分母1
 * @param {any} m2 分母2
 * @returns
 */
function getWidth(option) {
  var width = void 0;
  if (option.halfWidth) return 80;
  if (!option.m1) return 50;
  if (option.m2 === undefined) {
    if (option.m1 < 10) {
      return 80;
    } else {
      return 50;
    }
  }
  if (option.m2 >= 7) {
    switch (option.m1) {
      case 10:
      case 9:
      case 8:
      case 7:
      case 6:
        width = option.isTrueNum ? 30 : 45;
        break;
      case 5:
        width = option.isTrueNum ? 20 : 35;
        break;
      case 4:
        width = option.isTrueNum ? 15 : 30;
        break;
      case 3:
        width = option.isTrueNum ? 10 : 23;
        break;
      case 2:
        width = option.isTrueNum ? 8 : 13;
        break;
      case 1:
        width = 50;
    }
  } else {
    width = 50;
  }
  return width;
}

function getItem(type, m, f, num, hasBorder, isVertical, hasNum, hasBottomborder, scaleW) {
  var h = f || 1;
  var border = hasBorder ? isVertical ? 'caf_part_line_v' : 'caf_part_line_h' : 'hide_dom';
  var bottomborder = hasBottomborder ? 'caf_part_line_h_dashed' : 'hide_dom';
  var height = _Constants2.default.BLOCK_HEIGHT[h - 1];

  switch (type) {
    case 'brown':
      return $('<span class="js_item caf_part_item_brown_v">' + getNum({
        num: num,
        hasNum: hasNum,
        halfWidth: scaleW
      }) + '<span class="' + border + '"></span>' + '</span>').css({
        'width': 100 / m + '%',
        'height': height
      });
    case 'pink':
      return $('<span class="js_item caf_part_item_pink_h">' + getNum({
        num: num,
        hasNum: hasNum,
        halfWidth: scaleW
      }) + '<span class="' + border + '"></span>' + '</span>').css({
        'width': 100 / m + '%',
        'height': height
      });
    case 'trans':
      return $('<span class="js_item caf_part_item_transparent">' + getNum({
        num: num,
        hasNum: hasNum,
        halfWidth: scaleW
      }) + '<span class="' + border + '"></span>' + '<span class="' + bottomborder + '"></span>' + '</span>').css({
        'width': 100 / m + '%',
        'height': height
      });
  }
}

