          if (intNumIndex === 1) {
            // 被乘数是整数
            if (param.index === 1) {
              // 点被乘数
              if (param.num1.z / param.num1.m === 1) {
                param.$view.mulFParent.removeClass('hide_dom').siblings('ul').addClass('hide_dom');
                $.extend(param, { render: true, text: param.i18n.muled_num, parent: param.$view.mulFParent, type: 'brown', isVertical: true });
                _ShowBase2.default.init(param);
                param.$view.mulFParent.css('width', 100 / (param.num1.z / param.num1.m) + '%').parent('div').css('textAlign', 'center');
                if (param.num1.z / param.num1.m === 1) {
                  param.$view.mulFParent.find('.caf_part_item_brown_v').css('background-size', '100%,100%');
                }
              } else {
                param.$view.mulZParent.removeClass('hide_dom').siblings('ul').addClass('hide_dom');
                $.extend(param, { text: param.i18n.muled_num, parent: param.$view.mulZParent, type: 'brown', isVertical: true, showNum: param.num1 });
                _ShowBase2.default.init(param);
                _ShowBase2.default.mulRender(param.num1, true);
              }
            } else {
              // 点乘数
              if (param.num1.z / param.num1.m === 1) {
                param.$view.mulZParent.removeClass('hide_dom').siblings('ul').addClass('hide_dom');
                $.extend(param, { render: true, text: param.i18n.mul_num, parent: param.$view.mulZParent, type: 'brown', isVertical: true });
                _ShowBase2.default.init(param);
                param.$view.mulZParent.css('width', 100 / (param.num1.z / param.num1.m) + '%').parent('div').css('textAlign', 'center');
              } else {
                param.$view.mulZParent.removeClass('hide_dom').siblings('ul').addClass('hide_dom');
                $.extend(param, { text: param.i18n.mul_num, parent: param.$view.mulZParent, type: 'brown', isVertical: true, showNum: param.num1 });
                _ShowBase2.default.init(param);
                _ShowBase2.default.mulRender(param.num2);
              }
            }
          } else {
            // 乘数是整数
            if (param.index === 1) {
              // 点被乘数
              param.$view.mulZParent.removeClass('hide_dom').siblings('ul').addClass('hide_dom');
              $.extend(param, { render: true, text: param.i18n.muled_num, parent: param.$view.mulZParent, type: 'brown', isVertical: true });
              _ShowBase2.default.init(param);
              param.$view.mulZParent.css('width', 100 / (param.num2.z / param.num2.m) + '%').parent('div').css('textAlign', 'center');
            } else {
              // 点乘数
              if (param.num2.z / param.num2.m === 1) {
                param.$view.mulZParent.removeClass('hide_dom').siblings('ul').addClass('hide_dom');
                $.extend(param, { render: true, text: param.i18n.mul_num, parent: param.$view.mulZParent, type: 'brown', isVertical: true, showNum: param.num1 });
                _ShowBase2.default.init(param);
                param.$view.mulZParent.css('width', 100 / (param.num2.z / param.num2.m) + '%').parent('div').css('textAlign', 'center');
              } else {
                param.$view.mulZParent.removeClass('hide_dom').siblings('ul').addClass('hide_dom');
                $.extend(param, { text: param.i18n.mul_num, parent: param.$view.mulZParent, type: 'brown', isVertical: true, showNum: param.num2 });
                _ShowBase2.default.init(param);
                _ShowBase2.default.mulRender(param.num1);
              }
            }
          }
        } else {
          // 分数乘分数
          if (param.index === 1) {
            var _parent = param.$view.mulLeftParent;
            $.extend(param, { render: true, text: param.i18n.muled_num, parent: _parent, type: 'brown', isVertical: true });
            _ShowBase2.default.init(param);
          } else {
            var _parent2 = param.$view.mulRightParent;
            $.extend(param, { render: true, text: param.i18n.mul_num, parent: _parent2, type: 'pink', isVertical: false });
            _ShowBase2.default.init(param);
            _ShowBase2.default.mulRenderH();
          }
        }
        break;
      case '÷':
        this.except(param);
        break;
      case '-':
        numName = param.index === 1 ? param.i18n.subed_num : param.i18n.sub_num;
        parent = param.index === 1 ? param.$view.subLeftParent : param.$view.subRightParent;
        $.extend(param, { render: true, text: numName, parent: parent, type: 'brown', isVertical: true });
        _ShowBase2.default.init(param);
        break;

      case '+':
        numName = param.index === 1 ? param.i18n.added_num : param.i18n.add_num;
        parent = param.index === 1 ? param.$view.addLeftParent : param.$view.addRightParent;
        $.extend(param, { render: true, text: numName, parent: parent, type: 'brown', isVertical: true });
        _ShowBase2.default.init(param);
        break;

    }
  },
  // 除法展示
  except: function except(param) {
    var n1 = param.num1;
    var n2 = param.num2;
    var intNumIndex = hasInt.call(this, param); // 判断那一个是整数

    if (intNumIndex) {
      if (intNumIndex === 1) {
        // 被除数是整数
        if (param.num2.z === 1) {
          // 分子为1
          if (param.index === 1) {
            // 点被除数
            var parent = param.$view.divNoffParent;
            $.extend(param, { render: true, text: '', parent: parent, type: 'brown', isVertical: true });
            _ShowBase2.default.init(param);
            parent.css('width', 75 + '%');
            if (param.num1.z / param.num1.m === 1) {
              parent.find('.caf_part_item_brown_v').css('background-size', '100%,100%');
            }
          } else {
            // 点除数
            var _parent3 = param.$view.divNoffParent;
            $.extend(param, { text: '', parent: _parent3, type: 'brown', isVertical: true });
            _ShowBase2.default.init(param);
            _ShowBase2.default.oneDivRender();
          }
        } else {
          // 分子不为1为特殊情况
          if (param.index === 1) {
            var _parent4 = param.$view.divLeftParent;
            $.extend(param, { render: true, text: param.i18n.divided_num, parent: _parent4, type: 'brown', isVertical: true });
            _ShowBase2.default.init(param);
          } else {
            var _parent5 = param.$view.divRightParent;
            $.extend(param, { render: true, text: param.i18n.divide_num, parent: _parent5, type: 'brown', isVertical: true });
            _ShowBase2.default.init(param);
          }
        }
      } else {
        // 除数是整数
        if (param.index === 1) {
          // 点 被除数
          var _parent6 = param.$view.divNoffParent;
          $.extend(param, { render: true, text: '', parent: _parent6, type: 'brown', isVertical: true });
          _ShowBase2.default.init(param);
          _parent6.css('width', 75 + '%');
        } else {
          // 点 除数
          if (param.num1.z % (param.num2.z / param.num2.m) === 0) {
            // 能整除
            var _parent7 = param.$view.divNoffParent;
            $.extend(param, { text: '', parent: _parent7, type: 'brown', isVertical: true });
            _ShowBase2.default.init(param);
            _ShowBase2.default.enableDivRender();
          } else {
            // 不能整除
            var _parent8 = param.$view.divNoffParent;
            $.extend(param, { text: '', parent: _parent8, type: 'brown', isVertical: true });
            _ShowBase2.default.init(param);
            _ShowBase2.default.unEnableDivRender();
          }
        }
      }
    } else {
      // 分数除以分数
      if (n1.m === n2.m) {
        // 分母相同
        if (n1.z % n2.z === 0) {
          // 分子除分子能除尽
          if (param.index === 1) {
            var _parent9 = param.$view.divNoffParent;
            $.extend(param, { render: true, text: '', parent: _parent9, type: 'brown', isVertical: true });
            _ShowBase2.default.init(param);
          } else {
            var _parent10 = param.$view.divNoffParent;
            $.extend(param, { text: '', parent: _parent10, type: 'brown', isVertical: true });
            _ShowBase2.default.init(param);
            _ShowBase2.default.zEnableDivRender();
          }
        } else {
          // 分子除分子不能除尽
          if (param.index === 1) {
            var _parent11 = param.$view.divLeftParent;
            $.extend(param, { render: true, text: param.i18n.divided_num, parent: _parent11, type: 'brown', isVertical: true });
            _ShowBase2.default.init(param);
          } else {
            var _parent12 = param.$view.divRightParent;
            $.extend(param, { render: true, text: param.i18n.divide_num, parent: _parent12, type: 'brown', isVertical: true });
            _ShowBase2.default.init(param);
          }
        }
      } else {
        // 分母不相同
        if (param.index === 1) {
          var _parent13 = param.$view.divLeftParent;
          $.extend(param, { render: true, text: param.i18n.divided_num, parent: _parent13, type: 'brown', isVertical: true });
          _ShowBase2.default.init(param);
        } else {
          var _parent14 = param.$view.divRightParent;
          $.extend(param, { render: true, text: param.i18n.divide_num, parent: _parent14, type: 'brown', isVertical: true });
          _ShowBase2.default.init(param);
        }
      }
    }
    param.$view.divNoffParent.parent('div').css('textAlign', 'center');
  },
  showResult: function showResult(param) {
    var self = this;
    (0, _ComputeAnimate.clearTimer)(this._timerArray);
    this._timerArray = [];
    var delay = void 0;
    if (param.yfbtn || param.zhss) {
      delay = 0;
    } else {
      if (param.js) {
        if (param.hasTf) {
          delay = 0;
        } else {
          delay = 1000;
        }
      } else {
        delay = 1000;
      }
    }
    var t1 = setTimeout(function () {
      switch ($.trim(param.style)) {
        case '×':
          var hasIntNum = hasInt.call(self, param);
          $.extend(param, {
            lP: param.$view.mulLeftParent,
            rP: param.$view.mulRightParent,
            zP: param.$view.mulZParent,
            fP: param.$view.mulFParent,
            hasInt: hasIntNum
          });
          _ComputeAnimate.ComputeAnimate.translate(param);

          break;

        case '÷':
          $.extend(param, { lP: param.$view.divLeftParent, rP: param.$view.divRightParent });
          _BaseAnimate.BaseAnimate.addFraction(param);

          break;

        case '-':
          $.extend(param, { lP: param.$view.subLeftParent, rP: param.$view.subRightParent });
          _BaseAnimate.BaseAnimate.addFraction(param);

          break;

        case '+':
          $.extend(param, { lP: param.$view.addLeftParent, rP: param.$view.addRightParent });
          _BaseAnimate.BaseAnimate.addFraction(param);

          break;
      }
    }, delay);
    this._timerArray.push(t1);
  },
  destroy: function destroy() {
    (0, _ComputeAnimate.clearTimer)(this._timerArray);
  }
};
/** 私有类方法 **/
function hasInt(param) {
  var n1 = param.num1;
  var n2 = param.num2;
  if ((n1.z / n1.m | 0) === n1.z / n1.m) {
    return 1;
  }
  if ((n2.z / n2.m | 0) === n2.z / n2.m) {
    return 2;
  }
  return null;
}
exports.default = Connect;

