          lTrans: ko.observable(),
          pTop: ko.observable(),
          fTop: ko.observable(),
          vTop: ko.observable(),
          lTop: ko.observable(),
          h1Top: ko.observable(),
          h11Top: ko.observable(),
          h2Top: ko.observable(),
          h21Top: ko.observable(),
          pathShow: ko.observable(false),
          d: ko.observable(),
          lamp: ko.observable(1)
        },
        i18nText: {
          magneticText: ko.observable(),
          chargedText: ko.observable(),
          analyzeText: ko.observable(),
          formulaText: ko.observable(),
          rFormula: ko.observable(),
          tFormula: ko.observable(),
          startText: ko.observable(),
          stopText: ko.observable()
        }
      };
      this.i18nModel = {
        magneticText: '切换磁场方向',
        chargedText: '切换粒子电荷',
        analyzeText: '显示运动分析',
        hideAnalyzeText: '隐藏运动分析',
        formulaText: '半径、周期公式',
        rFormula: '半径:',
        tFormula: '周期:',
        startText: '计算并演示',
        pauseText: '暂停演示',
        stopText: '停止演示'
      };
      this.option = {
        min: this.initData.minValue, max: this.initData.maxValue, step: 0.1, value: this.initData.defaultValue
      };
      this.param = {
        m: this.initData.defaultValue,
        q: this.initData.defaultValue,
        v: this.initData.defaultValue,
        b: this.initData.defaultValue
      };
    },
    /**
     * Presenter对外暴露的方法
     */
    _service_: {
      constructor: function constructor(parent) {
        this.parent = parent;
      },
      // 获取需要同步给学生端的信息，如需编辑推送功能必须实现该接口
      getExtendData: function getExtendData() {
        return {};
      },
      // 获取题目信息，及对应配置
      getQuestionInfo: function getQuestionInfo() {
        return {
          id: this.parent.model.question_id || '',
          type_code: 'ChargedParticleMoving',
          type_name: '带电粒子在匀强磁场中的运动',
          statistics_type: 'no_need',
          noExistStatistics: true,
          noNeedQuizProgress: true
        };
      },
      setLocationProperties: function setLocationProperties(location) {
        if (location && !$.isEmptyObject(location)) {
          this.parent.i18nModel = location;
        }
      }
    },
    // 获取完整地址前缀
    setBasePath: function setBasePath(path) {
      var me = this;
      me.basePath = path;
    },
    // SubjectToolLauncher通过该接口获取服务，实现调用如：国际化接口
    getService: function getService() {
      var me = this;
      me._service_.constructor(me);
      return me._service_;
    },
    /** **以下开始为icPlayer的生命周期方法*****/
    run: function run(view, model) {
      var me = this;
      me.$view = $(view);
      me.model = model;
      me._initI18n();
      me._initView();
      me._initData();
      ko.applyBindings(this.viewModel, me.$view[0]);
    },
    __interface: {
