    },
    destroy: function destroy() {},
    /** **以下为界面操作接口*****/
    _initData: function _initData() {
      var me = this;
      var svg = me.$view.find('.js_cpm_svg');
      var swidth = svg.width();
      var sheight = svg.height();

      me.viewData = {
        maxDiameter: sheight - 85,
        minDiameter: 110,
        minTime: 3,
        maxTime: 10,
        swidth: swidth,
        sheight: sheight,
        svg: me.$view.find('.js_cpm_svg'),
        point: me.$view.find('.js_cpm_point'),
        f: me.$view.find('.js_cpm_f'),
        v: me.$view.find('.js_cpm_v'),
        lamp: me.$view.find('.js_cpm_lamp'),
        hand1: me.$view.find('.js_cpm_hand1'),
        hand11: me.$view.find('.js_cpm_hand11'),
        hand2: me.$view.find('.js_cpm_hand2'),
        hand21: me.$view.find('.js_cpm_hand21'),
        pTransX: 0,
        pTransY: 0,
        val: 0,
        x: 0,
        y: 0
      };
      // const $svg = $(me.viewData.svg)
      // $svg.attr('viewBox', '0 0 ' + $svg.width() + ' ' + $svg.height())

      me.viewData.diameterUnit = parseFloat(me.viewData.maxDiameter - me.viewData.minDiameter) / (me.initData.maxR - me.initData.minR);
      me.viewData.timeUnit = parseFloat(me.viewData.maxTime - me.viewData.minTime) / (me.initData.maxT - me.initData.minT);
      var dis = me.viewModel.param.magneticDis() - 20;
      me.viewModel.param.rightbottom('translate(' + swidth / 2 + 'px,' + sheight / 2 + 'px)');
      me.viewModel.param.lefttop('translate(' + (swidth / 2 - dis) + 'px,' + (sheight / 2 - dis) + 'px)rotate(180deg)');
      me.viewModel.param.leftbottom('translate(' + (swidth / 2 - dis) + 'px,' + sheight / 2 + 'px)rotate(90deg)');
      me.viewModel.param.righttop('translate(' + swidth / 2 + 'px,' + (sheight / 2 - dis) + 'px)rotate(-90deg)');
      me.viewModel.movingParam.fLength(me.viewData.minDiameter / 2.0 - 15 + 'px');
      me.fLength = me.viewData.minDiameter / 2.0 - 15;
      me.viewModel.movingParam.vLength(me.viewData.minDiameter / 2.0 + 'px');
      me.vLength = me.viewData.minDiameter / 2.0;
      me._resetView();
    },
    _playMoving: function _playMoving() {
      var me = this;

      player.controller.getEventBus().sendEvent('PLAYER.TOOL.FORBIDE_FULL', { id: me.model.$.unqiueId, isForbide: true });
      me.viewData.pointR = me.viewData.point.width() / 2;
      var R = (me.viewData.minDiameter + me.viewData.diameterUnit * (me._e2num(me.viewModel.param.rValue() + 'e' + me.viewModel.param.rEValue()) - me.initData.minR)) / 2.0;
      var invert = me.viewModel.button.chargeType();
      var invertB = me.viewModel.button.magneticDirect();

      var pTop = me.viewData.point.position().top;
      var isEnough = invert ^ invertB ? pTop + me.viewData.pointR - 2 * R - 20 > 0 : pTop + 2 * R + me.viewData.pointR < me.viewData.svg.height();
      if (!isEnough) {
        (function () {
          var now = mina.time();
          var vTran = me.viewModel.movingParam.vTrans();
          var fTran = me.viewModel.movingParam.fTrans();
          if (invert ^ invertB) {
            var dis = 2 * R - pTop + me.viewData.pointR;
            me.moving = mina(0, dis, now, now + Math.abs(dis) / me.viewData.sheight * 3000, mina.time, function (val) {
              var trans = 'translate(0,' + val + 'px)';
              me.viewModel.movingParam.pTrans(trans);
              me.viewData.pTransX = 0;
              me.viewData.pTransY = val;
              me.viewModel.movingParam.lTrans(trans);
              me.viewModel.movingParam.vTrans(trans + vTran);
              me.viewModel.movingParam.fTrans(trans + fTran);
              me.viewModel.movingParam.hTrans(trans);
            }, mina.linear);
          } else {
            var _dis = me.viewData.svg.height() - (pTop + 2 * R + me.viewData.pointR) - 20;
            me.moving = mina(0, _dis, now, now + Math.abs(_dis) / me.viewData.sheight * 3000, mina.time, function (val) {
              var trans = 'translate(0,' + val + 'px)';
              me.viewModel.movingParam.pTrans(trans);
              me.viewData.pTransX = 0;
              me.viewData.pTransY = val;
              me.viewModel.movingParam.lTrans(trans);
              me.viewModel.movingParam.vTrans(trans + vTran);
              me.viewModel.movingParam.fTrans(trans + fTran);
              me.viewModel.movingParam.hTrans(trans);
            }, mina.linear);
          }
          eve.once('mina.finish.' + me.moving.id, function () {
            delete me.moving;
            me.viewModel.movingParam.h1Top(me.viewData.hand1.position().top + 'px');
            me.viewModel.movingParam.h11Top(me.viewData.hand11.position().top + 'px');
            me.viewModel.movingParam.h2Top(me.viewData.hand2.position().top + 'px');
            me.viewModel.movingParam.h21Top(me.viewData.hand21.position().top + 'px');
            me.viewModel.movingParam.hTrans('');
            var tempvp = me.viewData.v.position().top;
            me.viewModel.movingParam.vTop(tempvp + 'px');
            me.viewModel.movingParam.fTop(tempvp + 'px');
            me.viewModel.movingParam.vTrans(vTran);
            me.viewModel.movingParam.fTrans(fTran);
            me.viewModel.movingParam.lTop(me.viewData.lamp.position().top + 'px');
            me.viewModel.movingParam.lTrans('');

            me.viewModel.movingParam.pTop(me.viewData.point.position().top + 'px');
            me.viewData.ptop = me.viewData.point.position().top;
            me.viewModel.movingParam.pTrans('');
            me.viewData.pTransX = 0;
            me.viewData.pTransY = 0;

            me.lampid = mina(10, 0, mina.time(), mina.time() + 500, mina.time, function (val) {
              me.viewModel.movingParam.lamp(val / 10);
            }, mina.linear);
            me.viewData.top = me.viewData.point.offset().top - me.viewData.svg.offset().top;
            me.viewData.left = me.viewData.point.offset().left - me.viewData.svg.offset().left;
            me.viewModel.movingParam.pathShow(true);
            me.viewData.hasFinish = false;
            var startx = me.viewData.left + me.viewData.pointR;
            var starty = me.viewData.top + me.viewData.pointR + R + 2;
            var endy = starty - R;
            if (invert ^ invertB) {
              starty = me.viewData.top + me.viewData.pointR - R;
            }
            me.viewModel.movingParam.cx(startx);
            me.viewModel.movingParam.cy(starty);
            me.viewModel.movingParam.rd('M' + startx + ',' + starty + ' L' + startx + ',' + endy);

            me._movingAction(R, invert, invertB);
          });
        })();
      } else {
        me.lampid = mina(10, 0, mina.time(), mina.time() + 500, mina.time, function (val) {
          me.viewModel.movingParam.lamp(val / 10);
        }, mina.linear);
        me.viewData.top = me.viewData.point.offset().top - me.viewData.svg.offset().top;
        me.viewData.left = me.viewData.point.offset().left - me.viewData.svg.offset().left;
        me.viewModel.movingParam.pathShow(true);
        me.viewData.hasFinish = false;
        var startx = me.viewData.left + me.viewData.pointR;
        var starty = me.viewData.top + me.viewData.pointR + R + 2;
        var endy = starty - R;
        if (invert ^ invertB) {
          starty = me.viewData.top + me.viewData.pointR - R;
        }
        me.viewModel.movingParam.cx(startx);
        me.viewModel.movingParam.cy(starty);
        me.viewModel.movingParam.rd('M' + startx + ',' + starty + ' L' + startx + ',' + endy);

        me._movingAction(R, invert, invertB);
      }
    },
    _movingAction: function _movingAction(R, invert, invertB) {
      var me = this;
      var now = mina.time();
      me.anim = mina(0, 360, now, Math.floor(now + (me.viewData.minTime + (me._e2num(me.viewModel.param.tValue() + 'e' + me.viewModel.param.tEValue()) * 10000 - me.initData.minT) * me.viewData.timeUnit) * 1000), mina.time, _.throttle(function (val) {
        if (me.viewModel.button.playMoving() === 'start') {
          return;
        }
        var radian = val / 180 * Math.PI;
        var x = -(R * Math.sin(radian));
        var y = invertB ^ invert ? -R + R * Math.cos(radian) : R - R * Math.cos(radian);
        me.viewModel.movingParam.pTrans('translate' + '(' + x + 'px,' + y + 'px)');
        me.viewData.pTransX = x;
        me.viewData.pTransY = y;
        me.viewModel.movingParam.vTrans('translate(' + x + 'px,' + y + 'px)' + ' rotate(' + (invert ^ invertB ? 180 + val : 180 - val) + 'deg)');
        me.viewModel.movingParam.fTrans('translate(' + x + 'px,' + y + 'px)' + ' rotate(' + (invertB ^ invert ? -90 + val : 90 - val) + 'deg)');
        me.viewModel.movingParam.vTextTrans('rotate(' + (invert ^ invertB ? 180 - val : 180 + val) + 'deg)');
        me.viewModel.movingParam.fTextTrans('rotate(' + (invertB ^ invert ? 90 - val : -90 + val) + 'deg)');
        if (!me.viewData.hasFinish) {
          if (val === 360) {
            me.viewData.hasFinish = true;
            me.viewModel.movingParam.d(me.viewModel.movingParam.d() + 'Z');
            return;
          }
          var d = 'M' + (me.viewData.left + me.viewData.pointR) + ',' + (me.viewData.top + me.viewData.pointR) + 'A' + R + ',' + R + ',0,' + (val >= 180 ? 1 : 0) + ',' + (invertB ^ invert ? 1 : 0) + ',' + (me.viewData.left + me.viewData.pointR + x) + ',' + (me.viewData.top + me.viewData.pointR + y);
          me.viewData.val = val;
          me.viewData.x = x;
          me.viewData.y = y;
          me.viewModel.movingParam.d(d);
        }
      }, 16, { leading: true, trailing: true }), mina.linear);
      eve.once('mina.finish.' + me.anim.id, function () {
        delete me.anim;
        me._movingAction(R, invert, invertB);
      });
    },
    _initView: function _initView() {
      var me = this;
      me.bars = {};
      var mSListener = function mSListener(e, ui) {
        me._buttonStatus('m', parseFloat(ui.value).toFixed(1));
      };
      me.bars['m'] = me.$view.find('.js_cpm_mbar').slider($.extend(me.option, {
        change: mSListener,
        slide: mSListener
      }));
      var qSListener = function qSListener(e, ui) {
        me._buttonStatus('q', parseFloat(ui.value).toFixed(1));
      };
      me.bars['q'] = me.$view.find('.js_cpm_qbar').slider($.extend(me.option, {
        slide: qSListener,
        change: qSListener
      }));
      var vSListener = function vSListener(e, ui) {
        me._buttonStatus('v', parseFloat(ui.value).toFixed(1));
      };
      me.bars['v'] = me.$view.find('.js_cpm_vbar').slider($.extend(me.option, {
        slide: vSListener,
        change: vSListener
      }));
      var bSListener = function bSListener(e, ui) {
        var value = parseFloat(ui.value).toFixed(1);
        me._buttonStatus('b', value);
        var fvalue = parseFloat(value);
        if (fvalue < me.initData.defaultValue) {
          fvalue = me.initData.magneticMid - (fvalue - me.initData.defaultValue) / Math.abs(me.initData.defaultValue - me.initData.minValue) * (me.initData.magneticMax - me.initData.magneticMid);
        } else {
          fvalue = me.initData.magneticMid - (fvalue - me.initData.defaultValue) / Math.abs(me.initData.defaultValue - me.initData.maxValue) * (me.initData.magneticMid - me.initData.magneticMin);
        }
        console.log(fvalue);
        me.viewModel.param.magneticDis(fvalue);
        var dis = me.viewModel.param.magneticDis() - 20;
        me.viewModel.param.rightbottom('translate(' + me.viewData.swidth / 2 + 'px,' + me.viewData.sheight / 2 + 'px)');
        me.viewModel.param.lefttop('translate(' + (me.viewData.swidth / 2 - dis) + 'px,' + (me.viewData.sheight / 2 - dis) + 'px)rotate(180deg)');
        me.viewModel.param.leftbottom('translate(' + (me.viewData.swidth / 2 - dis) + 'px,' + me.viewData.sheight / 2 + 'px)rotate(90deg)');
        me.viewModel.param.righttop('translate(' + me.viewData.swidth / 2 + 'px,' + (me.viewData.sheight / 2 - dis) + 'px)rotate(-90deg)');
      };
      me.bars['b'] = me.$view.find('.js_cpm_bbar').slider($.extend(me.option, {
        slide: bSListener,
        change: bSListener
      }));
    },
    _buttonStatus: function _buttonStatus(type, value) {
      var me = this;
      me.param[type] = parseFloat(value);
      me.viewModel.param[type + 'Value'](value);
      if (me.param[type] === me.option.max) {
        me.viewModel.param[type + 'Add'](false);
        me.viewModel.param[type + 'Min'](true);
      } else if (me.param[type] === me.option.min) {
        me.viewModel.param[type + 'Add'](true);
        me.viewModel.param[type + 'Min'](false);
      } else {
        me.viewModel.param[type + 'Add'](true);
        me.viewModel.param[type + 'Min'](true);
      }
    },
    _updateParamButtom: function _updateParamButtom(disabled) {
      var me = this;
      $.each(me.bars, function (k, v) {
        if (!disabled) {
          me.viewModel.param[k + 'Add'](false);
          me.viewModel.param[k + 'Min'](false);
          v.slider('disable');
        } else {
          v.slider('enable');
          v.slider('value', me.param[k]);
        }
      });
    },
    _initI18n: function _initI18n() {
      var me = this;
      $.each(me.viewModel.i18nText, function (k, v) {
        v(me.i18nModel[k]);
      });
    },
    _changeValue: function _changeValue(enable, type, action) {
      var me = this.presenter || this;
      if (!enable) {
        return;
      }
      me.bars[type].slider('value', action === 'add' ? me.param[type] + 0.1 : me.param[type] - 0.1);
    },
    _num2e: function _num2e(num) {
      var p = Math.floor(Math.log(num) / Math.LN10);
      var n = num * Math.pow(10, -p);
      return [p, n, p + 'e' + n];
    },
    _e2num: function _e2num(e) {
      var num = Number(e);
      return parseFloat(num.toString());
    },
    _buttonEvent: function _buttonEvent(action, param) {
      var me = this.presenter || this;
      switch (action) {
        case 'showFormula':
          {
            me.viewModel.button.displayFormula(!me.viewModel.button.displayFormula());
            break;
          }
        case 'changeMagnetic':
          {
            if (me.viewModel.button.playMoving() === 'pause' || me.viewModel.button.playMoving() === 'playing') {
              return;
            }
            me.viewModel.button.magneticDirect(!me.viewModel.button.magneticDirect());
            me._resetView();
            break;
          }
        case 'changeCharge':
          {
            if (me.viewModel.button.playMoving() === 'pause' || me.viewModel.button.playMoving() === 'playing') {
              return;
            }
            me.viewModel.button.chargeType(!me.viewModel.button.chargeType());
            me._resetView();
            break;
          }
        case 'displayMoving':
          {
            me.viewModel.button.displayMoving(!me.viewModel.button.displayMoving());
            if (me.viewModel.button.displayMoving()) {
              me.viewModel.i18nText.analyzeText(me.i18nModel.hideAnalyzeText);
            } else {
              me.viewModel.i18nText.analyzeText(me.i18nModel.analyzeText);
            }
            break;
          }
        case 'playMoving':
          {
            me._updateParamButtom(false);
            if (me.viewModel.button.playMoving() === 'playing') {
              me.viewModel.i18nText.startText(me.i18nModel.startText);
              me.viewModel.button.playMoving('pause');
              if (me.moving) {
                me.moving.pause();
              }
              if (me.anim) {
                me.anim.pause();
              }
            } else {
              me.viewModel.button.playMoving('playing');
              me.viewModel.i18nText.startText(me.i18nModel.pauseText);
              if (me.moving) {
                // avoid snap mina bug
                mina(1, 2, mina.time(), mina.time() + 1, mina.time, function (val) {}, mina.easeout);
                me.moving.resume();
              } else if (me.anim) {
                // avoid snap mina bug
                mina(1, 2, mina.time(), mina.time() + 1, mina.time, function (val) {}, mina.easeout);
                me.anim.resume();
              } else {
                var r = me._num2e(me.param.m * me.param.v / (me.param.q * me.param.b));
                var t = me._num2e(2 * Math.PI * me.param.m / (me.param.q * me.param.b));
                me.viewModel.param.rValue(r[1].toFixed(2));
                me.viewModel.param.tValue(t[1].toFixed(2));
                me.viewModel.param.rEValue(r[0] + 2);
                me.viewModel.param.tEValue(t[0] - 2);
                me._playMoving();
              }
            }
            break;
          }
        case 'playStop':
          {
            if (me.viewModel.button.playMoving() === 'start') {
              return;
            }

            player.controller.getEventBus().sendEvent('PLAYER.TOOL.FORBIDE_FULL', { id: me.model.$.unqiueId, isForbide: false });
            me._updateParamButtom(true);
            me.viewModel.button.playMoving('start');
            me.viewModel.i18nText.startText(me.i18nModel.startText);
            me.viewModel.movingParam.pathShow(false);
            me.viewData.hasFinish = false;
            // playing circle
            if (me.anim) {
              me.anim.stop();
              delete me.anim;
            }
            // playing moving
            if (me.moving) {
              me.moving.stop();
              var vPos = me.viewData.v.position().top;
              me.viewModel.movingParam.fTop(vPos + 'px');
              me.viewModel.movingParam.fTrans(me.viewModel.movingParam.fTrans());
              me.viewModel.movingParam.vTop(vPos + 'px');
              me.viewModel.movingParam.vTrans(me.viewModel.movingParam.vTrans());
              me.viewModel.movingParam.lTop(me.viewData.lamp.position().top + 'px');
              me.viewModel.movingParam.lTrans('');
              me.viewModel.movingParam.h1Top(me.viewData.hand1.position().top + 'px');
              me.viewModel.movingParam.h11Top(me.viewData.hand11.position().top + 'px');
              me.viewModel.movingParam.h2Top(me.viewData.hand2.position().top + 'px');
              me.viewModel.movingParam.h21Top(me.viewData.hand21.position().top + 'px');
              me.viewModel.movingParam.hTrans('');
              me.viewModel.movingParam.pTop(me.viewData.point.position().top + 'px');
              me.viewModel.movingParam.pTrans('');
              me.viewData.pTransX = 0;
              me.viewData.pTransY = 0;
              delete me.moving;
            }
            if (me.lampid) {
              me.lampid.stop();
              me.viewModel.movingParam.lamp(1);
              delete me.lampid;
            }
            me.viewModel.param.rValue('');
            me.viewModel.param.tValue('');
            me._resetView();
            break;
          }
      }
    },
    _resetView: function _resetView() {
      var me = this;
      me.viewModel.movingParam.pTrans('');
      me.viewData.pTransX = 0;
      me.viewData.pTransY = 0;
      var type = (me.viewModel.button.chargeType() ? 'Positive' : 'Negative') + (me.viewModel.button.magneticDirect() ? 'InSide' : 'OutSide');
      switch (type) {
        case 'PositiveInSide':
          {
            me.viewModel.movingParam.vTrans('rotate(180deg)');
            me.viewModel.movingParam.vTextTrans('rotate(180deg)');
            me.viewModel.movingParam.fTrans('rotate(90deg)');
            me.viewModel.movingParam.fTextTrans('rotate(-90deg)');
            break;
          }
        case 'PositiveOutSide':
          {
            me.viewModel.movingParam.vTrans('rotate(180deg)');
            me.viewModel.movingParam.vTextTrans('rotate(180deg)');
            me.viewModel.movingParam.fTrans('rotate(-90deg)');
            me.viewModel.movingParam.fTextTrans('rotate(90deg)');
            break;
          }
        case 'NegativeInSide':
          {
            me.viewModel.movingParam.vTrans('rotate(180deg)');
            me.viewModel.movingParam.vTextTrans('rotate(180deg)');
            me.viewModel.movingParam.fTrans('rotate(-90deg)');
            me.viewModel.movingParam.fTextTrans('rotate(90deg)');
            break;
          }
        case 'NegativeOutSide':
          {
            me.viewModel.movingParam.vTrans('rotate(180deg)');
            me.viewModel.movingParam.vTextTrans('rotate(180deg)');
            me.viewModel.movingParam.fTrans('rotate(90deg)');
            me.viewModel.movingParam.fTextTrans('rotate(-90deg)');
            break;
          }
      }
    }
  });
  window.AddonChargedParticleMoving_create = function () {
    return new Presenter();
  };
})();
