	          {
	            if (isSymbol) {
	              return _ViewModel2.default.$view.find('.s_dianyuan');
	            } else {
	              return _ViewModel2.default.$view.find('.p_dianyuan');
	            }
	          }
	        case 'sliderheostat':
	          {
	            if (isSymbol) {
	              return _ViewModel2.default.$view.find('.s_hdbanzuqi');
	            } else {
	              return _ViewModel2.default.$view.find('.p_hdbanzuqi');
	            }
	          }
	        case 'switch':
	          {
	            if (isSymbol) {
	              return _ViewModel2.default.$view.find('.s_kaiguan');
	            } else {
	              return _ViewModel2.default.$view.find('.p_kaiguan');
	            }
	          }
	        case 'voltmeter':
	          {
	            if (isSymbol) {
	              return _ViewModel2.default.$view.find('.s_dianyabiao');
	            } else {
	              return _ViewModel2.default.$view.find('.p_dianyabiao');
	            }
	          }
	      }
	    }
	  }, {
	    key: 'initEvents',
	    value: function initEvents() {
	      this.initListEvent(this);
	      this.initMoveAreaEvent(this);
	    }
	  }, {
	    key: 'initListEvent',
	    value: function initListEvent(that) {
	      var $symbollist = $(_ViewModel2.default.$view.find('.symbol_list li'));
	      $symbollist.unbind(this.touchstart);
	      $symbollist.unbind(this.touchmove);
	      $symbollist.unbind(this.touchend);
	      $symbollist.bind(this.touchstart, function (e) {
	        if (e && !e.clientX && e.originalEvent.touches[0] && e.originalEvent.touches[1]) {
	          return;
	        }
	        that.selectSymbol = $(this);
	        that.selectSymbol.addClass('ui_btn_active');
	        e.preventDefault();
	        that.action = $(this).data().action;
	        that.isSymbol = !!$(this).data().symbol;
	        that.moveObj = that.getMoveObj(that.action, that.isSymbol);
	        that.canvasTop = that.viewHoder.canvasDiv.offset().top;
	        that.canvasLeft = that.viewHoder.canvasDiv.offset().left;
	        that.canvasRight = that.viewHoder.canvasDiv.width() + that.canvasLeft;
	        that.canvasBottom = that.viewHoder.canvasDiv.height() + that.canvasTop;
	        that.moveAreaOffsetLeft = that.viewHoder.moveArea.offset().left;
	        that.moveAreaOffsetTop = that.viewHoder.moveArea.offset().top;
	        that.egretZoomX = that.viewHoder.canvasDiv.width() / 1920;
	        that.egretZoomY = that.viewHoder.canvasDiv.height() / 1200;
	        that.isMovingTime = setTimeout(function () {
	          var X = 0;
	          var Y = 0;
	          X = e.clientX ? e.clientX : e.originalEvent.touches[0].clientX;
	          Y = e.clientY ? e.clientY : e.originalEvent.touches[0].clientY;
	          that.isMoving = true;
	          that.moveObj.css('position', 'fixed');

	          that.moveObjOffsetHeight = that.moveObj.height() / 2;
	          that.moveObjOffsetWidth = that.moveObj.width() / 2;
	          that.moveObj.css('top', Y - that.moveObjOffsetHeight + 'px');
	          that.moveObj.css('left', X - that.moveObjOffsetWidth + 'px');
	          that.moveObj.css('display', '');
	        }, 150);
	      });
	      $symbollist.bind(this.touchmove, function (e) {
	        if (e && !e.clientX && e.originalEvent.touches[0] && e.originalEvent.touches[1]) {
	          return;
	        }
	        e.preventDefault();
	        if (!that.isMoving) {
	          e.stopPropagation();
	        }
	      });
	      $symbollist.bind(this.touchend, function (e) {
	        if (e && !e.clientX && e.originalEvent.touches[0] && e.originalEvent.touches[1]) {
	          return;
	        }
	        if (that.selectSymbol) {
	          that.selectSymbol.removeClass('ui_btn_active');
	          that.selectSymbol = null;
	        }
	        e.stopPropagation();
	        clearTimeout(that.isMovingTime);
	        if (!that.isMoving && that.action) {
	          if (window.egret_stages) {
	            window.egret_stages[0].dispatchEventWith('sendAction', true, {
	              'action': that.action,
	              'isSymbol': that.isSymbol
	            });
	            that.action = null;
	          }
	        } else {
	          that.cancelMove(that);
	        }
	      });
	    }
	  }, {
	    key: 'initMoveAreaEvent',
	    value: function initMoveAreaEvent(that) {
	      var $moveArea = $(that.viewHoder.moveArea);
	      $moveArea.unbind(this.touchcancel);
	      $moveArea.unbind(this.touchstart);
	      $moveArea.unbind(this.touchend);
	      $moveArea.unbind(this.touchmove);
	      $moveArea.bind(this.touchstart, function (e) {
	        if (e && !e.clientX && e.originalEvent.touches[0] && e.originalEvent.touches[1]) {
	          return;
	        }
	        e.preventDefault();
	      });
	      $moveArea.bind(this.touchmove, function (e) {
	        if (e && !e.clientX && e.originalEvent.touches[0] && e.originalEvent.touches[1]) {
	          return;
	        }
	        e.preventDefault();
	        if (that.isMovingTime) {
	          clearTimeout(that.isMovingTime);
	        }
	        if (that.isMoving && that.moveObj) {
	          var X = 0;
	          var Y = 0;
	          X = e.clientX ? e.clientX : e.originalEvent.touches[0].clientX;
	          Y = e.clientY ? e.clientY : e.originalEvent.touches[0].clientY;
	          that.moveObj.css('top', Y - that.moveObjOffsetHeight + 'px');
	          that.moveObj.css('left', X - that.moveObjOffsetWidth + 'px');
	          if (X < that.canvasRight && X > that.canvasLeft && Y > that.canvasTop && Y < that.canvasBottom) {
	            var egretx = (X - that.moveAreaOffsetLeft) / that.egretZoomX;
	            var egrety = (Y - that.moveAreaOffsetTop) / that.egretZoomY;
	            if (window.egret_stages && !that.hasAdd && that.action) {
	              that.moveObj.css('display', 'none');
	              window.egret_stages[0].dispatchEventWith('sendAction', true, {
	                'action': that.action,
	                'isSymbol': that.isSymbol,
	                'notSave': true,
	                'dragX': egretx,
	                'dragY': egrety
	              });
	              that.action = null;
	              that.hasAdd = true;
	            }
	            if (that.hasAdd) {
	              if (window.egret_stages) {
	                that.action = 'move';
	                window.egret_stages[0].dispatchEventWith('sendAction', true, {
	                  'action': that.action,
	                  'dragX': egretx,
	                  'dragY': egrety
	                });
	              }
	            }
	          }
	        }
	      });
	      $moveArea.bind(this.touchend, function (e) {
	        if (e && !e.clientX && e.originalEvent.touches[0] && e.originalEvent.touches[1]) {
	          return;
	        }
	        that.cancelMove(that);
	      });
	      $moveArea.bind(this.touchcancel, function (e) {
	        if (e && !e.clientX && e.originalEvent.touches[0] && e.originalEvent.touches[1]) {
	          return;
	        }
	        that.cancelMove(that);
	      });
	    }
	  }, {
	    key: 'cancelMove',
	    value: function cancelMove(that) {
	      if (that.selectSymbol) {
	        that.selectSymbol.removeClass('ui_btn_active');
	        that.selectSymbol = null;
	      }
	      if (that.isMoving && that.moveObj) {
	        if (window.egret_stages) {
	          that.action = 'moveend';
	          window.egret_stages[0].dispatchEventWith('sendAction', true, {
	            'action': that.action
	          });
	        }
	        that.isMoving = false;
	        that.hasAdd = false;
	        clearTimeout(that.isMovingTime);
	        that.moveObj.css('display', 'none');
	        that.moveObj = null;
	      }
	    }
	  }]);

	  return DrawAction;
	}();

	exports.default = DrawAction;

