addon.presenter:
'use strict';

(function () {
    var helper = {
        pxmRatio: 0.35, //px/v,默认1px=0.1m/s
        diameter: 150 - 15, //小球的直径150,图片边缘空白12个像素
        domObj: {},
        i18n: null,
        showType: 'elastic' //默认弹性碰撞
    };
    var collisionController = void 0;
    var Presenter = BasicPresenter.extend({
        metaInfo: {
            name: 'Collision',
            type: PresenterType.PRESENTER_COMPONENT
        },
        /**
         * Presenter的初始化方法
         * @private
         */
        $init: function $init() {
            this._super();
            //定义日志
            this.logger.appendHandler(function (message, context) {
                switch (context.level.name) {
                    case 'DEBUG':
                        console.debug(message);
                        break;
                    case 'INFO':
                        console.info(message);
                        break;
                    case 'WARN':
                        console.warn(message);
                        break;
                    case 'ERROR':
                        console.error(message);
                        break;
                    default:
                        console.log(message);
                        break;
                }
            }, { level: Logger.DEBUG });
        },
        /**
         * Presenter对外暴露的方法
         */
        _service_: {
            constructor: function constructor(parent) {
                this.parent = parent;
            },
            setLocationProperties: function setLocationProperties(properties) {
                helper.i18n = properties;
            },
            getExtendData: function getExtendData() {
                var options = {};
                return options;
            },
            getQuestionInfo: function getQuestionInfo() {
                return {
                    id: helper.model.ID,
                    type_code: 'Collision',
                    type_name: helper.i18n['collision'],
                    url: '',
                    item: 'Collision',
                    statistics_type: 'no_need',
                    dispatchOnly: true,
                    notExistStatistics: true,
                    noNeedQuizProgress: true
                };
            }
        },
        getService: function getService() {
            this._service_.constructor(this);
            return this._service_;
        },
        /****以下开始为icPlayer的生命周期方法*****/
        run: function run(view, model) {
            var _this = this;

            helper.model = model;
            helper.$view = $(view);
            helper.basePath = this.basePath;
            this._initComponent();

            var $deferred = $.Deferred();
            require(['PublishSubscribe', 'CollisionController'], function (PublishSubscribe, CollisionController) {
                helper.publishSubscribe = new PublishSubscribe();
                collisionController = new CollisionController({ helper: helper });
            }).then(function () {
                _this.logger.log('加载[CollisionController]模块完成');
                $deferred.resolve();
            }).catch(function () {
                _this.logger.error('加载模块[CollisionController]失败');
                $deferred.reject();
            });
            return $deferred.promise();
        },
        pageShow: function pageShow(view, model) {
            this._bindEvent();
            collisionController.init();
            ko.applyBindings(helper.i18n, view);
        },
        pageLeave: function pageLeave() {
            this._unbindEvent();
        },
        destroy: function destroy() {
            collisionController.destroy();
        },
        /**如果不需要处理icplayer的状态恢复事件, 请将以下两个方法删除掉**/
        getState: function getState() {},
        setState: function setState(state, options) {},
        /****以下开始为Presenter的私有方法*****/
        _initComponent: function _initComponent() {
            //工具底部按钮
            helper.domObj.$jsCollisionChooseBtn = helper.$view.find('._js_collision_choose_btn a');

            //弹出框
            helper.domObj.$jsPopBoxElasticEquation = helper.$view.find('._js_pop-box_elastic-equation');
            /*弹性公式*/
            helper.domObj.$jsPopBoxInelasticEquation = helper.$view.find('._js_pop-box_inelastic-equation');
            /*非弹性公式*/
            helper.domObj.$jsYesIKnownBtn = helper.$view.find('._js_yes_i_known_btn');
            /*我知道了 按钮*/

            helper.domObj.$jsPopBoxResetTips = helper.$view.find('._js_pop-box_reset_tips');
            /*重置提示框*/
            helper.domObj.$jsPopBoxResetTipsBtns = helper.$view.find('._js_pop-box_reset_tips a');
            /*重置提示框 按钮*/

            // 操作按钮
            helper.domObj.$jsToolBtnsStep = helper.$view.find('._js_tool_btns_step a');

            //展示内容框
            helper.domObj.$jsCollisionDemonstration = helper.$view.find('._js_collision_demonstration'); //弹性碰撞
            helper.domObj.$jsNonelasticCollisionDemonstration = helper.$view.find('._js_nonelastic_collision_demonstration'); //非弹性碰撞
            helper.domObj.$jsNoCentralCollisionDemonstration = helper.$view.find('._js_noCentral_collision_demonstration'); //非对心碰撞
        },
        _bindEvent: function _bindEvent() {
            //工具底部按钮
            helper.domObj.$jsCollisionChooseBtn.on('click', function () {
                var $this = $(this);
                var type = $this.data('type');
                $this.addClass('ui_btn_active').siblings().removeClass('ui_btn_active');
                switch (type) {
                    case 'elastic':
                        /*弹性*/
                        helper.domObj.$jsCollisionDemonstration.removeClass('hide_dom');
                        helper.domObj.$jsNonelasticCollisionDemonstration.addClass('hide_dom');
                        helper.domObj.$jsNoCentralCollisionDemonstration.addClass('hide_dom');
                        helper.domObj.$jsSpolarDropNumselect.removeClass('nonelastic-content');
                        helper.showType = 'elastic';
                        break;
                    case 'inelastic':
                        /*非弹性*/
                        helper.domObj.$jsCollisionDemonstration.addClass('hide_dom');
                        helper.domObj.$jsNonelasticCollisionDemonstration.removeClass('hide_dom');
                        helper.domObj.$jsNoCentralCollisionDemonstration.addClass('hide_dom');
                        helper.domObj.$jsSpolarDropNumselect.addClass('nonelastic-content');
                        helper.showType = 'inelastic';
                        break;
                    case 'central':
                        /*非对心*/
                        helper.domObj.$jsCollisionDemonstration.addClass('hide_dom');
                        helper.domObj.$jsNonelasticCollisionDemonstration.addClass('hide_dom');
                        helper.domObj.$jsNoCentralCollisionDemonstration.removeClass('hide_dom');
                        helper.showType = 'central';
                        break;
                    case 'reset':
                        /*重置*/
                        if ($this.hasClass('click_disabled')) {
                            return;
                        }
                        helper.domObj.$jsPopBoxResetTips.removeClass('hide_dom');
                        break;
                }
                collisionController.changeScene();
            });
            /*我知道了 按钮*/
            helper.domObj.$jsYesIKnownBtn.on('click', function () {
                helper.domObj.$jsPopBoxElasticEquation.addClass('hide_dom');
                helper.domObj.$jsPopBoxInelasticEquation.addClass('hide_dom');
            });
            /*重置提示框 按钮*/
            helper.domObj.$jsPopBoxResetTipsBtns.on('click', function () {
                var $this = $(this);
                var type = $this.data('type');
                helper.domObj.$jsPopBoxResetTips.addClass('hide_dom');
                $(helper.domObj.$jsCollisionChooseBtn[3]).removeClass('ui_btn_active');
                if (type == 'yes') {
                    collisionController.reset();
                    $(helper.domObj.$jsCollisionChooseBtn[3]).addClass('click_disabled');
                } else if (type == 'no') {
                    $(helper.domObj.$jsCollisionChooseBtn[3]).removeClass('click_disabled');
                }
            });
            // 操作按钮
            helper.domObj.$jsToolBtnsStep.on('click', function () {
                var $this = $(this);
                var type = $this.data('type');
                if ($this.hasClass('click_disabled')) {
                    return;
                }
                var textDom = helper.$view.find('._js_spolar_drop_numselect_active ._js_num_input_text');
                if (textDom) {
                    if (!helper.inputValueChange) {
                        $(textDom).html(helper.lastInputValue);
                    }
                    textDom.parent().removeClass('editor-state');
                }
                helper.inputValueChange = false;
                helper.domObj.$jsSpolarDropNumselect.addClass('hide_dom');
                helper.domObj.$jsSpolarDropNum.removeClass('ui_open');
                helper.domObj.$jsSpolarDropNum.removeClass('_js_spolar_drop_numselect_active');
                $(helper.domObj.$jsSpolarDropNumselectBtn[10]).removeClass('ui_btn_gray'); //负号删除不可点击
                switch (type) {
                    case 'showEquation':
                        /*显示公式*/
                        collisionController.showExpression();
                        break;
                    case 'stepDemonstration':
                        /*分步演示*/
                        collisionController.stepDemonstration();
                        break;
                    case 'startDemonstration':
                        /*开始演示*/
                        collisionController.fullDemonstration();
                        break;
                }
            });
            //绑定键盘
            collisionController.bindKeyBoard();
        },
        _unbindEvent: function _unbindEvent() {}
    }, 'Collision');

    window.AddonCollision_create = function () {
        return new Presenter();
    };
})();
'use strict';

define('BaseCollision', function () {
    var BaseCollision = Class.extend({
        $init: function $init(opt) {
            this.opt = $.extend({}, opt);
            this.helper = this.opt.helper;
            this.resetState = false;
            //1表示分步演示，2表示下一步
            this.stepButtonState = 1;
            //1表示开始演示，2表示重新演示
            this.fullButtonState = 1;
            //是否碰撞的标记
            this.isCollision = false;

            this.$stepButton = null;
            this.$fullButton = null;
        },
        init: function init() {
            this.initResetButton(this.resetState);
            this.unbindEvent();
            this.bindEvent();
        },
        initResetButton: function initResetButton(resetState) {
            this.resetState = !!resetState;
            var $resetButton = $(this.helper.domObj.$jsCollisionChooseBtn[3]);
            this.resetState ? $resetButton.removeClass('click_disabled') : $resetButton.addClass('click_disabled');
        },
        /**
         * 分步演示
         */
        stepDemonstration: function stepDemonstration() {},
        /**
         * 完整演示
         */
        fullDemonstration: function fullDemonstration() {},
        /**
         * 显示需要显示的值
         */
        showValue: function showValue() {
            this.textChange();
        },
        /**
         * 重置
         */
        reset: function reset() {
            this.resetState = false;
            this.init();
        },
        //文字处理
        textChange: function textChange() {},
        //重置操作按钮 文字
        textReset: function textReset() {
            var self = this;
            this.stepButtonState = 1;
            this.fullButtonState = 1;
            this.$stepButton.html(self.helper.i18n.step_demonstration);
            this.$fullButton.html(self.helper.i18n.start_demonstration);
        },
        bindEvent: function bindEvent() {},
        unbindEvent: function unbindEvent() {},
        svgAddClass: function svgAddClass(dom, className) {
            dom.classList.add(className);
        },
        svgRemoveClass: function svgRemoveClass(dom, className) {
            dom.classList.remove(className);
        }
    });
    return BaseCollision;
});
'use strict';

define('BaseElasticInelastocCollision', ['BaseCollision'], function (BaseCollision) {
    var BaseElasticInelastocCollision = BaseCollision.extend({
        $init: function $init(opt) {
            this.opt = $.extend({}, opt);
            this.helper = this.opt.helper;
            this._super(opt);
            this._m1 = 5;
            this._v1 = 0;
            this._m2 = 5;
            this._v2 = 0;
        },
        /**
         * 分步演示
         */
        stepDemonstration: function stepDemonstration() {},
        /**
         * 完整演示
         */
        fullDemonstration: function fullDemonstration() {},
        /**
         * 显示公式
         */
        showExpression: function showExpression() {},
        /**
         * 重置
         */
        reset: function reset() {
            this._super();
        },
        //重置操作按钮 文字
        textReset: function textReset() {
            this._super();
            this.$stepButton.parent().addClass('click_disabled');
            this.$fullButton.parent().addClass('click_disabled');
        },
        //判断小球是否可以碰撞
        speedJudge: function speedJudge() {
            var v1 = parseFloat(this._v1);
            var v2 = parseFloat(this._v2);
            var result = false;
            if (v1 > v2 && v2 >= 0 || //同一向右方向
            v1 > v2 && v1 <= 0 //同一向左方向
            || v1 > 0 && v2 < 0 //v1向右，v2向左
            ) {
                    result = true;
                }
            return result;
        },
        _resetBallPosi: function _resetBallPosi() {}
    });
    return BaseElasticInelastocCollision;
});
'use strict';

/**
 *控制器
 **/
define('CollisionController', ['ElasticCollision', 'InelastocCollision', 'NonCenteringCollision', 'KeyBoard'], function (ElasticCollision, InelastocCollision, NonCenteringCollision, KeyBoard) {
    var CollisionController = Class.extend({
        $init: function $init(opt) {
            this.opt = $.extend({}, opt);
            this.helper = this.opt.helper;

            /*数字键盘*/
            this.keyBoard = new KeyBoard({ helper: this.helper });

            this._scene = {
                elastic: { //弹性
                    collision: new ElasticCollision({ helper: this.helper })
                },
                inelastic: { //非弹性
                    collision: new InelastocCollision({ helper: this.helper })
                },
                central: { //非对心
                    collision: new NonCenteringCollision({ helper: this.helper })
                }
            };
        },
        init: function init() {
            var helper = this.helper;
            helper.currentScene = this._scene[helper.showType];
            this.unbindEvent();
            this.bindEvent();
        },

        changeScene: function changeScene() {
            var helper = this.helper;
            helper.currentScene = this._scene[helper.showType];
            helper.currentScene.collision.init();
        },
        /**
         * 分步演示
         */
        stepDemonstration: function stepDemonstration() {
            var helper = this.helper;
            this._scene[helper.showType].collision.stepDemonstration();
        },
        /**
         * 完整演示
         */
        fullDemonstration: function fullDemonstration() {
            var helper = this.helper;
            this._scene[helper.showType].collision.fullDemonstration();
        },
        /**
         * 显示公式
         */
        showExpression: function showExpression() {
            var self = this;
            if (self.helper.showType == 'elastic') {
                self.helper.domObj.$jsPopBoxElasticEquation.removeClass('hide_dom');
            } else if (self.helper.showType == 'inelastic') {
                self.helper.domObj.$jsPopBoxInelasticEquation.removeClass('hide_dom');
            }
        },
        /**
         * 重置
         */
        reset: function reset() {
            this.helper.currentScene.collision.reset();
        },
        destroy: function destroy() {
            for (var scene in this._scene) {
                this._scene[scene] && this._scene[scene].collision && this._scene[scene].collision.reset();
            }
        },
        //分步演示与下一步 文字处理
        textChange: function textChange() {
            var helper = this.helper;
            this._scene[helper.showType].collision.textChange();
        },
        bindKeyBoard: function bindKeyBoard() {
            this.keyBoard.initView();
        },
        bindEvent: function bindEvent() {},
        unbindEvent: function unbindEvent() {}
    });
    return CollisionController;
});
'use strict';

/**
 *弹性
 **/
define('ElasticCollision', ['BaseElasticInelastocCollision'], function (BaseElasticInelastocCollision) {
    var ElasticCollision = BaseElasticInelastocCollision.extend({
        $init: function $init(opt) {
            this.opt = $.extend({}, opt);
            this.helper = this.opt.helper;
            this._super(opt);

            this._endSpeed1 = null;
            this._endSpeed2 = null;
            this._leftBallDis = 715;
            this._rightBallDis = 1065;
            this.$stepButton = $(this.helper.domObj.$jsToolBtnsStep[1]).children();
            this.$fullButton = $(this.helper.domObj.$jsToolBtnsStep[2]).children();
            this._findView();
        },
        /**
         * 找到动画操作元素
         */
        _findView: function _findView() {
            var self = this;
            self.helper.leftBall = $(self.helper.$view.find('._js_ball_left'));
            self.helper.rightBall = $(self.helper.$view.find('._js_ball_right'));
            this._resetBallPosi();
        },
        /**
         * 分步演示
         */
        stepDemonstration: function stepDemonstration() {
            if (this.stepButtonState === 1) {
                this._getInitValue();
                if (this.speedJudge()) {
                    this._countEndSpeed();
                }
                this.isCollision = false;
                this.stopAnimation();
                this._resetBallPosi();
                this.startAnimation(this._stepDemonstrationCallback);
            } else {
                this.stepButtonState = 1;
                this.textChange();
                this.showValue();
                this._v1 = this._endSpeed1;
                this._v2 = this._endSpeed2;
                this.startAnimation();
            }
        },
        _stepDemonstrationCallback: function _stepDemonstrationCallback() {
            this.stopAnimation();
            this.stepButtonState = this.stepButtonState === 1 ? 2 : 1;
            this.textChange();
        },
        /**
         * 完整演示
         */
        fullDemonstration: function fullDemonstration() {
            this.isCollision = false;
            this._getInitValue();
            if (this.speedJudge()) {
                this._countEndSpeed();
            }
            this.stopAnimation();
            this._resetBallPosi();
            this.fullButtonState = 2;
            this.textChange();
            this.startAnimation(this._fullDemonstration);
        },
        _fullDemonstration: function _fullDemonstration() {
            this._v1 = this._endSpeed1;
            this._v2 = this._endSpeed2;
            this.showValue();
        },
        startAnimation: function startAnimation(callback) {
            var _this = this;

            var helper = this.helper;
            var v1 = this._v1 * helper.pxmRatio;
            var v2 = this._v2 * helper.pxmRatio;

            if (!this.isCollision && helper.diameter < this._rightBallDis - this._leftBallDis && helper.diameter >= this._rightBallDis + v2 - (this._leftBallDis + v1)) {

                var leftBallDis = this._leftBallDis + v1;
                var rightBallDis = this._rightBallDis + v2;
                var diff = helper.diameter - (rightBallDis - leftBallDis);
                if (this._v1 > this._v2 && this._v2 > 0) {
                    //同一向右方向
                    leftBallDis = leftBallDis - diff / 2;
                    rightBallDis = rightBallDis + diff / 2;
                } else if (this._v1 > this._v2 && this._v1 < 0) {
                    //同一向左方向
                    leftBallDis = leftBallDis - diff / 2;
                    rightBallDis = rightBallDis + diff / 2;
                } else if (this._v1 > 0 && this._v2 < 0) {
                    //v1向右，v2向左
                    leftBallDis = leftBallDis - diff / 2;
                    rightBallDis = rightBallDis + diff / 2;
                } else if (this._v1 === 0 && this._v2 !== 0) {
                    //v1不动，v2运动
                    rightBallDis = rightBallDis + diff;
                } else if (this._v1 !== 0 && this._v2 === 0) {
                    //v1运动，v2不动
                    leftBallDis = leftBallDis - diff;
                }

                this._leftBallDis = leftBallDis;
                this._rightBallDis = rightBallDis;
                this.helper.leftBall.attr({
                    transform: 'translate(' + leftBallDis + ' 120)'
                });
                this.helper.rightBall.attr({
                    transform: 'translate(' + rightBallDis + ' 120)'
                });
                this.isCollision = true;
                callback && callback.apply(this);
                if (!this.startAnimationId) {
                    return;
                }
                v1 = v2 = 0;
            }
            this._leftBallDis = this._leftBallDis + v1;
            this._rightBallDis = this._rightBallDis + v2;
            this.helper.leftBall.attr({
                transform: 'translate(' + this._leftBallDis + ' 120)'
            });
            this.helper.rightBall.attr({
                transform: 'translate(' + this._rightBallDis + ' 120)'
            });

            this.startAnimationId = requestAnimationFrame(function () {
                _this.startAnimation(callback);
            });
        },
        stopAnimation: function stopAnimation() {
            this.startAnimationId && cancelAnimationFrame(this.startAnimationId);
            this.startAnimationId = undefined;
        },
        /**
         * 显示公式
         */
        showExpression: function showExpression() {},
        /**
         * 重置
         */
        reset: function reset() {
            var self = this;
            self._super();

            self.isCollision = false;
            this.stopAnimation();

            self._m1 = 5;
            self._v1 = 0;
            self._m2 = 5;
            self._v2 = 0;
            $(self.helper.domObj.$jsNumInputText[0]).html(self._m1);
            $(self.helper.domObj.$jsNumInputText[2]).html(self._m2);
            $(self.helper.domObj.$jsNumInputText[1]).html(self._v1);
            $(self.helper.domObj.$jsNumInputText[3]).html(self._v2);

            self._endSpeed1 = null;
            self._endSpeed2 = null;
            $(self.helper.showNumDom[0]).html('');
            $(self.helper.showNumDom[1]).html('');

            self.textReset();
            self._resetBallPosi();
        },
        showValue: function showValue() {
            this._super();
            // this._getInitValue();
            // this._countEndSpeed();
            $(this.helper.showNumDom[0]).html(this._endSpeed1);
            $(this.helper.showNumDom[1]).html(this._endSpeed2);
        },
        _getInitValue: function _getInitValue() {
            this._m1 = parseFloat($(this.helper.domObj.$jsNumInputText[0]).html());
            this._m2 = parseFloat($(this.helper.domObj.$jsNumInputText[2]).html());
            this._v1 = parseFloat($(this.helper.domObj.$jsNumInputText[1]).html());
            this._v2 = parseFloat($(this.helper.domObj.$jsNumInputText[3]).html());
        },
        /**
         * 计算小球碰撞后的速度
         */
        _countEndSpeed: function _countEndSpeed() {
            var self = this;
            var mass = self._m1 + self._m2;
            self._endSpeed1 = (self._v1 * (self._m1 - self._m2) + 2 * self._m2 * self._v2) / mass;
            self._endSpeed2 = (self._v2 * (self._m2 - self._m1) + 2 * self._m1 * self._v1) / mass;
            var i = 2,
                y = 2;
            if (self._endSpeed1 != 0) {
                while (self._endSpeed1.toFixed(i) == 0) {
                    i++;
                }
            }
            if (self._endSpeed2 != 0) {
                while (self._endSpeed2.toFixed(y) == 0) {
                    y++;
                }
            }
            self._endSpeed1 = self._endSpeed1.toFixed(i);
            self._endSpeed2 = self._endSpeed2.toFixed(y);
        },
        //分步演示与下一步 文字处理
        textChange: function textChange() {
            if (this.stepButtonState === 1) {
                this.$stepButton.html(this.helper.i18n.step_demonstration);
            } else if (this.stepButtonState === 2) {
                this.$stepButton.html(this.helper.i18n.next_step_demonstration);
            }

            if (this.fullButtonState === 1) {
                this.$fullButton.html(this.helper.i18n.start_demonstration);
            } else if (this.fullButtonState === 2) {
                this.$fullButton.html(this.helper.i18n.again_demonstration);
            }
        },
        bindEvent: function bindEvent() {},
        unbindEvent: function unbindEvent() {},
        _resetBallPosi: function _resetBallPosi() {
            var self = this;
            this.stopAnimation();
            this.isCollision = false;
            this.fullButtonState = 1;
            this.stepButtonState = 1;
            this.textChange();
            this._leftBallDis = 715;
            this._rightBallDis = 1065;
            self.helper.leftBall.attr({
                transform: 'translate(' + this._leftBallDis + ' 120)'
            });

            self.helper.rightBall.attr({
                transform: 'translate(' + this._rightBallDis + ' 120)'
            });
        }
    });
    return ElasticCollision;
});
'use strict';

/**
 *非弹性
 **/
define('InelastocCollision', ['BaseElasticInelastocCollision'], function (BaseElasticInelastocCollision) {
    var InelastocCollision = BaseElasticInelastocCollision.extend({
        $init: function $init(opt) {
            this.opt = $.extend({}, opt);
            this.helper = this.opt.helper;
            this._super(opt);
            this._leftBallDis = 715;
            this._rightBallDis = 1065;
            this._endSpeed = null;
            this._changeEnergy = null;

            this._leftBallClass = ['ball-left_00', 'ball-left_01', 'ball-left_02', 'ball-left_03', 'ball-left_04', 'ball-left_05', 'ball-left_06', 'ball-left_07', 'ball-left_08', 'ball-left_09', 'ball-left_10', 'ball-left_11', 'ball-left_12', 'ball-left_13', 'ball-left_14'];
            this._rightBallClass = ['ball-right_00', 'ball-right_01', 'ball-right_02', 'ball-right_03', 'ball-right_04', 'ball-right_05', 'ball-right_06', 'ball-right_07', 'ball-right_08', 'ball-right_09', 'ball-right_10', 'ball-right_11', 'ball-right_12', 'ball-right_13', 'ball-right_14'];

            this._v1Diff = 0;
            this._v2Diff = 0;
            this._leftBallFrameNum = 0;
            this._rightBallFrameNum = 0;
            this._preloadBallImages();

            this.$stepButton = $(this.helper.domObj.$jsToolBtnsStep[4]).children();
            this.$fullButton = $(this.helper.domObj.$jsToolBtnsStep[5]).children();
            this._findView();
        },
        /**
         * 找到动画操作元素
         */
        _findView: function _findView() {
            this.helper.leftBallNonelastic = $(this.helper.$view.find('._js_nonelastic_ball_left'));
            this.helper.rightBallNonelastic = $(this.helper.$view.find('._js_nonelastic_ball_right'));
            /*粘在一起文字dom*/
            this.helper.textNonelastic = $(this.helper.$view.find('._js_nonelastic_collision-time-text'));

            this.helper.leftBallNonelastic.attr({
                transform: 'translate(715 120)'
            });

            this.helper.rightBallNonelastic.attr({
                transform: 'translate(1065 120)'
            });
        },
        /**
         * 分步演示
         */
        stepDemonstration: function stepDemonstration() {
            if (this.stepButtonState === 1) {
                this._getInitValue();
                if (this.speedJudge()) {
                    this._countEndSpeed();
                    this._countChangeEnergy();
                }
                this.isCollision = false;
                this.stopAnimation();
                this._resetBallPosi();
                this.startAnimation(this._stepDemonstrationCallback);
            } else {
                this.stepButtonState = 1;
                this.textChange();
                this.showValue();
                this._v1 = this._v2 = this._endSpeed;
                this.startAnimation();
            }
        },
        _stepDemonstrationCallback: function _stepDemonstrationCallback() {
            this.stopAnimation();

            // this.svgAddClass(this.helper.leftBallNonelastic[0], 'on');
            // this.svgAddClass(this.helper.rightBallNonelastic[0], 'on');
            // this.svgAddClass(this.helper.leftBallNonelastic[0], 'collision-time');
            // this.svgAddClass(this.helper.rightBallNonelastic[0], 'collision-time');
            // this.stepAnimationCollision && clearTimeout(this.stepAnimationCollision);
            // this.stepAnimationCollision = setTimeout(() => {
            //     this.svgRemoveClass(this.helper.leftBallNonelastic[0], 'on');
            //     this.svgRemoveClass(this.helper.rightBallNonelastic[0], 'on');
            // }, 100);

            this.svgRemoveClass(this.helper.textNonelastic[0], 'hide_dom');
            var textWidth = this.helper.textNonelastic[0].getBoundingClientRect().width / 2;
            this.helper.textNonelastic.attr({
                transform: 'translate(' + (960 - textWidth) + ' 60)'
            });
            this.stepButtonState = this.stepButtonState === 1 ? 2 : 1;
            this.textChange();
        },
        /**
         * 完整演示
         */
        fullDemonstration: function fullDemonstration() {
            this.isCollision = false;
            this._getInitValue();
            if (this.speedJudge()) {
                this._countEndSpeed();
                this._countChangeEnergy();
            }

            this.stopAnimation();
            this._resetBallPosi();
            this.fullButtonState = 2;
            this.textChange();
            this.startAnimation(this._fullDemonstration);
        },
        _fullDemonstration: function _fullDemonstration() {
            this._v1 = this._endSpeed;
            this._v2 = this._endSpeed;

            // this.svgAddClass(this.helper.leftBallNonelastic[0], 'on');
            // this.svgAddClass(this.helper.rightBallNonelastic[0], 'on');
            // this.svgAddClass(this.helper.leftBallNonelastic[0], 'collision-time');
            // this.svgAddClass(this.helper.rightBallNonelastic[0], 'collision-time');
            // this.fullAnimationCollision && clearTimeout(this.fullAnimationCollision);
            // this.fullAnimationCollision = setTimeout(() => {
            //     this.svgRemoveClass(this.helper.leftBallNonelastic[0], 'on');
            //     this.svgRemoveClass(this.helper.rightBallNonelastic[0], 'on');
            //
            // }, 100);

            this.svgRemoveClass(this.helper.textNonelastic[0], 'hide_dom');
            var textWidth = this.helper.textNonelastic[0].getBoundingClientRect().width / 2;
            this.helper.textNonelastic.attr({
                transform: 'translate(' + (960 - textWidth) + ' 60)'
            });

            this.showValue();
        },
        startAnimation: function startAnimation(callback) {
            var _this = this;

            var helper = this.helper;
            var v1 = this._v1 * helper.pxmRatio;
            var v2 = this._v2 * helper.pxmRatio;

            if (!this.isCollision && helper.diameter + 10 < this._rightBallDis - this._leftBallDis && helper.diameter + 10 >= this._rightBallDis + v2 - (this._leftBallDis + v1)) {
                var leftBallDis = this._leftBallDis + v1;
                var rightBallDis = this._rightBallDis + v2;
                var diff = helper.diameter + 10 - (rightBallDis - leftBallDis);
                if (this._v1 > this._v2 && this._v2 > 0) {
                    //同一向右方向
                    leftBallDis = leftBallDis - diff / 2;
                    rightBallDis = rightBallDis + diff / 2;
                } else if (this._v1 > this._v2 && this._v1 < 0) {
                    //同一向左方向
                    leftBallDis = leftBallDis - diff / 2;
                    rightBallDis = rightBallDis + diff / 2;
                } else if (this._v1 > 0 && this._v2 < 0) {
                    //v1向右，v2向左
                    leftBallDis = leftBallDis - diff / 2;
                    rightBallDis = rightBallDis + diff / 2;
                } else if (this._v1 === 0 && this._v2 !== 0) {
                    //v1不动，v2运动
                    rightBallDis = rightBallDis + diff;
                } else if (this._v1 !== 0 && this._v2 === 0) {
                    //v1运动，v2不动
                    leftBallDis = leftBallDis - diff;
                }

                this._leftBallDis = leftBallDis;
                this._rightBallDis = rightBallDis;
                this.helper.leftBallNonelastic.attr({
                    transform: 'translate(' + leftBallDis + ' 120)'
                });
                this.helper.rightBallNonelastic.attr({
                    transform: 'translate(' + rightBallDis + ' 120)'
                });
                this.isCollision = true;
                this._v1Diff = 0;
                this._v2Diff = 0;
                this._leftBallFrameNum = 0;
                this._rightBallFrameNum = 0;

                v1 = v2 = 0;
            }
            if (this.isCollision && this._leftBallFrameNum < 14 && this._rightBallFrameNum < 14) {
                this._v1Diff = this._v1Diff + v1;
                this._v2Diff = this._v2Diff + v2;

                var oldLeftBallFrameNum = this._leftBallFrameNum;
                var oldRightBallFrameNum = this._rightBallFrameNum;
                var leftNum = Math.floor(this._leftBallFrameNum + Math.abs(this._v1Diff));
                if (leftNum > this._leftBallFrameNum) {
                    this._leftBallFrameNum = leftNum;
                }
                var rightNum = Math.floor(this._rightBallFrameNum + Math.abs(this._v2Diff));
                if (rightNum > this._rightBallFrameNum) {
                    this._rightBallFrameNum = rightNum;
                }
                this._v1Diff = Math.abs(this._v1Diff) > 1 ? 0 : this._v1Diff;
                this._v2Diff = Math.abs(this._v2Diff) > 1 ? 0 : this._v2Diff;

                if (this._leftBallFrameNum > this._rightBallFrameNum) {
                    this._rightBallFrameNum = this._leftBallFrameNum;
                } else {
                    this._leftBallFrameNum = this._rightBallFrameNum;
                }

                if (this._leftBallFrameNum > oldLeftBallFrameNum) {
                    this._leftBallDis = this._leftBallDis - (this._leftBallFrameNum - oldLeftBallFrameNum > 14 ? 14 : this._leftBallFrameNum - oldLeftBallFrameNum);
                    // console.log('left',(this._leftBallFrameNum - oldLeftBallFrameNum > 14 ? 14 : this._leftBallFrameNum - oldLeftBallFrameNum))
                    // console.log(this._leftBallFrameNum);
                    this._leftBallDis = this._leftBallDis + this._v1 * helper.pxmRatio + 0.2;
                }
                if (this._rightBallFrameNum > oldRightBallFrameNum) {
                    this._rightBallDis = this._rightBallDis + (this._rightBallFrameNum - oldRightBallFrameNum > 14 ? 14 : this._rightBallFrameNum - oldRightBallFrameNum);
                    // console.log('right',(this._rightBallFrameNum - oldRightBallFrameNum > 14 ? 14 : this._rightBallFrameNum - oldRightBallFrameNum))
                    this._rightBallDis = this._rightBallDis + this._v2 * helper.pxmRatio + 0.2;
                }

                var _diff = 145 - (this._rightBallDis - this._leftBallDis);
                this._leftBallDis = this._leftBallDis - _diff * (Math.abs(this._v1) / (Math.abs(this._v1) + Math.abs(this._v2)));
                this._rightBallDis = this._rightBallDis + _diff * (Math.abs(this._v2) / (Math.abs(this._v1) + Math.abs(this._v2)));
                console.log(this._rightBallFrameNum);

                if (this._leftBallFrameNum >= 14 || this._rightBallFrameNum >= 14) {
                    this._leftBallFrameNum = 14;
                    this._rightBallFrameNum = 14;
                }

                for (var i = 0, iLen = this._leftBallClass.length; i < iLen; i++) {
                    this.svgRemoveClass(this.helper.leftBallNonelastic[0], this._leftBallClass[i]);
                }
                for (var _i = 0, _iLen = this._rightBallClass.length; _i < _iLen; _i++) {
                    this.svgRemoveClass(this.helper.rightBallNonelastic[0], this._rightBallClass[_i]);
                }
                this.svgAddClass(this.helper.leftBallNonelastic[0], 'ball-left_' + this._getInt2Num(this._leftBallFrameNum));
                this.svgAddClass(this.helper.rightBallNonelastic[0], 'ball-right_' + this._getInt2Num(this._rightBallFrameNum));

                // console.log(this._v1 * helper.pxmRatio, this._v2 * helper.pxmRatio);


                if (this._leftBallFrameNum === 14 || this._rightBallFrameNum === 14) {
                    console.log('最后');
                    //XXX 有小球静止时，需要处理处理，不然会有1-2个像素偏差
                    if (this._v1 === 0) {
                        this.helper.leftBallNonelastic.attr({
                            transform: 'translate(' + this._leftBallDis + ' 120)'
                        });
                        this.helper.rightBallNonelastic.attr({
                            transform: 'translate(' + this._rightBallDis + ' 120)'
                        });
                    } else if (this._v2 === 0) {
                        this.helper.leftBallNonelastic.attr({
                            transform: 'translate(' + this._leftBallDis + ' 120)'
                        });
                        this.helper.rightBallNonelastic.attr({
                            transform: 'translate(' + this._rightBallDis + ' 120)'
                        });
                    } else {
                        this.helper.leftBallNonelastic.attr({
                            transform: 'translate(' + this._leftBallDis + ' 120)'
                        });
                        this.helper.rightBallNonelastic.attr({
                            transform: 'translate(' + this._rightBallDis + ' 120)'
                        });
                    }

                    callback && callback.apply(this);
                    if (!this.startAnimationId) {
                        return;
                    }
                } else {
                    this.helper.leftBallNonelastic.attr({
                        transform: 'translate(' + this._leftBallDis + ' 120)'
                    });
                    this.helper.rightBallNonelastic.attr({
                        transform: 'translate(' + this._rightBallDis + ' 120)'
                    });
                }
            } else {
                this._leftBallDis = this._leftBallDis + v1;
                this._rightBallDis = this._rightBallDis + v2;
                this.helper.leftBallNonelastic.attr({
                    transform: 'translate(' + this._leftBallDis + ' 120)'
                });
                this.helper.rightBallNonelastic.attr({
                    transform: 'translate(' + this._rightBallDis + ' 120)'
                });
            }

            this.startAnimationId = requestAnimationFrame(function () {
                _this.startAnimation(callback);
            });
        },
        stopAnimation: function stopAnimation() {
            this.startAnimationId && cancelAnimationFrame(this.startAnimationId);
            this.startAnimationId = undefined;
        },
        /**
         * 显示公式
         */
        showExpression: function showExpression() {},
        /**
         * 重置
         */
        reset: function reset() {
            this._super();
            var self = this;
            self._m1 = 5;
            self._v1 = 0;
            self._m2 = 5;
            self._v2 = 0;
            $(self.helper.domObj.$jsNumInputText[4]).html(self._m1);
            $(self.helper.domObj.$jsNumInputText[6]).html(self._m2);
            $(self.helper.domObj.$jsNumInputText[5]).html(self._v1);
            $(self.helper.domObj.$jsNumInputText[7]).html(self._v2);

            self._endSpeed = null;
            self._changeEnergy = null;
            $(self.helper.showNumDom[2]).html('');
            $(self.helper.showNumDom[3]).html('');

            self.textReset();
            self._resetBallPosi();
        },
        showValue: function showValue() {
            this._super();

            $(this.helper.showNumDom[2]).html(this._endSpeed);
            $(this.helper.showNumDom[3]).html(this._changeEnergy);
        },
        _getInt2Num: function _getInt2Num(num) {
            var val = '00';
            if (num < 10) {
                val = '0' + num;
            } else {
                val = num;
            }
            return val;
        },
        _preloadBallImages: function _preloadBallImages() {
            this._leftBalls = [];
            this._rightBalls = [];
            for (var i = 0; i < 15; i++) {
                this._leftBalls[i] = new Image();
                this._leftBalls[i].src = this.helper.basePath + 'resources/wood/images/ball_ani_frame/collision_green_ball_' + this._getInt2Num(i) + '.png';
            }
            for (var i = 0; i < 15; i++) {
                this._rightBalls[i] = new Image();
                this._rightBalls[i].src = this.helper.basePath + 'resources/wood/images/ball_ani_frame/collision_purple_ball_' + this._getInt2Num(i) + '.png';
            }
        },
        _getInitValue: function _getInitValue() {
            this._m1 = parseFloat($(this.helper.domObj.$jsNumInputText[4]).html());
            this._m2 = parseFloat($(this.helper.domObj.$jsNumInputText[6]).html());
            this._v1 = parseFloat($(this.helper.domObj.$jsNumInputText[5]).html());
            this._v2 = parseFloat($(this.helper.domObj.$jsNumInputText[7]).html());
        },
        /**
         * 计算小球碰撞后的速度
         */
        _countEndSpeed: function _countEndSpeed() {
            var mass = this._m1 + this._m2;
            this._endSpeed = (this._v1 * this._m1 + this._m2 * this._v2) / mass;
            var i = 2;
            if (this._endSpeed != 0) {
                while (this._endSpeed.toFixed(i) == 0) {
                    i++;
                }
            }
            this._endSpeed = this._endSpeed.toFixed(i);
        },
        /**
         * 计算动能变化
         */
        _countChangeEnergy: function _countChangeEnergy() {
            var self = this;
            var mass = self._m1 + self._m2;
            var k = 1 / 2;
            self._changeEnergy = k * self._m1 * Math.pow(self._v1, 2) + k * self._m2 * Math.pow(self._v2, 2) - k * mass * Math.pow(self._endSpeed, 2);
            var i = 2;
            if (self._changeEnergy != 0) {
                while (self._changeEnergy.toFixed(i) == 0) {
                    i++;
                }
            }
            self._changeEnergy = self._changeEnergy.toFixed(i);
        },
        //分步演示与下一步 文字处理
        textChange: function textChange() {
            if (this.stepButtonState == 1) {
                this.$stepButton.html(this.helper.i18n.step_demonstration);
            } else if (this.stepButtonState == 2) {
                this.$stepButton.html(this.helper.i18n.next_step_demonstration);
            }

            if (this.fullButtonState === 1) {
                this.$fullButton.html(this.helper.i18n.start_demonstration);
            } else if (this.fullButtonState === 2) {
                this.$fullButton.html(this.helper.i18n.again_demonstration);
            }
        },
        bindEvent: function bindEvent() {},
        unbindEvent: function unbindEvent() {},
        _resetBallPosi: function _resetBallPosi() {
            this.stopAnimation();
            this.isCollision = false;
            this.fullButtonState = 1;
            this.stepButtonState = 1;
            this.textChange();
            this._leftBallDis = 715;
            this._rightBallDis = 1065;

            for (var i = 0, iLen = this._leftBallClass.length; i < iLen; i++) {
                this.svgRemoveClass(this.helper.leftBallNonelastic[0], this._leftBallClass[i]);
            }
            for (var _i2 = 0, _iLen2 = this._rightBallClass.length; _i2 < _iLen2; _i2++) {
                this.svgRemoveClass(this.helper.rightBallNonelastic[0], this._rightBallClass[_i2]);
            }
            this.svgAddClass(this.helper.leftBallNonelastic[0], 'ball-left_00');
            this.svgAddClass(this.helper.rightBallNonelastic[0], 'ball-right_00');

            //粘在一起文字隐藏
            this.svgAddClass(this.helper.textNonelastic[0], 'hide_dom');

            this.helper.leftBallNonelastic.attr({
                transform: 'translate(' + this._leftBallDis + ' 120)'
            });

            this.helper.rightBallNonelastic.attr({
                transform: 'translate(' + this._rightBallDis + ' 120)'
            });
        }
    });
    return InelastocCollision;
});
'use strict';

/**
 * 绑定数字键盘
 **/
define('KeyBoard', function () {
    var KeyBoard = Class.extend({
        $init: function $init(opt) {
            this.opt = $.extend({}, opt);
            this.helper = this.opt.helper;
            this.helper.inputValue = ''; //键盘现在输入值
            this.helper.lastInputValue = ''; //键盘上次输入值
            this.helper.pointBtnClick = true; //键盘小数点是否已点击
            this.helper.inputValueChange = false; //键盘是否确认输入这次值
            this.helper.inputType = ' '; //是质量数值还是速度数值
        },
        initView: function initView() {
            var self = this;

            self.helper.domObj.$jsPopBoxNumberRange = self.helper.$view.find('._js_pop-box_number_range');
            /*输入数值范围警告框*/
            self.helper.domObj.$jsNumberRange = self.helper.$view.find('._js_number_range');
            /*输入数值范围文本*/

            //数值输入框
            self.helper.domObj.$jsSpolarDropNum = self.helper.$view.find('._js_spolar_drop_num');
            //数值键盘
            self.helper.domObj.$jsSpolarDropNumselect = self.helper.$view.find('._js_spolar_drop_numselect');
            //键盘小按钮
            self.helper.domObj.$jsSpolarDropNumselectBtn = self.helper.$view.find('._js_spolar_drop_numselect a');

            //输入的数值
            self.helper.domObj.$jsNumInputText = self.helper.$view.find('._js_num_input_text');
            //计算出的数值
            self.helper.showNumDom = self.helper.$view.find('._js_spolar_num_show_text');

            self._bindEvent();
        },
        _bindEvent: function _bindEvent() {
            var self = this;
            //数值输入框
            self.helper.domObj.$jsSpolarDropNum.on('click', function (e) {
                self.helper.domObj.$jsSpolarDropNumselect.removeClass('hide_dom');
                var $this = $(this);
                if (!self.helper.inputValueChange) {
                    //当前键盘未关闭直接跳转另一键盘
                    var _textDom = self.helper.$view.find('._js_spolar_drop_numselect_active ._js_num_input_text');
                    if (_textDom) {
                        $(_textDom).html(self.helper.lastInputValue);
                        _textDom.parent().removeClass('editor-state');
                    }
                    self.helper.inputValueChange = false;
                }
                self.helper.domObj.$jsSpolarDropNum.removeClass('ui_open').removeClass('_js_spolar_drop_numselect_active');
                $this.addClass('ui_open').addClass('_js_spolar_drop_numselect_active');
                $(self.helper.domObj.$jsSpolarDropNumselectBtn[10]).removeClass('ui_btn_gray'); //负号删除不可点击
                self.helper.inputType = $this.data('type');
                var posi = $this.data('posi');
                if (self.helper.inputType == 'speed') {
                    self.helper.domObj.$jsSpolarDropNumselect.addClass('speed_keyboard');
                } else if (self.helper.inputType == 'mass') {
                    self.helper.domObj.$jsSpolarDropNumselect.removeClass('speed_keyboard');
                }
                if (posi == 'right') {
                    self.helper.domObj.$jsSpolarDropNumselect.addClass('right_keybooad');
                } else if (posi == 'left') {
                    self.helper.domObj.$jsSpolarDropNumselect.removeClass('right_keybooad');
                }
                self.helper.inputValue = '';
                self.helper.domObj.$jsSpolarDropNumselectBtn.removeClass('ui_btn_active');
                self.helper.pointBtnClick = true;
                var textDom = $this.find('._js_num_input_text');
                self.helper.lastInputValue = textDom.html();
                e.stopPropagation();
            });
            //键盘输入
            self.helper.domObj.$jsSpolarDropNumselect.on('click', function (e) {
                e.stopPropagation();
            });
            self.helper.domObj.$jsSpolarDropNumselectBtn.on('click', function (e) {
                var $this = $(this);
                var value = $this.data('value');
                $this.addClass('ui_btn_active').siblings().removeClass('ui_btn_active');
                var textDom = self.helper.$view.find('._js_spolar_drop_numselect_active ._js_num_input_text');
                textDom.parent().addClass('editor-state');
                var textlength = self.helper.inputValue.length;
                switch (value) {
                    case 0:
                        if (textlength === 6) {
                            return;
                        }
                        if (self.helper.inputValue == '0') {
                            self.helper.inputValue = '0';
                        } else if (self.helper.inputValue == '-0') {
                            self.helper.inputValue = '-0';
                        } else {
                            self.helper.inputValue += '0';
                        }
                        $(self.helper.domObj.$jsSpolarDropNumselectBtn[10]).addClass('ui_btn_gray');
                        break;
                    case 1:
                    case 2:
                    case 3:
                    case 4:
                    case 5:
                    case 6:
                    case 7:
                    case 8:
                    case 9:
                        if (textlength === 6) {
                            return;
                        }
                        if (self.helper.inputValue == '0') {
                            self.helper.inputValue = value;
                        } else if (self.helper.inputValue == '-0') {
                            self.helper.inputValue = '-' + value;
                        } else {
                            self.helper.inputValue = self.helper.inputValue + '' + value;
                        }
                        $(self.helper.domObj.$jsSpolarDropNumselectBtn[10]).addClass('ui_btn_gray');
                        break;
                    case 'point':
                        if (textlength === 6) {
                            return;
                        }
                        if (self.helper.pointBtnClick) {
                            if (self.helper.inputValue == '') {
                                self.helper.inputValue = '0.';
                            } else if (self.helper.inputValue == '-') {
                                self.helper.inputValue = '-0.';
                            } else {
                                self.helper.inputValue += '.';
                            }
                            $(self.helper.domObj.$jsSpolarDropNumselectBtn[10]).addClass('ui_btn_gray');
                            self.helper.pointBtnClick = false;
                        }
                        break;
                    case 'none':
                        if (textlength == 1) {
                            $(self.helper.domObj.$jsSpolarDropNumselectBtn[10]).removeClass('ui_btn_gray');
                        }
                        if (textlength !== 0) {
                            var strArr = self.helper.inputValue.split("");
                            if (strArr[strArr.length - 1] === ".") {
                                self.helper.pointBtnClick = true;
                            }
                            self.helper.inputValue = self.helper.inputValue.substring(0, self.helper.inputValue.length - 1);
                        }
                        break;
                    case 'nagitive':
                        if (textlength === 6) {
                            return;
                        }
                        if ($this.hasClass('ui_btn_gray')) {
                            return;
                        }
                        self.helper.inputValue = '-';
                        break;
                    case 'finish':
                        $this.removeClass('ui_btn_active');
                        self.helper.domObj.$jsSpolarDropNumselect.addClass('hide_dom');
                        self.helper.domObj.$jsSpolarDropNum.removeClass('ui_open');
                        textDom.parent().removeClass('editor-state');
                        self.helper.inputValueChange = true;
                        $(self.helper.domObj.$jsSpolarDropNumselectBtn[10]).removeClass('ui_btn_gray'); //负号删除不可点击
                        if (self.helper.inputValue === '-0' || self.helper.inputValue === '-0.0') {
                            self.helper.inputValue = '0';
                        }
                        if (self.helper.inputValue !== '') {
                            self.helper.inputValue = Number(self.helper.inputValue).toString();
                        }
                        if (self.helper.inputValue !== '') {
                            //有输入时，才做判断
                            //判断m1,m2,v1,v2的数值是否合适
                            self.helper.fit = self._judgeNumRange();
                            //判断v1和v2 确认分布演示按钮和开始演示按钮是否可以点击
                            if (self.helper.fit) {
                                self._btnState();
                            }
                            //重置按钮变为可点击
                            $(self.helper.domObj.$jsCollisionChooseBtn[3]).removeClass('click_disabled');
                        } else {
                            $(textDom).html(self.helper.lastInputValue);
                        }
                        break;
                }
                if (value == 'finish') {
                    //按enter健时，判断是否合理，如不合理，恢复为上次数据
                    if (self.helper.fit) {
                        self.helper.currentScene.collision.resetState = true;
                        if (self.helper.inputValue !== '') {
                            var _strArr = self.helper.inputValue.split("");
                            if (_strArr[_strArr.length - 1] === ".") {
                                self.helper.inputValue = self.helper.inputValue.substring(0, self.helper.inputValue.length - 1);
                            }
                            $(textDom).html(self.helper.inputValue);

                            //每次确认修改参数时，重置上次计算出的数值为空
                            self._resetCountNum();
                        }
                    } else {
                        $(textDom).html(self.helper.lastInputValue);
                    }
                    if (self.helper.inputValue !== '') {
                        self.helper.currentScene.collision._resetBallPosi();
                    }
                } else {
                    self.helper.inputValue = self._toFixed1(self.helper.inputValue).toString();
                    $(textDom).html(self.helper.inputValue);
                    //每次重新开始输入时，将enter健是否点击过的值恢复为 false
                    self.helper.inputValueChange = false;
                }
                e.stopPropagation();
            });
            //关闭键盘
            self.helper.$view.on('click', function () {
                var textDom = self.helper.$view.find('._js_spolar_drop_numselect_active ._js_num_input_text');
                if (textDom) {
                    if (!self.helper.inputValueChange) {
                        $(textDom).html(self.helper.lastInputValue);
                    }
                    textDom.parent().removeClass('editor-state');
                }
                self.helper.inputValueChange = false;
                self.helper.domObj.$jsSpolarDropNumselect.addClass('hide_dom');
                self.helper.domObj.$jsSpolarDropNum.removeClass('ui_open');
                self.helper.domObj.$jsSpolarDropNum.removeClass('_js_spolar_drop_numselect_active');
                $(self.helper.domObj.$jsSpolarDropNumselectBtn[10]).removeClass('ui_btn_gray'); //负号删除不可点击
            });
        },
        //每次确认修改参数时，重置上次计算出的数值为空
        _resetCountNum: function _resetCountNum() {
            var self = this;
            if (self.helper.showType === 'elastic') {
                $(self.helper.showNumDom[0]).html('');
                $(self.helper.showNumDom[1]).html('');
            } else if (self.helper.showType === 'inelastic') {
                $(self.helper.showNumDom[2]).html('');
                $(self.helper.showNumDom[3]).html('');
            }
        },
        //小数点保留一位
        _toFixed1: function _toFixed1(num) {
            num = '' + num;
            if (/\./.test(num)) {
                return num.substring(0, num.indexOf('.') + 2);
            } else {
                return num;
            }
        },
        //判断数值范围是否合适
        _judgeNumRange: function _judgeNumRange() {
            var self = this;
            var fit = false;
            if (self.helper.inputType === 'mass') {
                var m1 = void 0,
                    m2 = void 0;
                if (self.helper.showType === 'elastic') {
                    m1 = Math.abs(parseFloat($(self.helper.domObj.$jsNumInputText[0]).html()));
                    m2 = Math.abs(parseFloat($(self.helper.domObj.$jsNumInputText[2]).html()));
                } else if (self.helper.showType === 'inelastic') {
                    m1 = Math.abs(parseFloat($(self.helper.domObj.$jsNumInputText[4]).html()));
                    m2 = Math.abs(parseFloat($(self.helper.domObj.$jsNumInputText[6]).html()));
                }
                if (m1 <= 100.0 && m1 >= 0.1 && m2 <= 100.0 && m2 >= 0.1) {
                    fit = true;
                }
            } else if (self.helper.inputType === 'speed') {
                var v1 = void 0,
                    v2 = void 0;
                if (self.helper.showType === 'elastic') {
                    v1 = Math.abs(parseFloat($(self.helper.domObj.$jsNumInputText[1]).html()));
                    v2 = Math.abs(parseFloat($(self.helper.domObj.$jsNumInputText[3]).html()));
                } else if (self.helper.showType === 'inelastic') {
                    v1 = Math.abs(parseFloat($(self.helper.domObj.$jsNumInputText[5]).html()));
                    v2 = Math.abs(parseFloat($(self.helper.domObj.$jsNumInputText[7]).html()));
                }
                if (v1 <= 100.0 && v2 <= 100.0) {
                    fit = true;
                }
            }
            if (!fit) {
                if (self.helper.inputType == 'mass') {
                    self.helper.domObj.$jsNumberRange.html('[0.1, 100.0]');
                } else if (self.helper.inputType == 'speed') {
                    self.helper.domObj.$jsNumberRange.html('[-100.0, 100.0]');
                }
                self.helper.domObj.$jsPopBoxNumberRange.removeClass('hide_dom');
                //3秒后自动关闭
                setTimeout(function () {
                    self.helper.domObj.$jsPopBoxNumberRange.addClass('hide_dom');
                }, 3000);
            }
            return fit;
        },
        //根据设置的小球的v1和v2的速度确定分布演示和开始演示是否可以点击
        _btnState: function _btnState() {
            var self = this;
            if (self.helper.showType == 'elastic') {
                var v1 = $(self.helper.domObj.$jsNumInputText[1]).html();
                var v2 = $(self.helper.domObj.$jsNumInputText[3]).html();
                var result = self._speedJudge(v1, v2);
                if (result) {
                    $(self.helper.domObj.$jsToolBtnsStep[1]).removeClass('click_disabled');
                    $(self.helper.domObj.$jsToolBtnsStep[2]).removeClass('click_disabled');
                } else {
                    $(self.helper.domObj.$jsToolBtnsStep[1]).addClass('click_disabled');
                    $(self.helper.domObj.$jsToolBtnsStep[2]).addClass('click_disabled');
                }
            } else if (self.helper.showType == 'inelastic') {
                var _v = $(self.helper.domObj.$jsNumInputText[5]).html();
                var _v2 = $(self.helper.domObj.$jsNumInputText[7]).html();
                var _result = self._speedJudge(_v, _v2);
                if (_result) {
                    $(self.helper.domObj.$jsToolBtnsStep[4]).removeClass('click_disabled');
                    $(self.helper.domObj.$jsToolBtnsStep[5]).removeClass('click_disabled');
                } else {
                    $(self.helper.domObj.$jsToolBtnsStep[4]).addClass('click_disabled');
                    $(self.helper.domObj.$jsToolBtnsStep[5]).addClass('click_disabled');
                }
            }
        },
        //小球速度不都为0
        _speedJudge: function _speedJudge(v1, v2) {
            var v1 = parseFloat(v1);
            var v2 = parseFloat(v2);
            var result = false;
            if (v1 !== 0 || v2 !== 0) {
                result = true;
            }
            return result;
        },
        /**
         * 重置
         */
        reset: function reset() {}
    });
    return KeyBoard;
});
'use strict';

/**
 *非对心
 **/
define('NonCenteringCollision', ['BaseCollision'], function (BaseCollision) {
    var NonCenteringCollision = BaseCollision.extend({
        $init: function $init(opt) {
            this.opt = $.extend({}, opt);
            this.helper = this.opt.helper;
            this._super(opt);

            this.$stepButton = $(this.helper.domObj.$jsToolBtnsStep[6]).children();
            this.$fullButton = $(this.helper.domObj.$jsToolBtnsStep[7]).children();

            this.currentAnimation;
            this._findView();
            this.aniFrame = 1;
        },
        /**
         * 找到动画操作元素
         */
        _findView: function _findView() {
            var self = this;

            self.helper.centralBallLeft = $(self.helper.$view.find('._js_central_ball_left'));
            self.helper.centralBallRight = $(self.helper.$view.find('._js_central_ball_right'));

            self.helper.noCentralBallLeft = $(self.helper.$view.find('._js_noCentral_ball_left'));
            self.helper.noCentralBallRight = $(self.helper.$view.find('._js_noCentral_ball_right'));

            self._resetBallPosi();

            self.helper.modifyingElements = self.helper.$view.find('._js_modifyingElements');
        },
        /**
         * 分步演示
         */
        stepDemonstration: function stepDemonstration() {
            this._aniStop();
            //this.stepButtonState = this.stepButtonState === 1 ? 2 : 1;
            this.fullButtonState = 1;
            this.stepButtonState === 1 && (this.isCollision = false);
            this.stepButtonState === 1 && this._resetBallPosi();
            this.stepButtonState === 1 && (this.aniFrame = 1);
            this.stepButtonState === 2 && (this.stepButtonState = 1);
            this.textChange();
            this._startAnimation('step');
        },
        /**
         * 完整演示
         */
        fullDemonstration: function fullDemonstration() {
            var self = this;
            self._aniStop();
            this.stepButtonState = 1;
            this.fullButtonState = 2;
            this.textChange();
            self.aniFrame = 1;
            self._startAnimation('full');
            self.fullDemonstrationMina && clearTimeout(self.fullDemonstrationMina);
            self.fullDemonstrationMina = setTimeout(function () {
                self.aniFrame = 2;
                self._startAnimation('full');
            }, 550);
        },
        _startAnimation: function _startAnimation(animationName) {
            var self = this;
            self.currentAnimation = animationName;
            this.initResetButton(true);
            var now = mina.time();
            var ani = {
                frame01: function frame01() {
                    self._resetBallPosi();
                    // self.textReset();

                    //对心碰撞
                    self.leftBallMoveMina && self.leftBallMoveMina.stop();
                    self.leftBallMoveMina = mina(715, 930, now, now + 500, mina.time, function (val) {
                        console.log(val);
                        console.log(self.leftBallMoveMina.status());
                        self.helper.centralBallLeft.attr({
                            transform: 'translate(' + val + ' 120)'
                        });
                        if (val === 930 && !self.isCollision && animationName === 'step') {
                            // self.isCollision = true;
                            // self.stepButtonState = 2;
                            // self.textChange();
                        }
                    });
                    eve.once('mina.finish.' + self.leftBallMoveMina.id, function () {
                        if (!self.isCollision && animationName === 'step') {
                            console.log('动画结束');
                            self.isCollision = true;
                            self.stepButtonState = 2;
                            self.textChange();
                        }
                    });

                    //非对心碰撞
                    self.leftNoCentralBallMoveMina01 && self.leftNoCentralBallMoveMina01.stop();
                    self.leftNoCentralBallMoveMina01 = mina([715, 120], [948, 186], now, now + 500, mina.time, function (val) {
                        self.helper.noCentralBallLeft.attr({
                            transform: 'translate(' + val + ')'
                        });
                    });

                    //修饰元素
                    self.helper.modifyingElements.show().fadeOut(500);
                },
                frame02: function frame02() {
                    //对心碰撞
                    self.rightBallMoveMina && self.rightBallMoveMina.stop();
                    self.helper.centralBallLeft.attr({
                        transform: 'translate(930 120)'
                    });
                    self.rightBallMoveMina = mina(1065, 2013, now, now + 2000, mina.time, function (val) {
                        self.helper.centralBallRight.attr({
                            transform: 'translate(' + val + ' 120)'
                        });
                    });

                    //非对心碰撞
                    self.leftNoCentralBallMoveMina02 && self.leftNoCentralBallMoveMina02.stop();
                    self.leftNoCentralBallMoveMina02 = mina([944, 188], [1100, 400], now, now + 500, mina.time, function (val) {
                        self.helper.noCentralBallLeft.attr({
                            transform: 'translate(' + val + ')'
                        });
                    });
                    self.rightNoCentralBallMoveMina && self.rightNoCentralBallMoveMina.stop();
                    self.rightNoCentralBallMoveMina = mina([1065, 120], [1300, -200], now, now + 500, mina.time, function (val) {
                        self.helper.noCentralBallRight.attr({
                            transform: 'translate(' + val + ')'
                        });
                    });
                }
            };
            if (self.aniFrame === 1) {
                // self.textChange();
                ani.frame01();
                self.aniFrame = 2;
            } else if (self.aniFrame === 2) {
                // self.textChange();
                ani.frame02();
                self.aniFrame = 1;
            }
        },
        /**
         * 重置
         */
        reset: function reset() {
            var self = this;
            self.resetState = false;
            self.stepButtonState = 1;
            self.fullButtonState = 1;
            self.aniFrame = 1;
            self._aniStop();
            self.textReset();
            self._resetBallPosi();
            self.helper.modifyingElements.show();
        },
        bindEvent: function bindEvent() {},
        unbindEvent: function unbindEvent() {},
        /*
         *  停止小球动画
         * */
        _aniStop: function _aniStop() {
            var self = this;
            self.leftBallMoveMina && self.leftBallMoveMina.stop();
            self.leftNoCentralBallMoveMina01 && self.leftNoCentralBallMoveMina01.stop();
            self.rightBallMoveMina && self.rightBallMoveMina.stop();
            self.leftNoCentralBallMoveMina02 && self.leftNoCentralBallMoveMina02.stop();
            self.rightNoCentralBallMoveMina && self.rightNoCentralBallMoveMina.stop();
            self.fullDemonstrationMina && clearTimeout(self.fullDemonstrationMina);
        },
        //分步演示与下一步 文字处理
        textChange: function textChange() {
            if (this.stepButtonState == 1) {
                this.$stepButton.html(this.helper.i18n.step_demonstration);
            } else if (this.stepButtonState == 2) {
                this.$stepButton.html(this.helper.i18n.next_step_demonstration);
            }

            if (this.fullButtonState === 1) {
                this.$fullButton.html(this.helper.i18n.start_demonstration);
            } else if (this.fullButtonState === 2) {
                this.$fullButton.html(this.helper.i18n.again_demonstration);
            }
        },
        _resetBallPosi: function _resetBallPosi() {
            this.helper.centralBallLeft.attr({
                transform: 'translate(715 120)'
            });
            this.helper.centralBallRight.attr({
                transform: 'translate(1065 120)'
            });

            this.helper.noCentralBallLeft.attr({
                transform: 'translate(715 120)'
            });
            this.helper.noCentralBallRight.attr({
                transform: 'translate(1065 120)'
            });
        }
    });
    return NonCenteringCollision;
});
'use strict';

define('PublishSubscribe', function () {
    var PublishSubscribe = Class.extend({
        $init: function $init() {
            this._queues = {};
            this._topics = {};
            this._topicIndex = -1;
            this._topicName = {
                //小球碰撞事件
                OnBallCollisionEvent: Symbol.for('On Ball Collision Event'),
                //重置的事件
                OnResetEvent: Symbol.for('On Reset Event')
            };
        },

        //发布消息
        publish: function publish(topic, args) {
            if (!this._topics[topic]) {
                return;
            }
            var subs = this._topics[topic];
            for (var i = 0, iLen = subs.length; i < iLen; i++) {
                subs[i].func(topic, args);
            }
        },
        //监听消息
        subscribe: function subscribe(topic, func) {
            this._topics[topic] = this._topics[topic] ? this._topics[topic] : [];
            var token = (++this._topicIndex).toString();
            this._topics[topic].push({
                token: token,
                func: func
            });
            return token;
        },
        remove: function remove(topic, token) {
            var result = void 0;
            for (var i = 0, iLen = this._topics.length; i < iLen; i++) {
                if (this._topics[i].token === token) {
                    result = this._topics.splice(i, 1);
                    break;
                }
            }
            return result;
        },
        //重置消息发布订阅器
        reset: function reset() {
            this._queues = {};
            this._topics = {};
            this._topicIndex = -1;
        }
    });

    return PublishSubscribe;
});
