addon.presenter:
(function () {

	var PresenterName = 'CompositionOutline';

	var parseModel = function (model) {
		// 正则替换，解析题目中的填空横线
		if (model.QuestionContent && model.QuestionContent.title) {
			model.QuestionContent.title = model.QuestionContent.title.replace(/<textentryinteraction.*?<\/textentryinteraction>/gi, '<textEntry/>');
		}
		return model;
	};

	var parseComponents = function (context, model) {

		var modules = {
			layout: 'WriteLayout',
			controllers: [{
				presenter: 'I18nFilter'
			}, {
				presenter: 'InteractionStateRecoverFilter'
			}, {
				presenter: 'AnswerFlowController',
				depends: [{
					property: 'TimerService',
					presenter: 'TimerController'
				}]
			}, {
				presenter: 'CompositionOutlineBusiness'
			}],
			components: [{
				presenter: 'InteractionTimer',
				renderTo: 'timer'
			}, {
				presenter: 'CompositionPanel',
				renderTo: 'panel'
			}]
		};
		var settings = [{
				name: 'WriteLayout',
				type: 'string',
				value: JSON.stringify([{
					name: 'questionType',
					type: 'string',
					value: 'composition'
				}])
			},
			{
				name: 'InteractionTimer',
				type: 'string',
				value: JSON.stringify([{
					'name': 'view_type',
					'type': 'string',
					'value': 'new_timer'
				}])
			},
			{
				name: 'AnswerFlowController',
				type: 'string',
				value: JSON.stringify([{
					name: 'linkQuestionId',
					type: 'string',
					value: model.QuestionId
				}])
			}
		];
		// CompositionPanel
		settings.push({
			name: "CompositionPanel",
			type: "string",
			value: function (content) {
				var result = [{
					name: "titleType", // 标题类型
					type: "string",
					value: content.titleType + ''
				}, {
					name: "title", // 作文标题
					type: "string",
					value: content.title
				}, {
					name: "text", // 作文要求
					type: "string",
					value: content.requirements
				}, {
					name: "assetTitle", // 素材名称
					type: "string",
					value: JSON.stringify(content.assets.map(function (item) {
						return item.title;
					}))
				}, {
					name: "asset", // 素材内容
					type: "string",
					value: JSON.stringify(content.assets.map(function (item) {
						return item.content;
					}))
				}];
				return JSON.stringify(result);
			}(model.QuestionContent)
		});
		// CompositionOutlineBusiness
		settings.push({
			name: "CompositionOutlineBusiness",
			type: "string",
			value: JSON.stringify([{
				name: 'QuestionId',
				type: 'string',
				value: model.QuestionId
			}, {
				name: 'QuestionContent',
				type: 'json',
				value: ['compositionStyle', 'outlineLevel', 'outlineTemplate', 'checkList'].reduce(function (memo, item) {
					memo[item] = model.QuestionContent[item];
					return memo;
				}, {})
			}])
		});
		switch (true) {
			case context.isPPTShell:
			case context.isWeb:
			case context.isPC:
				modules.controllers.push({
					presenter: 'AnswerClassStatis'
				}, {
					presenter: 'ClassroomStatusController'
				});
				settings.push({
					name: 'AnswerClassStatis',
					type: 'string',
					value: JSON.stringify([{
						name: 'config',
						type: 'json',
						value: {
							//统计底部按钮设置，退出按钮、排序、查看原题、再次发题
							toolbar: ['exit', 'order', 'question', 'send.again'],
							//统计插件设置：同屏对比、生生互评、作文提纲
							plugins: ['AnswerContrast', 'AnswerAssessment', 'CompositionOutlineStatisPlugin']
						}
					}])
				}, {
					name: 'ClassroomStatusController',
					type: 'string',
					value: JSON.stringify([{
						name: 'questionId',
						type: 'string',
						value: model.QuestionId
					}])
				});
				break;
			case context.isApp:
				modules.components.push({
					presenter: 'InteractionSubmitButton',
					renderTo: 'submit'
				});
				settings.push({
					name: "InteractionSubmitButton",
					type: "string",
					value: JSON.stringify([{
							name: "can_modify",
							type: "string",
							value: true
						},
						{
							name: "linkQuestionType",
							type: "string",
							value: PresenterName
						},
						{
							name: "linkQuestionId",
							type: "string",
							value: model.QuestionId
						},
						{
							name: "view_type",
							type: "string",
							value: "new_em_button"
						}
					])
				});
				break;
			default:
				break;
		}
		settings.push({
			name: 'Components',
			type: 'json',
			text: JSON.stringify(modules)
		});
		return settings;
	};

	var Presenter = BasicPresenter.extend({
		metaInfo: {
			name: PresenterName
		},
		run: function (view, model) {
			view.style.width = 0;
			view.style.height = 0;
			var components = parseComponents(this, parseModel(model));
			this.$requireComponent('ComponentLoader', components, $(view), {
				left: 0,
				top: 0,
				width: 0,
				height: 0
			});
		}
	}, PresenterName);

	window['Addon' + PresenterName + '_create'] = function () {
		return new Presenter();
	};
})();
