exports.syncCallback = syncCallback;
function syncCallback(me, eventData) {
  switch (eventData.type) {
    case 'request':
      // 同步题目成功或失败
      me.syncId = eventData.value.syncId;
      if (eventData.value.result) {
        // 成功显示“结束答题”的按钮
        me._context.$view.find('.stop').removeClass('hide');
      }
      break;
    case 'cancel':
      // 结束同步
      if (eventData.value.result) {
        // 成功关闭“结束答题”的按钮
        me._context.$view.find('.stop').addClass('hide');
      }
      break;
  }
}

