// 获取移动时的坐标
function getRelateLocation(_context, event, view) {
  var x = void 0,
      y = void 0;
  var paddingSpace = parseFloat(_context.$view.find('.bunch').css('padding-top'));
  var tempHeight = parseFloat(_context.$view.find('.shaft-item-pearl').css('height'));
  if (event.originalEvent.changedTouches) {
    x = event.originalEvent.changedTouches[0].clientX;
    y = event.originalEvent.changedTouches[0].clientY;
  } else {
    x = event.pageX;
    y = event.pageY;
  }
  var tempPosition = view.offset();
  return {
    x: x - tempPosition.left,
    y: y - tempPosition.top - paddingSpace - tempHeight / 2
  };
}

