      multipleAudio: '',
      unitAudio: ''
    }];
  }

  _createClass(CounterConfig, [{
    key: 'initShaftNum',
    value: function initShaftNum(_context) {
      // 初始化每个杆的珠子数量
      var isNotZero = false;
      for (var index = 0; index < _context.initNum.length; index++) {
        this.config[index].num = _context.initNum[index];
        if (_context.initNum[index] > 0) {
          isNotZero = true;
        }
        if (isNotZero) {
          this.config[index].showNum = true;
        }
      }
      return this.config;
    }
  }]);

  return CounterConfig;
}();

