exports.default = {

    //返回坐标
    coordTansform: function coordTansform(event) {
        var x = 0,
            y = 0;

        switch (event.type) {
            case "mousedown":
            case "mousemove":
            case "mouseup":
                x = event.clientX;
                y = event.clientY;
                break;
            case "touchstart":
            case "touchmove":
                x = event.touches[0].clientX;
                y = event.touches[0].clientY;
            case "touchend":
                x = event.changedTouches[0].clientX;
                y = event.changedTouches[0].clientY;
                break;
        }

        return {
            x: x,
            y: y
        };
    },

    //滑动方向和距离
    getSlideParam: function getSlideParam(curPosition, lastPosition) {

        var distance = curPosition.x - lastPosition.x;
        var direction = distance > 0 ? "right" : "left";

        return {
            direction: direction,
            distance: distance
        };
    }

};

