				}
			}
			/****判断图形是否为有效的长方形****/
			rectAngle = this._sortShapeList(rectAngle);
			var rectLength = rectAngle.length;
			//单个小正方形
			if (rectLength == 1) {
				return { rectAngle: { length: 1, width: 1 }, array: array };
			}
			//如果第二个小正方形和第一个在同一行
			if (rectAngle[0][0] == rectAngle[1][0]) {
				var index = void 0;
				for (var i = 1; i < rectLength; i++) {
					if (rectAngle[i][0] != rectAngle[0][0]) {
						index = i;
						break;
					}
				}
				if (!index) {
					return { rectAngle: { length: rectLength, width: 1 }, array: array };
				}
				if (rectLength % index == 0) {
					var len = rectLength / index;
					for (var _i = 1; _i < len; _i++) {
						for (var j = 0; j < index; j++) {
							if (rectAngle[index * _i + j][0] != rectAngle[j][0] + _i || rectAngle[index * _i + j][1] != rectAngle[j][1]) {
								return { rectAngle: false, array: array };
							}
						}
					}
					if (index >= len) {
						return { rectAngle: { length: index, width: len }, array: array };
					} else {
						return { rectAngle: { length: len, width: index }, array: array };
					}
				}
			} else if (rectAngle[0][1] == rectAngle[1][1]) {
				var _iteratorNormalCompletion2 = true;
				var _didIteratorError2 = false;
				var _iteratorError2 = undefined;

				try {
					for (var _iterator2 = rectAngle[Symbol.iterator](), _step2; !(_iteratorNormalCompletion2 = (_step2 = _iterator2.next()).done); _iteratorNormalCompletion2 = true) {
						var rect = _step2.value;

						if (rect[1] != rectAngle[0][1]) {
							return { rectAngle: false, array: array };
						}
					}
				} catch (err) {
					_didIteratorError2 = true;
					_iteratorError2 = err;
				} finally {
					try {
						if (!_iteratorNormalCompletion2 && _iterator2.return) {
							_iterator2.return();
						}
					} finally {
						if (_didIteratorError2) {
							throw _iteratorError2;
						}
					}
				}

				return { rectAngle: { length: rectLength, width: 1 }, array: array };
			}
			return { rectAngle: false, array: array };
		}

		/**
   * 查找单个小正方形的相邻八个小正方形中有在当前区域显示的
   * @param row 单个小正方形的行值
   * @param col 单个小正方形的列值
   * @param array 所有的小正方形数组
   * @returns {Array}
   * @private
   */

	}, {
		key: '_find',
		value: function _find(row, col, array) {
			var result = [];
			//周围八个小正方形
			var nearRect = [[row, col + 1], [row + 1, col + 1], [row + 1, col], [row + 1, col - 1], [row, col - 1], [row - 1, col - 1], [row - 1, col], [row - 1, col + 1]];
			var _iteratorNormalCompletion3 = true;
			var _didIteratorError3 = false;
			var _iteratorError3 = undefined;

			try {
				for (var _iterator3 = nearRect[Symbol.iterator](), _step3; !(_iteratorNormalCompletion3 = (_step3 = _iterator3.next()).done); _iteratorNormalCompletion3 = true) {
