   * @param list
   * @private
   */

	}, {
		key: '_fillTable',
		value: function _fillTable(list) {
			//删除除表头之外的其它列
			this._rectAngles = list;
			this._data.$table.find("tr td").remove();
			var row1 = this._data.$table.find("tr:eq(0)");
			var row2 = this._data.$table.find("tr:eq(1)");
			var row3 = this._data.$table.find("tr:eq(2)");
			if (list.length > 8) list.length = 8; //去除多余的数据
			for (var i = 0, len = list.length; i < len; i++) {
				if (i === len - 1) {
					row1.append('<td style="border-right-style:none;"><div align="center">' + list[i]["length"] + '</div></td>');
					row2.append('<td style="border-right-style:none;"><div align="center">' + list[i]["width"] + '</div></td>');
					row3.append('<td style="border-right-style:none;"><div align="center">' + list[i]["length"] * list[i]["width"] + '</div></td>');
