   * @param list 二维数组 格式为[[row,col],[row,col]...]
   * @private
   */

	}, {
		key: '_sortShapeList',
		value: function _sortShapeList(list) {
			list.sort(function (a, b) {
				return a[0] - b[0] == 0 ? a[1] - b[1] : a[0] - b[0];
			});
			return list;
		}

		/**
   * 点击生成表格后，隐藏自由拼摆下的所有按钮并切换到统计表格界面
   * 当传入参数false，则由统计表格界面切换回自由拼摆的操作界面
   * @param isCreate 是否是生成表格操作
   */

	}, {
		key: '_free2Table',
		value: function _free2Table() {
			var isCreate = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : true;

			if (isCreate) {
				this._data.$showFormula.removeClass("disabled");
				this._data.$switch.hide();
				this._data.$fccd.hide();
				this._data.$tableButtons.show();
				this._data.$coverPage.show();
				this._data.$table.show();
				//隐藏显示提示框时打开的遮罩层
				this._data.$confirmLayer.hide();
			} else {
				this._data.$switch.show();
				this._data.$fccd.show();
				this._data.$tableButtons.hide();
				this._data.$table.hide();
				this._data.$formula.hide();
				this._data.$coverPage.hide();
			}
		}
	}, {
		key: 'inDelMode',
		get: function get() {
			return this._data.inDelMode;
		}

		/**
   * 提示框
   */

	}, {
		key: 'tooltip',
		get: function get() {
			return this._tooltip;
		}
	}]);

	return Common;
}(_baseControl2.default);

