                        areas[maxArea] = -1;
                    }
                    // 判断当前区域是否已有小正方形，如果没有，则返回此小正方形的坐标
                    else if (this.getShape(newType, arr[maxArea].row - 1, arr[maxArea].col - 1) === null) {
                            return { row: arr[maxArea].row - 1, col: arr[maxArea].col - 1 };
                        } else {
                            areas[maxArea] = -1;
                        }
                }
                if (areas[maxArea] === -1) {
                    return { row: -1, col: -1 };
                }
            } else {
                return { row: -2, col: -2 };
            }
            //当小正方形落在待测图形之外时，返回{row:-2,col:-2}
            return { row: -2, col: -2 };
        }

