var Util = function () {
	function Util() {
		_classCallCheck(this, Util);
	}

	_createClass(Util, null, [{
		key: "dist",

		/**
   * 两点距离
   */
		value: function dist(p1x, p1y, p2x, p2y) {
			var x = p1x - p2x;
			var y = p1y - p2y;
			return Math.floor(Math.sqrt(x * x + y * y));
		}

		/**
   * 判断两个矩形是否相交
   * 这种算法要求两个矩形的边都是垂直与x或者y轴
   */

	}, {
		key: "areaIntersect",
		value: function areaIntersect(area1, area2) {
			var zx = Math.abs(area1.left + area1.right - area2.left - area2.right);
			var x = Math.abs(area1.left - area1.right) + Math.abs(area2.left - area2.right);
			var zy = Math.abs(area1.top + area1.bottom - area2.top - area2.bottom);
			var y = Math.abs(area1.top - area1.bottom) + Math.abs(area2.top - area2.bottom);
			return zx <= x && zy <= y;
		}

		/**
   * 判断点是否在矩形中
   */

	}, {
		key: "pointInArea",
		value: function pointInArea(px, py, area) {
			return area.left <= px && px <= area.right && area.top <= py && py <= area.bottom;
		}

		/**
   * 判断矩形small是否在矩形big内部
   */

	}, {
		key: "areaInArea",
		value: function areaInArea(small, big) {
			return Util.pointInArea(small.left, small.top, big) && Util.pointInArea(small.right, small.top, big) && Util.pointInArea(small.left, small.bottom, big) && Util.pointInArea(small.right, small.bottom, big);
		}

		/**
   * 获取对象的值
   */

	}, {
		key: "values",
		value: function values(obj) {
			var res = [];
			for (var key in obj) {
				res.push(obj[key]);
			}
			return res;
		}

		/**
   * 判断 value 是否介于 min 和 max 之间
   */

	}, {
		key: "between",
		value: function between(value, min, max) {
			if (value < min || value > max) {
				return false;
			}
			return true;
		}
	}]);

	return Util;
}();

exports.default = Util;
