                this.data.$lenText.text(value);
                if (this.data.$widText.text() > value) this.data.$widText.text(value);
            } else {
                if (this.data.$widText.text() == value) return;
                this.data.$widText.text(value);
            }
            _mainCompare2.default.getInst().resetSize(this.data.$widText.text(), this.data.$lenText.text());
            //清空待测图形里的小正方形
            _mainCompare2.default.getInst().clearShape([_mainCompare2.default.getInst().curEnableData.type]);
            _mainCompare2.default.getInst().plugScale.autoScaleBySmall();
        }

