/**
 * 组件：拉杆
 * @example
 *        创建一个拉杆组件，用于控制音量，在0~100之间
 *        let audioRod = new PlugRod(
 *            0,
 *            100,
 *            (val, pct) => console.log(`当前音量值：${val}, 当前音量百分比:${pct}`),
 *            $myAudioDom
 *        );
 *
 * @author Tiago
 */

var PlugRod = function () {
	/**
  * 构造
  *
  * @param minVal    取值下限
  * @param maxVal    取值上限
  * @param cb        值变化时的回调，参数是当前值，以及百分比
  * @param $silder   背景 Dom
  * @param $handle   滑块 Dom
  * @param $canver     监听鼠标移动的 Dom
  * @param defVal    默认值，默认是 minVal
  * @param useHor     水平方向(true) | 垂直方向(false)
  */
	function PlugRod(minVal, maxVal, cb, $silder, $handle, $canver, defVal) {
		var useHor = arguments.length > 7 && arguments[7] !== undefined ? arguments[7] : true;

		_classCallCheck(this, PlugRod);

		// 设置锁定，锁定后值将不会变化，也无法响应交互
		this.lock = false;
		// 强制 curVal 为 [minVal, maxVal] 间的整数
		this.useInt = true;
		this._cb = cb;
		this._curVal = -1;
		this._minVal = minVal;
		this._maxVal = maxVal;
		this._useHor = useHor;
		if (this._useHor) {
			this._silderWidth = $silder.outerWidth();
			this._step = this._silderWidth / (this._maxVal - this._minVal);
			this._handleWidth = $handle.outerWidth();
		} else {
			this._silderHeight = $silder.outerHeight();
			this._step = this._silderHeight / (this._maxVal - this._minVal);
			this._handleHeight = $handle.outerHeight();
		}
		console.log('$handle', this._step);
		this._$silder = $silder;
		this._$handle = $handle;
		this._$canver = $canver;
		// 移动信息
		this._moveInfo = { beginX: null, beginY: null };
		// 移动过程中保留的小数点位数
		this._fixed = 1;
		this._init(defVal != null ? defVal : this._minVal);
	}

	/**
  * 析构
  */


	_createClass(PlugRod, [{
		key: 'destroy',
		value: function destroy() {
			this._$silder.off();
			this._$handle.off();
			this._$canver.off(this._viewEvt);
			this._$b1 && this._$b1.off();
			this._$b2 && this._$b2.off();
