			}
			var handle_top = this._$handle.position().top * ratio;
			this._$handle.css('top', handle_top);
			// console.log('this._silderWidth',this._silderWidth,'this._step',this._step,'this._silderHeight',this._silderHeight)
			// console.log('top',ratio,this._$handle.position().top)
		}

