			if (this._moveInfo.beginX == null) {
				//_( event.type, "1" );
				return;
			}
			//_( event.type, "2" );
			var perPercent = this.curPercent,
			    newPercent = this.curPercent;
			if (this._useHor) {
				newPercent = (realEvt.pageX - this._$silder.offset().left) / this._silderWidth;
			} else {
				newPercent = (realEvt.pageY - this._$silder.offset().top) / this._silderHeight;
			}
			newPercent = newPercent.toFixed(this._fixed);
			perPercent != newPercent && (this.curPercent = newPercent);
		}

		/**
   * 放弃移动
   */

	}, {
		key: '_onMouseUp',
		value: function _onMouseUp(event) {
