			this._clearMove();
		}

		/**
   * 鼠标移出目标
   */

	}, {
		key: '_onMouseOut',
		value: function _onMouseOut(event) {
			//_( event.type, "0", event );
			this._clearMove();
		}
	}, {
		key: 'curVal',
		get: function get() {
			return this._curVal;
		},
		set: function set(newVal) {
			if (this._lock == true) {
				return;
			}
			this.useInt && (newVal = Math.floor(newVal));
			newVal = Math.min(Math.max(this._minVal, newVal), this._maxVal);
			var oldVal = this._curVal;
			this._curVal = newVal;
			if (this._cb(this.curVal, this.curPercent) == false) {
				this._curVal = oldVal;
				return;
			}
			var size = (newVal - this._minVal) * this._step;
			if (this._useHor) {
				this._$handle.css('left', size - this._handleWidth / 2 + 'px');
			} else {
				this._$handle.css('top', size - this._handleHeight / 2 + 'px');
			}
		}

