  * 析构
  */


	_createClass(SelectList, [{
		key: 'destroy',
		value: function destroy() {
			this._$selView.off();
		}

		/**
   * 初始化
   * @private
   */

	}, {
		key: '_init',
		value: function _init() {
			var _this = this;

			//下拉列表数字绑定事件
			this._$selView.on('mousedown touchstart', 'a', function (e) {
				return _this._setInputValue(e);
			});
			this._$selView.on('mousedown touchstart', function (e) {
				e.stopPropagation();
			});
		}

		/***************************************************************************
   * 对外接口
   **************************************************************************/
		/**
   * 显示下拉列表
   * @param currentValue 当前输入框的数值
   * @param maxNum  下拉列表可选的最大值
   */

	}, {
		key: 'showSelectList',
		value: function showSelectList(currentValue, maxNum) {
			this._setRange(maxNum);
			this.setItemActive(currentValue);
			this._$selView.show();
		}

		/**
   * 将选择的数字高亮
   * @param index
   */

	}, {
		key: 'setItemActive',
		value: function setItemActive(index) {
			this._$selView.find('a').eq(index - 1).addClass('active');
		}

		/**
   * 移除列表中高亮的数值
   * @param e
   */

	}, {
		key: 'removeItemsActive',
		value: function removeItemsActive(e) {
			this._$selView.find('a').removeClass('active');
		}

		/**
   * 隐藏选择列表
   */

	}, {
		key: 'hideSelectList',
		value: function hideSelectList() {
			this._$selView.hide();
		}

		/**
   * 设置选择列表显示的位置
   * @param left
   * @param top
   */

	}, {
		key: 'setPosition',
		value: function setPosition(left, top) {
			this._$selView.css({ "left": left, "top": top });
		}

		/***************************************************************************
   * 鼠标相关
   **************************************************************************/
		/**
   * 设置输入框值，下拉列表选项点击的监听函数
   * @param e
   */

	}, {
		key: '_setInputValue',
		value: function _setInputValue(e) {
			e.stopPropagation();
			e.preventDefault();
			//如果是鼠标中键或右键点到下拉菜单数字时不做行为
			if (e.which == 2 || e.which == 3) {
				return;
			}
			var value = e.currentTarget.textContent;
			value = parseInt(value);
			this.removeItemsActive(e);
			this.setItemActive(value);
			this._cb(value);
			this.hideSelectList();
		}

		/**
   * 根据传入的数值设置键盘的显示范围
   * @private
   */

	}, {
		key: '_setRange',
		value: function _setRange(num) {
			var fragment = '';
			for (var i = 1; i <= num; i++) {
				fragment += '<a href="javascript:;"><span>' + i + '</span></a>';
			}
			this._$selView.empty().append(fragment);
		}
	}]);

	return SelectList;
}();

exports.default = SelectList;

