  * @param time 提示框存在的时间，默认是2s
  */
	function Tooltip($tipDom, cb) {
		var time = arguments.length > 2 && arguments[2] !== undefined ? arguments[2] : 2000;

		_classCallCheck(this, Tooltip);

		this._$tooltipDom = $tipDom;
		this._cb = cb;
