							nextClass: ''
						});
					}
				}
				this.app.data.curTip = '';
				return;
			}
			this.app.data.curTip = classString;
			var group = Snap.select('.activeGroup');
			var currentCircle = group.select('.' + classString.replace(/invisibleCircle/i, 'controlCircle').replace(/invisible/i, '').split(' ').join('.'));
			this.tipPosition = {
				x: +currentCircle.attr('cx'),
				y: this.coordinate.y - currentCircle.attr('cy') + this.coordinate.offsetBottom + this.app.config.selectedPointRadius
			};
			var value = (Math.round((this.coordinate.y - currentCircle.attr('cy')) / this.coordinate.gridYWidth * 0.2 * 100) / 100).toFixed(2);
			this.toggleTip(true, value, this.tipPosition);

			var nextElement = currentCircle.node.nextElementSibling;

			if (nextElement) {
				currentCircle.data({
					nextClass: nextElement.getAttribute('class')
				});
			}
			currentCircle.addClass('activeCircle').toFront(currentCircle.parent());
			currentCircle.attr({
				r: this.app.config.selectedPointRadius,
				fill: this.app.config.selectCircleColor
			});
		}
		//全屏缩放

	}, {
		key: 'handlerResizeScreen',
		value: function handlerResizeScreen(data) {
			this.ratio = eval(data.ratio);
			if (this.index == 1) {
				this.initRatio = this.ratio;
				this.index++;
			}
			//console.log('初始比例',this.index ,this.initRatio);
			if (this.tipPosition) {
				if (this.lastRatio == this.ratio) {
					this.numberTip.css({
						left: this.tipPosition.x,
						bottom: this.tipPosition.y
					});
				} else {
					this.numberTip.css({
						left: this.tipPosition.x * this.ratio,
						bottom: this.tipPosition.y * this.ratio
					});
				}
			}
		}

		/**
   * 切换折线上的数值显示
   * @param  {[bool]} visible  [是否显示数值]
   * @param  {[string]} value    [显示的字符串数值内容]
   * @param  {[obj]} position [显示的位置]
   */

	}, {
		key: 'toggleTip',
		value: function toggleTip(visible, value, position) {
			if (!visible) {
				this.numberTip.css('display', 'none');
				return this;
			}

			this.numberTip.find('i').html(value);
			if (this.ratio == this.initRatio) {
				position.x = parseInt(position.x * this.ratio - this.numberTip.outerWidth() * 0.2);
				position.y = position.y * this.ratio;
			} else {
				position.x = parseInt(position.x - this.numberTip.outerWidth() * 0.2);
			}
			this.numberTip.css({
				left: position.x,
				bottom: position.y
			});
			this.lastRatio = this.ratio;
			this.numberTip.css('display', 'block');
		}
		/**
   * 生成id
   * @return {[string]} [返回随机字符串]
   */

	}, {
		key: 'uuid',
		value: function uuid() {
			var s = [],
			    hexDigits = '0123456789abcdef';
			for (var i = 0; i < 10; i++) {
				s[i] = hexDigits.substr(Math.floor(Math.random() * 0x10), 1);
			}

			return s.join('');
		}

		/**
   * 根据lines控制折线的显示
   * @param  {[arr]} showLines [要显示的线]
   */

	}, {
		key: 'controlLines',
		value: function controlLines(showLines) {
			var hideLines = Snap.selectAll('.lineToShow');
			for (var i = 0; i < hideLines.length; i++) {
				hideLines[i].addClass('hide_dom');
			};

			showLines.map(function (el) {
				Snap.select('.' + el).removeClass('hide_dom').toFront();
			});
		}

		/**
   * 创建绘制svg路径动画对象
   * @param  {[number]} u1     [静摩擦力]
   * @param  {[number]} u2     [动摩擦力]
   * @param  {[number]} weight [压力]
   * @param  {[number]} speed  [速度]
   * @return {[object]}        [返回动画对象]
   */

	}, {
		key: 'drawLine',
		value: function drawLine() {

			var u1 = this.app.config.coefficient[this.app.data.curIndex].staticCoefficient;
			var u2 = this.app.config.coefficient[this.app.data.curIndex].dynamicCoefficient;
			var weight = this.app.data.totalWeight;
			var speed = this.app.data.curSpeed;

			var time = this.app.data.totalTime;
			var color = this.app.config.currentLineColor;
			var radius = this.pointRadius;
			var lineWidth = this.app.config.currentLineWidth;
			var f1 = u1 * weight;
			var f2 = u2 * weight;
			var k1 = f1 / 0.2 * this.coordinate.gridYWidth / (this.coordinate.gridXWidth * time * 2 / 3);
			var path1 = 'M' + this.coordinate.x + ' ' + this.coordinate.y + 'L' + (this.coordinate.gridXWidth * time * 2 / 3 + this.coordinate.x + this.axisXWidth / 2 - this.app.config.currentLineWidth / 2) + ' ' + (this.coordinate.y - f1 / 0.2 * this.coordinate.gridYWidth + this.axisYWidth / 2);
			var path2 = 'M' + (this.coordinate.gridXWidth * time * 2 / 3 + this.coordinate.x + this.axisXWidth / 2 - this.app.config.currentLineWidth / 2) + ' ' + (this.coordinate.y - f1 / 0.2 * this.coordinate.gridYWidth + this.axisYWidth / 2) + 'L' + (this.coordinate.gridXWidth * time * 2 - this.coordinate.gridXWidth * time * 2 * 5.8 / 9 + this.coordinate.x + this.axisXWidth / 2 - this.app.config.currentLineWidth / 2) + ' ' + (this.coordinate.y - f2 / 0.2 * this.coordinate.gridYWidth + this.axisYWidth / 2);
			var path3 = 'M' + (this.coordinate.gridXWidth * time * 2 - this.coordinate.gridXWidth * time * 2 * 5.8 / 9 + this.coordinate.x + this.axisXWidth / 2 - this.app.config.currentLineWidth / 2) + ' ' + (this.coordinate.y - f2 / 0.2 * this.coordinate.gridYWidth + this.axisYWidth / 2) + 'L' + (this.coordinate.gridXWidth * time * 2 + this.coordinate.x) + ' ' + (this.coordinate.y - f2 / 0.2 * this.coordinate.gridYWidth + this.axisYWidth / 2);
			var length1 = Snap.path.getTotalLength(path1);
			var length2 = Snap.path.getTotalLength(path2);
			var length3 = Snap.path.getTotalLength(path3);

			this.animateGroup = this.svg.g().addClass('hide_dom');
			this.animateGroup.add(this.svg.path({
				path: path1,
				strokeWidth: lineWidth,
				stroke: color,
				strokeDasharray: length1,
				'stroke-dashoffset': length1
			}).addClass('path1'), this.svg.path({
				path: path2,
				strokeWidth: lineWidth,
				stroke: color,
				strokeDasharray: length2,
				'stroke-dashoffset': length2
			}).addClass('path2'), this.svg.path({
				path: path3,
				strokeWidth: lineWidth,
				stroke: color,
				strokeDasharray: length3,
				'stroke-dashoffset': length3
			}).addClass('path3'));

			var t1 = new TimelineMax({
				data: {
					u1: u1,
					u2: u2,
					weight: weight,
					speed: speed
				},
				onStart: function onStart() {
					if (this.animateGroup) {
						this.animateGroup.removeClass('hide_dom');
					}
				},
				onComplete: function onComplete() {
					if (this.animateGroup) {
						this.animateGroup.remove();
					}
				},
				onStartScope: this,
				onCompleteScope: this,
				ease: Linear.easeNone,
				paused: true
			});

			t1.to($('.path1'), time / 3, {
				'stroke-dashoffset': 0
			}, 0).addCallback(function () {
				if (this.animateGroup) {
					this.animateGroup.select('.path1').attr({
						'stroke-linecap': 'square'
					});
				}
			}, time / 3, null, this).to($('.path2'), time * 3.2 / 9 - time / 3, {
				'stroke-dashoffset': 0

			}).addCallback(function () {
				if (this.animateGroup) {
					this.animateGroup.select('.path2').attr({
						'stroke-linecap': 'square'
					});
				}
			}, time * 3.2 / 9, null, this).to($('.path3'), time * 5.8 / 9, {
				'stroke-dashoffset': 0

			});

			return t1;
		}

		/**
   * 根据state控制动画状态
   * @param  {[number]} state [当前动画状态]
   */

	}, {
		key: 'animateControl',
		value: function animateControl(state) {
			switch (state) {
				case this.app.config.INIT_STATE:
					// 动画未初始化
					if (this.animateObj) {

						this.animateGroup ? this.animateGroup.addClass('hide_dom').remove() : null;
						this.animateObj.progress(0);
						this.animateObj.kill();
						this.animateGroup = null;
						this.animateObj = null;
					}
					break;
				case this.app.config.RUN_STATE:
					// 动画开始
					this.toggleTip(false);
					this.unselectGroup();
					if (this.animateObj) {
						this.animateObj.resume();
						return;
					}
					this.animateObj = this.drawLine();
					this.animateObj.play();
					break;
				case this.app.config.PAUSE_STATE:
					// 动画暂停
					if (this.animateObj) {
						this.animateObj.pause();
						if (this.app.config.initStudentMobile && this.app.data.animateProgress) {
							this.animateObj.progress(this.app.data.animateProgress);
						}
					}
					break;
				case this.app.config.COMPLETE_STATE:
					// 动画结束
					if (!this.animateObj) {
						return;
					}
					this.animateObj.progress(1);
					var classString = 'line' + this.animateObj.data.u1 * 100 + this.animateObj.data.weight * 10 + this.animateObj.data.speed;
					var lines = this.app.data.lines;
					if (!lines.includes(classString)) {
						lines.push(classString);
					} else {
						var index = lines.indexOf(classString);
						lines.splice(index, 1);
						lines.push(classString);
					}
					this.app.data.lines = lines;
					this.animateObj = null;
					break;
				default:
					break;
			}
		}

		/**
   * 对传入的多个数组组合后合并成单个数组
   * @param  {[arr]} arrs [一个数组包含需要被组合的多个数组]
   * @return {[arr]}      [组合后的单个数组]
   */

	}, {
		key: 'joinArr',
		value: function joinArr(arrs) {
			return arrs.reduce(function (prev, next) {
				var newNums = [];
				for (var i = 0; i < next.length; i++) {
					var a = next[i];
					prev.map(function (el) {

						newNums.push(el + ',' + a);
					});
				};

				return !prev.length ? next : newNums;
			}, []);
		}
	}]);

	return GraphControl;
}(_BaseControl3.default);

exports.default = GraphControl;

