	    value: function isPointIn(x, y) {
	      var me = this;
	      if (!me.pos || !me.pos.offset) {
	        return;
	      }
	      var pt = { x: x, y: y };
	      var left = me.pos.offset.left;
	      var width = me.pos.width;
	      var height = me.pos.height;
	      var top = me.pos.offset.top;

	      var isin = false;
	      switch (me.type) {
	        case _Config.TYPE.TRIA:
	          {
	            var v1 = { x: left + width / 2, y: top };
	            var v2 = { x: left, y: top + height };
