	            isin = _Utils2.default.pointInTriangle(pt, v1, v2, v3);
	            break;
	          }
	        case _Config.TYPE.CIRCLE:
	          {
	            var cx = left + width / 2;
	            var cy = top + height / 2;
	            var r = me.pos.width / 2;
	            isin = (cx - pt.x) * (cx - pt.x) + (cy - pt.y) * (cy - pt.y) <= r * r;
	            break;
	          }
	        case _Config.TYPE.RIBBONS:
	        case _Config.TYPE.SQUARE:
	        case _Config.TYPE.RECT:
	          {
	            var right = left + width;
	            var bottom = top + height;
	            isin = pt.x > left && pt.x < right && pt.y > top && pt.y < bottom;
	            break;
	          }
	        case _Config.TYPE.LINE:
	          {
	            var rh = 60 / 100;
	            var rw = 40 / 800;
	            var _right = left + width;
	            var _bottom = top + height;
	            // bottom part
