addon.presenter:
(function () {
    var Presenter = BasicPresenter.extend({
        metaInfo: {
            name: 'FreeCombination',
            type: PresenterType.PRESENTER_COMPONENT
        },
        /**
         * Presenter的初始化方法
         * @private
         */
        $init: function () {
            this._super();
            this.componentModel = [];
            this.componentSetting = {};
            //定义日志
            this.logger.appendHandler(function (message, context) {
                switch (context.level.name) {
                    case 'DEBUG':
                        console.debug(message);
                        break;
                    case 'INFO':
                        console.info(message);
                        break;
                    case 'WARN':
                        console.warn(message);
                        break;
                    case 'ERROR':
                        console.error(message);
                        break;
                    default:
                        console.log(message);
                        break;
                }
            }, {level: Logger.DEBUG});
        },
        /****以下开始为icPlayer的生命周期方法*****/
        run: function (view, model) {
            this.$view = $(view);
            this.model = model;
            this.$view.hide();

            this._configComponents();
            this._adaptedModelData();
            this.componentModel.push({
                name: "Components",
                type: "json",
                text: JSON.stringify(this.componentSetting)
            });
            this.$requireComponent('ComponentLoader', this.componentModel, this.$view,
                {left: 0, top: 0, width: 0, height: 0});
        },
        destroy: function () {
        },
        /**
         * 配置加载组件的内容
         * @private
         */
        _configComponents: function () {
            if(this.isPPTShell) {
                this.componentSetting = {
                    controllers: [
                        {presenter: "I18nFilter"},
                        {presenter: "InteractionStateRecoverFilter"},
                        {
                            presenter: "AnswerFlowController",
                            depends: [{
                                property: "TimerService",
                                presenter: "TimerController"
                            }]
                        },
                        {
                            presenter: "FreeCombinationLoader",
                            renderTo: "CenterPanel",
                            depends: [{
                                property: "ToolbarService",
                                presenter: "CommonToolbar",
                                renderTo: "RightPanel",
                            }]
                        },
                        {presenter: "ClassroomStatusController"},   // 课堂状态控制器
                        {presenter: "AnswerClassStatis"}
                    ],
                    layout: "ObjectiveInteractionLayout",
                    components: [
                        {presenter: "InteractionTimer", renderTo: "TopLeftPanel"}
                    ]
                };
            }
            else if (this.isPC || this.isWeb) {
                this.componentSetting = {
                    controllers: [
                        {presenter: "I18nFilter"},
                        {presenter: "InteractionStateRecoverFilter"},
                        {
                            presenter: "AnswerFlowController",
                            depends: [{
                                property: "TimerService",
                                presenter: "TimerController"
                            }]
                        },
                        {
                            presenter: "FreeCombinationLoader",
                            renderTo: "CenterPanel",
                            depends: [{
                                property: "ToolbarService",
                                presenter: "CommonToolbar",
                                renderTo: "RightPanel"
                            }]
                        },
                        {presenter: "AnswerClassStatis"}
                    ],
                    layout: "ObjectiveInteractionLayout",
                    components: [
                        {presenter: "InteractionTimer", renderTo: "TopLeftPanel"},
                        {presenter: "InteractionSubmitButton", renderTo: "BottomPanel"}
                    ]
                };
            } else if (this.isApp) {
                this.componentSetting = {
                    controllers: [
                        {presenter: "I18nFilter"},
                        {presenter: "InteractionStateRecoverFilter"},
                        {
                            presenter: "AnswerFlowController",
                            depends: [{
                                property: "TimerService",
                                presenter: "TimerController"
                            }]
                        },
                        {
                            presenter: "FreeCombinationLoader",
                            renderTo: "CenterPanel"
                        }
                    ],
                    layout: "ObjectiveInteractionLayout",
                    components: [
                        {presenter: "InteractionTimer", renderTo: "TopLeftPanel"},
                        {presenter: "InteractionSubmitButton", renderTo: "BottomPanel"}
                    ]
                };
            }
        },
        /**
         * 适配模型数据
         * @private
         */
        _adaptedModelData: function () {
            var loaderModel = [{
                name: "QuestionId",
                type: "string",
                value: this.model.QuestionId
            }, {
                name: "StemContent",
                type: "html",
                text: this.model.StemContent
            }, {
                name: "StemAttachment",
                type: "json",
                value: {attachments: this.model.StemAttachment}
            }, {
                name: "StageSize",
                type: "json",
                value: {
                    width: this.model.StageWidth,
                    height: this.model.StageHeight
                }
            }, {
                name: "StageElements",
                type: "json",
                value: {elements: this.model.StageElements}
            }];
            var toolbarSettings = {};
            //元素加载器的模型
            this.componentModel.push({
                name: "FreeCombinationLoader",
                type: "string",
                value: JSON.stringify(loaderModel)
            });
            //计时器的模型
            this.componentModel.push({
                name: "InteractionTimer",
                type: "string",
                value: JSON.stringify([{
                    "name": "view_type",
                    "type": "string",
                    "value": "new_timer"
                }])
            });
            this.componentModel.push({
                name: "ObjectiveInteractionLayout",
                type: "string",
                value: JSON.stringify([{
                    name: "questionType",
                    type: "string",
                    value: "FreeCombination"
                }, {
                    name: "specialTemplate",
                    type: "string",
                    value: "true"
                }])
            });
            //提交按钮的模型
            this.componentModel.push({
                name: "InteractionSubmitButton",
                type: "string",
                value: JSON.stringify([
                    {
                        name: "can_modify",
                        type: "string",
                        value: true
                    },
                    {
                        name: "linkQuestionType",
                        type: "string",
                        value: "FreeCombination"
                    },
                    {
                        name: "linkQuestionId",
                        type: "string",
                        value: this.model.QuestionId
                    },
                    {
                        name: "view_type",
                        type: "string",
                        value: "new_em_button"
                    }
                ])
            });
            //作答流程的模型
            this.componentModel.push({
                name: "AnswerFlowController",
                type: "string",
                value: JSON.stringify([{
                    name: "linkQuestionId",
                    type: "string",
                    value: this.model.QuestionId
                }])
            });
			//D类统计重构
			this.componentModel.push({
                name: "AnswerClassStatis",
                type: "string",
                value: JSON.stringify([{
					name: "config",
					type: "string",
					value: JSON.stringify({
						"toolbar": ["exit", "order", "question", "send.again"],
						"plugins": ["AnswerContrast"]
					})
				}])
            });
            this.componentModel.push({
                name: 'ClassroomStatusController',
                type: 'string',
                value: JSON.stringify([{
                    name: 'questionId',
                    type: 'string',
                    value: this.model.QuestionId
                }])
            });
        }
    }, 'FreeCombination');

    window.AddonFreeCombination_create = function () {
        return new Presenter();
    }
})();
