	  _bindEvent: function _bindEvent() {
	    var _this2 = this;

	    var domObj = this.helper;
	    var interval = null;
	    /*显示倒计时框*/
	    this.showCountDown = function (type, time) {
	      var _this = this;

	      var timeCur = time;
	      domObj.js_countDownTime.html(timeCur);
	      domObj.js_countDownBox.removeClass('hide_dom');
	      interval = setInterval(function () {
	        timeCur--;
	        domObj.js_countDownTime.html(timeCur);
	        if (timeCur === 0) {
	          clearInterval(interval);
	          interval = null;
	          domObj.js_countDownBox.addClass('hide_dom');
	          // callback && callback();
	          if (_this.markType === 'dot') {
	            _this.graphMain.markDotCallback();
	          } else {
	            _this.graphMain.markEdgeCallback();
	          }
	        }
	      }, 1000);
	    };
	    this.eventBus.on('countDown', function (type) {
	      if (_this2.markType === type && interval !== null) {
	        return;
	      }
	      _this2.markType = type;
	      var timeCur = type === 'dot' ? _constant.countDownTime.dot : _constant.countDownTime.edge;
	      _this2.showCountDown(type, timeCur);
	    });

	    /*按钮状态*/
	    function chDomState(jqDom, val) {
	      jqDom.removeClass(_constant.style.btnDisabled + ' ' + _constant.style.comBtnActive);
	      if (val === 0) jqDom.addClass(_constant.style.btnDisabled);else if (val === 2) jqDom.addClass(_constant.style.comBtnActive);
	    }

	    this.eventBus.on('changeBtnState', function () {
	      var state = _this2.graphMain.getDataState();
	      chDomState(domObj.js_markDot, state.dot);
	      chDomState(domObj.js_markEdge, state.edge);
	      chDomState(domObj.js_changeTuringDot, state.chTurningDot);
	      chDomState(domObj.js_toggleRotationAngle, state.toggleRotationAngle);

	      var currentIndex = _this2.graphMain.getCurrentSceneIndex();
	      domObj.js_menuList.find('li').removeClass(_constant.style.menuListOn);
	      domObj.js_menuList.find('li').eq(currentIndex).addClass(_constant.style.menuListOn);

	      if (state.canUndo) {
	        domObj.js_undo.removeClass(_constant.style.btnDisabled);
	      } else {
	        domObj.js_undo.addClass(_constant.style.btnDisabled);
	      }
	      if (state.canRedo) {
	        domObj.js_redo.removeClass(_constant.style.btnDisabled);
	      } else {
	        domObj.js_redo.addClass(_constant.style.btnDisabled);
