	        domObj.js_toggleRotationAngleText.text(i18nData['gr_showRotationAngle']);
	      }
	    });
	    /*鼠标拖动旋转*/
	    domObj.js_main.on('touchstart mousedown', function (event) {
	      var mainBCR = domObj.js_main.get(0).getBoundingClientRect();
	      var center = {
	        x: mainBCR.left + mainBCR.width / 2,
	        y: mainBCR.top + mainBCR.height / 2
	      };
	      var pointerDown = event.originalEvent.changedTouches ? event.originalEvent.changedTouches[0] : event;
	      var downAngle = _utils.utils.getAngle(center, { x: pointerDown.pageX, y: pointerDown.pageY });
	      var startAngle = _this2.graphMain.getAngle();
	      domObj.js_main.on('touchmove mousemove', function (event) {
	        var pointerMove = event.originalEvent.changedTouches ? event.originalEvent.changedTouches[0] : event;
	        /*移动区域判断*/
	        if (pointerMove.pageX > mainBCR.left && pointerMove.pageX < mainBCR.right && pointerMove.pageY > mainBCR.top && pointerMove.pageY < mainBCR.bottom) {
	          var moveAngle = _utils.utils.getAngle(center, { x: pointerMove.pageX, y: pointerMove.pageY });
	          var angleCut = (startAngle + moveAngle - downAngle) % 360;
	          var angle = angleCut < 0 ? angleCut + 360 : angleCut;
	          angle = Math.round(angle);
	          _this2.graphMain.rotate(angle);
	        } else {
	          domObj.js_main.trigger('touchend mouseup');
	          domObj.js_main.trigger('touchcancel mouseleave');
	        }
	      });
	      domObj.js_main.on('touchend mouseup', function (event) {

	        // alert(111)
	        var angle = _this2.graphMain.getAngle();
	        /*吸附*/
	        if (angle >= 355 || angle <= 5) {
	          angle = 0;
	        } else if (angle >= 85 && angle <= 95) {
	          angle = 90;
	        } else if (angle >= 175 && angle <= 185) {
	          angle = 180;
	        } else if (angle >= 265 && angle <= 275) {
	          angle = 270;
	        }
	        _this2.graphMain.rotateEnd(angle);

	        domObj.js_main.off('touchmove mousemove');
	        domObj.js_main.off('touchend mouseup');
	        domObj.js_main.off('touchcancel mouseleave');
	      });
	      domObj.js_main.on('touchcancel mouseleave', function (event) {

	        // alert(222)
	        var angle = _this2.graphMain.getAngle();
	        /*吸附*/
	        if (angle >= 355 || angle <= 5) {
	          angle = 0;
	        } else if (angle >= 85 && angle <= 95) {
	          angle = 90;
	        } else if (angle >= 265 && angle <= 275) {
	          angle = 270;
	        }
	        _this2.graphMain.rotate(angle);

	        domObj.js_main.off('touchmove mousemove');
	        domObj.js_main.off('touchend mouseup');
	        domObj.js_main.off('touchcancel mouseleave');
	      });
	    });

	    /*点击标点*/
	    domObj.js_markDot.click(function () {
	      if (domObj.js_markDot.hasClass(_constant.style.comBtnActive) || domObj.js_markDot.hasClass(_constant.style.btnDisabled)) {
	        return;
	      }
	      _this2.graphMain.activeDot();
	      /*如果有倒计时，倒计时停止*/
